"use client";

import { useId, useMemo, useRef, useState } from \"react\";
import confetti from \"canvas-confetti\";
import { motion } from \"framer-motion\";
import NumberFlow from \"@number-flow/react\";
import { Button } from \"@/components/ui/button\";
import { Label } from \"@/components/ui/label\";
import { Switch } from \"@/components/ui/switch\";
import { useMediaQuery } from \"@/hooks/use-media-query\";
import { cn } from \"@/lib/utils\";
import DynamicIcon from \"@/components/DynamicIcon\";
import Link from \"@/components/Link\";

export interface PricingPlan {
  name: string;
  price: number;
  yearlyPrice: number;
  period: string;
  features: string[];
  description: string;
  buttonText: string;
  href: string;
  isPopular?: boolean;
  buttonTarget?: \"_blank\" | \"_self\";
  buttonRel?: string;
}

export interface PricingProps {
  plans: PricingPlan[];
  title?: string;
  description?: string;
  billingToggleLabel?: string;
  billingToggleHighlight?: string;
  monthlyBillingText?: string;
  annualBillingText?: string;
  popularBadgeText?: string;
  currency?: string;
  priceMinimumFractionDigits?: number;
  priceMaximumFractionDigits?: number;
  className?: string;
}

export function Pricing({
  plans,
  title = \"Simple, Transparent Pricing\",
  description = \"Choose the plan that works for you\\nAll plans include access to our platform, lead generation tools, and dedicated support.\",
  billingToggleLabel = \"Annual billing\",
  billingToggleHighlight = \"Save 20%\",
  monthlyBillingText = \"billed monthly\",
  annualBillingText = \"billed annually\",
  popularBadgeText = \"Popular\",
  currency = \"USD\",
  priceMinimumFractionDigits = 0,
  priceMaximumFractionDigits = 0,
  className,
}: PricingProps) {
  const [isMonthly, setIsMonthly] = useState(true);
  const isDesktop = useMediaQuery(\"(min-width: 768px)\");
  const switchRef = useRef<HTMLButtonElement | null>(null);
  const billingToggleId = useId();

  const priceFormatter = useMemo(
    () =>
      new Intl.NumberFormat(\"en-US\", {
        style: \"currency\",
        currency,
        minimumFractionDigits: priceMinimumFractionDigits,
        maximumFractionDigits: priceMaximumFractionDigits,
      }),
    [currency, priceMinimumFractionDigits, priceMaximumFractionDigits]
  );

  const handleToggle = (checked: boolean) => {
    setIsMonthly(!checked);
    if (checked && typeof window !== \"undefined\" && switchRef.current) {
      const rect = switchRef.current.getBoundingClientRect();
      const x = rect.left + rect.width / 2;
      const y = rect.top + rect.height / 2;

      confetti({
        particleCount: 50,
        spread: 60,
        origin: {
          x: x / window.innerWidth,
          y: y / window.innerHeight,
        },
        colors: [
          \"hsl(var(--primary))\",
          \"hsl(var(--accent))\",
          \"hsl(var(--secondary))\",
          \"hsl(var(--muted))\",
        ],
        ticks: 200,
        gravity: 1.2,
        decay: 0.94,
        startVelocity: 30,
        shapes: [\"circle\"],
      });
    }
  };

  return (
    <div className={cn(\"container py-20\", className)}>
      <div className=\"mb-12 space-y-4 text-center\">
        <h2 className=\"text-4xl font-bold tracking-tight sm:text-5xl\">{title}</h2>
        <p className=\"whitespace-pre-line text-lg text-muted-foreground\">{description}</p>
      </div>

      <div className=\"mb-10 flex justify-center\">
        <div className=\"relative inline-flex cursor-pointer items-center\">
          <Label htmlFor={billingToggleId} className=\"sr-only\">
            {isMonthly ? `Enable ${billingToggleLabel}` : `Disable ${billingToggleLabel}`}
          </Label>
          <Switch
            id={billingToggleId}
            ref={switchRef}
            checked={!isMonthly}
            onCheckedChange={handleToggle}
            className=\"relative\"
          />
        </div>
        <span className=\"ml-2 font-semibold\">
          {billingToggleLabel}{\" \"}
          <span className=\"text-primary\">({billingToggleHighlight})</span>
        </span>
      </div>

      <div className=\"grid grid-cols-1 gap-4 md:grid-cols-3\">
        {plans.map((plan, index) => {
          const isPopularPlan = Boolean(plan.isPopular);
          const displayedPrice = isMonthly ? plan.price : plan.yearlyPrice;
          const linkTarget = plan.buttonTarget ?? \"_self\";
          const linkRel =
            plan.buttonRel ??
            (linkTarget === \"_blank\" ? \"noopener noreferrer\" : undefined);

          return (
            <motion.div
              key={plan.name}
              initial={{ y: 50, opacity: 1 }}
              whileInView=
                {isDesktop
                  ? {
                      y: isPopularPlan ? -20 : 0,
                      opacity: 1,
                      x: index === 2 ? -30 : index === 0 ? 30 : 0,
                      scale: index === 0 || index === 2 ? 0.94 : 1,
                    }
                  : {}}
              viewport={{ once: true }}
              transition={{
                duration: 1.6,
                type: \"spring\",
                stiffness: 100,
                damping: 30,
                delay: 0.4,
                opacity: { duration: 0.5 },
              }}
              className={cn(
                \"relative flex flex-col rounded-2xl border bg-background p-6 text-center transition-transform duration-300 lg:flex lg:flex-col lg:justify-center\",
                isPopularPlan ? \"border-2 border-primary\" : \"border-border\",
                !isPopularPlan ? \"mt-5\" : undefined
              )}
            >
              {isPopularPlan && (
                <div className=\"absolute right-0 top-0 flex items-center rounded-bl-xl rounded-tr-xl bg-primary px-2 py-0.5\">
                  <DynamicIcon name=\"Star\" className=\"h-4 w-4 text-primary-foreground\" />
                  <span className=\"ml-1 font-sans font-semibold text-primary-foreground\">{popularBadgeText}</span>
                </div>
              )}
              <div className=\"flex flex-1 flex-col\">
                <p className=\"text-base font-semibold text-muted-foreground\">{plan.name}</p>
                <div className=\"mt-6 flex items-center justify-center gap-x-2\">
                  <span className=\"text-5xl font-bold tracking-tight text-foreground\">
                    <NumberFlow
                      value={displayedPrice}
                      format={{
                        style: \"currency\",
                        currency,
                        minimumFractionDigits: priceMinimumFractionDigits,
                        maximumFractionDigits: priceMaximumFractionDigits,
                      }}
                      formatter={(value) =>
                        priceFormatter.format(
                          typeof value === \"number\" ? value : Number(value)
                        )
                      }
                      transformTiming={{
                        duration: 500,
                        easing: \"ease-out\",
                      }}
                      willChange
                      className=\"[font-variant-numeric:tabular-nums]\"
                    />
                  </span>
                  {plan.period !== \"Next 3 months\" && (
                    <span className=\"text-sm font-semibold leading-6 tracking-wide text-muted-foreground\">
                      / {plan.period}
                    </span>
                  )}
                </div>

                <p className=\"text-xs leading-5 text-muted-foreground\">
                  {isMonthly ? monthlyBillingText : annualBillingText}
                </p>

                <ul className=\"mt-5 flex flex-col gap-2 text-left\">
                  {plan.features.map((feature, featureIndex) => (
                    <li
                      key={`${plan.name}-feature-${featureIndex}`}
                      className=\"flex items-start gap-2\"
                    >
                      <DynamicIcon
                        name=\"Check\"
                        className=\"mt-1 h-4 w-4 flex-shrink-0 text-primary\"
                      />
                      <span>{feature}</span>
                    </li>
                  ))}
                </ul>

                <hr className=\"my-4 w-full\" />

                <Button
                  asChild
                  variant={isPopularPlan ? \"default\" : \"outline\"}
                  className={cn(
                    \"group relative w-full gap-2 overflow-hidden text-lg font-semibold tracking-tighter transition-all duration-300 ease-out\",
                    \"transform-gpu ring-offset-current hover:ring-2 hover:ring-primary hover:ring-offset-1\",
                    isPopularPlan
                      ? \"bg-primary text-primary-foreground hover:bg-primary\"
                      : \"bg-background text-foreground hover:bg-primary hover:text-primary-foreground\"
                  )}
                >
                  <Link
                    href={plan.href}
                    className=\"flex w-full items-center justify-center\"
                    target={linkTarget}
                    rel={linkRel}
                  >
                    {plan.buttonText}
                  </Link>
                </Button>
                <p className=\"mt-6 text-xs leading-5 text-muted-foreground\">{plan.description}</p>
              </div>
            </motion.div>
          );
        })}
      </div>
    </div>
  );
}

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground hover:bg-destructive/90",
        outline:
          "border border-input bg-background hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-10 px-4 py-2",
        sm: "h-9 rounded-md px-3",
        lg: "h-11 rounded-md px-8",
        icon: "h-10 w-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  },
)
Button.displayName = "Button"

export { Button, buttonVariants }

"use client"

import * as React from "react"
import * as LabelPrimitive from "@radix-ui/react-label"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const labelVariants = cva(
  "text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70",
)

const Label = React.forwardRef<
  React.ElementRef<typeof LabelPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof LabelPrimitive.Root> &
    VariantProps<typeof labelVariants>
>(({ className, ...props }, ref) => (
  <LabelPrimitive.Root
    ref={ref}
    className={cn(labelVariants(), className)}
    {...props}
  />
))
Label.displayName = LabelPrimitive.Root.displayName

export { Label }

"use client"

import * as React from "react"
import * as SwitchPrimitives from "@radix-ui/react-switch"

import { cn } from "@/lib/utils"

const Switch = React.forwardRef<
  React.ElementRef<typeof SwitchPrimitives.Root>,
  React.ComponentPropsWithoutRef<typeof SwitchPrimitives.Root>
>(({ className, ...props }, ref) => (
  <SwitchPrimitives.Root
    className={cn(
      "peer inline-flex h-6 w-11 shrink-0 cursor-pointer items-center rounded-full border-2 border-transparent transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 focus-visible:ring-offset-background disabled:cursor-not-allowed disabled:opacity-50 data-[state=checked]:bg-primary data-[state=unchecked]:bg-input",
      className,
    )}
    {...props}
    ref={ref}
  >
    <SwitchPrimitives.Thumb
      className={cn(
        "pointer-events-none block h-5 w-5 rounded-full bg-background shadow-lg ring-0 transition-transform data-[state=checked]:translate-x-5 data-[state=unchecked]:translate-x-0",
      )}
    />
  </SwitchPrimitives.Root>
))
Switch.displayName = SwitchPrimitives.Root.displayName

export { Switch }
