"use client";

import * as React from "react";
import { cn } from "@/lib/utils";
import Link from "@/components/Link";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";

interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

interface SocialLink {
  name: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

interface FooterLink {
  name: string;
  href: string;
  iconName: string; // lucide icon name
}

interface FooterColumn {
  title: string;
  links: FooterLink[];
}

export interface FooterProps extends React.HTMLAttributes<HTMLDivElement> {
  brand: {
    name: string;
    description: string;
    href: string;
    target?: "_blank" | "_self";
    rel?: string;
  };
  socialLinks: SocialLink[];
  columns: FooterColumn[];
  copyright?: string;
  backgroundColor?: string;
  backgroundImage?: ImageProps;
  socialLinksSeparator?: string;
}

export const Footer = React.forwardRef<HTMLDivElement, FooterProps>(
  (
    {
      className,
      brand,
      socialLinks,
      columns,
      copyright,
      backgroundColor,
      backgroundImage,
      socialLinksSeparator,
      style,
      ...props
    },
    ref
  ) => {
    const mergedStyle = React.useMemo<React.CSSProperties | undefined>(() => {
      return { ...style, ...(backgroundColor ? { backgroundColor } : {}) };
    }, [style, backgroundColor]);

    return (
      <div ref={ref} className={cn("pt-24 relative", className)} style={mergedStyle} {...props}>
        {backgroundImage ? (
          <div className="absolute inset-0 -z-10">
            <Image
              {...backgroundImage}
              className={cn("h-full w-full object-cover", backgroundImage.className)}
            />
          </div>
        ) : null}

        <div className="max-w-screen-xl mx-auto px-4 lg:px-8">
          <div className="grid grid-cols-1 lg:grid-cols-12">
            <div className="lg:col-span-4">
              <Link href={brand.href} className="text-xl font-semibold" target={brand.target} rel={brand.rel}>
                {brand.name}
              </Link>
              <p className="text-sm text-foreground/60">{brand.description}</p>

              <p className="text-sm font-light text-foreground/55 mt-3.5">
                {socialLinks.map((link, index) => (
                  <React.Fragment key={link.name}>
                    <Link
                      className="hover:text-foreground/90"
                      href={link.href}
                      target={link.target}
                      rel={link.rel}
                    >
                      {link.name}
                    </Link>
                    {typeof socialLinksSeparator === "string" && index < socialLinks.length - 1
                      ? socialLinksSeparator
                      : null}
                  </React.Fragment>
                ))}
              </p>
            </div>

            <div className="grid grid-cols-2 mt-16 md:grid-cols-3 lg:col-span-8 lg:justify-items-end lg:mt-0">
              {columns.map(({ title, links }) => (
                <div key={title} className="last:mt-12 md:last:mt-0">
                  <h3 className="text-sm font-semibold">{title}</h3>
                  <ul className="mt-4 space-y-2.5">
                    {links.map(({ name, iconName, href }) => (
                      <li key={name}>
                        <Link
                          href={href}
                          className="text-sm transition-all text-foreground/60 hover:text-foreground/90 group"
                        >
                          <DynamicIcon
                            name={iconName}
                            className="inline stroke-2 h-4 mr-1.5 transition-all stroke-foreground/60 group-hover:stroke-foreground/90"
                          />
                          {name}
                        </Link>
                      </li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          </div>

          {copyright ? (
            <div className="mt-20 border-t pt-6 pb-8">
              <p className="text-xs text-foreground/55">{copyright}</p>
            </div>
          ) : null}
        </div>
      </div>
    );
  }
);

Footer.displayName = "Footer";
