"use client";

import * as React from "react";
import { cn } from "@/lib/utils";
import Link from "@/components/Link";
import DynamicIcon from "@/components/DynamicIcon";

export interface Icon {
  package: "lucide";
  name: string;
  type: "icon";
}

export interface FooterBrand {
  name: string;
  description: string;
  href: string;
}

export interface FooterSocialLink {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

export interface FooterColumnLink {
  label: string;
  href: string;
  icon: Icon;
  target?: "_blank" | "_self";
  rel?: string;
}

export interface FooterColumn {
  title: string;
  links: FooterColumnLink[];
}

export interface FooterProps extends React.HTMLAttributes<HTMLDivElement> {
  brand: FooterBrand;
  socialLinks: FooterSocialLink[];
  columns: FooterColumn[];
  copyright?: string;
}

export const Footer = React.forwardRef<HTMLDivElement, FooterProps>(
  (
    {
      className,
      brand,
      socialLinks,
      columns,
      copyright,
      ...props
    },
    ref
  ) => (
    <div ref={ref} className={cn("pt-24", className)} {...props}>
      <div className="mx-auto max-w-screen-xl px-4 lg:px-8">
        <div className="grid grid-cols-1 lg:grid-cols-12">
          <div className="lg:col-span-4">
            <Link href={brand.href} className="text-xl font-semibold">
              {brand.name}
            </Link>
            <p className="text-sm text-foreground/60">{brand.description}</p>
            {socialLinks.length > 0 && (
              <p className="mt-3.5 text-sm font-light text-foreground/55">
                {socialLinks.map((link, index) => (
                  <React.Fragment key={link.label}>
                    <Link
                      href={link.href}
                      className="hover:text-foreground/90"
                      target={link.target}
                      rel={
                        link.target === "_blank"
                          ? link.rel ?? "noopener noreferrer"
                          : link.rel
                      }
                    >
                      {link.label}
                    </Link>
                    {index < socialLinks.length - 1 && " • "}
                  </React.Fragment>
                ))}
              </p>
            )}
          </div>
          <div className="mt-16 grid grid-cols-2 md:grid-cols-3 lg:col-span-8 lg:mt-0 lg:justify-items-end">
            {columns.map((column) => (
              <div key={column.title} className="last:mt-12 md:last:mt-0">
                <h3 className="text-sm font-semibold">{column.title}</h3>
                <ul className="mt-4 space-y-2.5">
                  {column.links.map((link) => (
                    <li key={link.label}>
                      <Link
                        href={link.href}
                        className="group text-sm text-foreground/60 transition-all hover:text-foreground/90"
                        target={link.target}
                        rel={
                          link.target === "_blank"
                            ? link.rel ?? "noopener noreferrer"
                            : link.rel
                        }
                      >
                        <DynamicIcon
                          name={link.icon.name}
                          className="mr-1.5 inline h-4 stroke-foreground/60 stroke-2 transition-all group-hover:stroke-foreground/90"
                        />
                        {link.label}
                      </Link>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
        {copyright && (
          <div className="mt-20 border-t pt-6 pb-8">
            <p className="text-xs text-foreground/55">{copyright}</p>
          </div>
        )}
      </div>
    </div>
  )
);

Footer.displayName = "Footer";
