"use client";

import React from "react";
import { motion } from "framer-motion";
import Link from "@/components/Link";
import Image from "@/components/Image";

const transition = {
  type: "spring",
  mass: 0.5,
  damping: 11.5,
  stiffness: 100,
  restDelta: 0.001,
  restSpeed: 0.001,
} as const;

interface MenuItemProps {
  setActive: (item: string) => void;
  active: string | null;
  item: string;
  children?: React.ReactNode;
}

export const MenuItem: React.FC<MenuItemProps> = ({ setActive, active, item, children }) => (
  <div onMouseEnter={() => setActive(item)} className="relative">
    <motion.p transition={{ duration: 0.3 }} className="cursor-pointer text-black hover:opacity-90 dark:text-white">
      {item}
    </motion.p>
    {active !== null && (
      <motion.div initial={{ opacity: 0, scale: 0.85, y: 10 }} animate={{ opacity: 1, scale: 1, y: 0 }} transition={transition}>
        {active === item && (
          <div className="absolute left-1/2 top-[calc(100%+1.2rem)] -translate-x-1/2 transform pt-4">
            <motion.div
              transition={transition}
              layoutId="active"
              className="rounded-2xl border border-black/20 bg-white shadow-xl backdrop-blur-sm dark:border-white/20 dark:bg-black"
            >
              <motion.div layout className="h-full w-max p-4">
                {children}
              </motion.div>
            </motion.div>
          </div>
        )}
      </motion.div>
    )}
  </div>
);

interface MenuProps {
  setActive: (item: string | null) => void;
  children: React.ReactNode;
}

export const Menu: React.FC<MenuProps> = ({ setActive, children }) => (
  <nav onMouseLeave={() => setActive(null)} className="relative flex justify-center space-x-4 rounded-full border border-transparent px-8 py-6 shadow-input dark:border-white/20">
    {children}
  </nav>
);

type SupportedImageType = "background" | "profile" | "logo" | "banner" | "gallery" | "thumbnail" | "icon" | "other";

interface ProductItemProps {
  title: string;
  description: string;
  href: string;
  imageSrc: string;
  imageAlt?: string;
  imageWidth?: number;
  imageHeight?: number;
  imageType?: SupportedImageType;
}

export const ProductItem: React.FC<ProductItemProps> = ({
  title,
  description,
  href,
  imageSrc,
  imageAlt,
  imageWidth = 140,
  imageHeight = 70,
  imageType = "other",
}) => {
  const resolvedAlt = imageAlt ?? title;

  return (
    <Link href={href} className="flex space-x-2">
      <Image src={imageSrc} alt={resolvedAlt} width={imageWidth} height={imageHeight} className="flex-shrink-0 rounded-md shadow-2xl" type={imageType} />
      <div>
        <h4 className="mb-1 text-xl font-bold text-black dark:text-white">{title}</h4>
        <p className="max-w-[10rem] text-sm text-neutral-700 dark:text-neutral-300">{description}</p>
      </div>
    </Link>
  );
};

type HoveredLinkProps = React.PropsWithChildren<React.ComponentProps<typeof Link>>;

export const HoveredLink: React.FC<HoveredLinkProps> = ({ children, className, ...rest }) => {
  const baseClassName = "text-neutral-700 hover:text-black dark:text-neutral-200";
  const combinedClassName = className ? `${baseClassName} ${className}` : baseClassName;

  return (
    <Link {...rest} className={combinedClassName}>
      {children}
    </Link>
  );
};
