"use client";

import * as React from "react";
import { motion, stagger, useAnimate, useInView } from "framer-motion";
import { cn } from "@/lib/utils";

export interface TypewriterWord {
  text: string;
  className?: string;
}

export interface TypewriterEffectProps {
  words: TypewriterWord[];
  className?: string;
  cursorClassName?: string;
}

export function TypewriterEffect({
  words,
  className,
  cursorClassName,
}: TypewriterEffectProps) {
  const wordsWithCharacters = React.useMemo(
    () =>
      words.map((word) => ({
        ...word,
        characters: word.text.split(""),
      })),
    [words]
  );

  const [scope, animate] = useAnimate();
  const isInView = useInView(scope);

  React.useEffect(() => {
    if (isInView) {
      void animate(
        "span",
        {
          display: "inline-block",
          opacity: 1,
          width: "fit-content",
        },
        {
          duration: 0.3,
          delay: stagger(0.1),
          ease: "easeInOut",
        }
      );
    }
  }, [animate, isInView]);

  return (
    <div
      className={cn(
        "text-base text-center font-bold sm:text-xl md:text-3xl lg:text-5xl",
        className
      )}
    >
      <motion.div ref={scope} className="inline">
        {wordsWithCharacters.map((word, wordIndex) => (
          <div key={`word-${wordIndex}`} className="inline-block">
            {word.characters.map((char, charIndex) => (
              <motion.span
                key={`char-${wordIndex}-${charIndex}-${char}`}
                className={cn(
                  "hidden opacity-0 text-black dark:text-white",
                  word.className
                )}
              >
                {char}
              </motion.span>
            ))}
            &nbsp;
          </div>
        ))}
      </motion.div>
      <motion.span
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{
          duration: 0.8,
          repeat: Infinity,
          repeatType: "reverse",
        }}
        className={cn(
          "inline-block h-4 w-[4px] rounded-sm bg-blue-500 md:h-6 lg:h-10",
          cursorClassName
        )}
      />
    </div>
  );
}

export interface TypewriterEffectSmoothProps {
  words: TypewriterWord[];
  className?: string;
  cursorClassName?: string;
}

export function TypewriterEffectSmooth({
  words,
  className,
  cursorClassName,
}: TypewriterEffectSmoothProps) {
  const wordsWithCharacters = React.useMemo(
    () =>
      words.map((word) => ({
        ...word,
        characters: word.text.split(""),
      })),
    [words]
  );

  return (
    <div className={cn("my-6 flex space-x-1", className)}>
      <motion.div
        className="overflow-hidden pb-2"
        initial={{ width: "0%" }}
        whileInView={{ width: "fit-content" }}
        transition={{
          duration: 2,
          ease: "linear",
          delay: 1,
        }}
      >
        <div
          className="text-xs font-bold sm:text-base md:text-xl lg:text-3xl xl:text-5xl"
          style={{ whiteSpace: "nowrap" }}
        >
          <div>
            {wordsWithCharacters.map((word, wordIndex) => (
              <div key={`smooth-word-${wordIndex}`} className="inline-block">
                {word.characters.map((char, charIndex) => (
                  <span
                    key={`smooth-char-${wordIndex}-${charIndex}-${char}`}
                    className={cn(
                      "text-black dark:text-white",
                      word.className
                    )}
                  >
                    {char}
                  </span>
                ))}
                &nbsp;
              </div>
            ))}
          </div>
        </div>
      </motion.div>
      <motion.span
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{
          duration: 0.8,
          repeat: Infinity,
          repeatType: "reverse",
        }}
        className={cn(
          "block h-4 w-[4px] rounded-sm bg-blue-500 sm:h-6 xl:h-12",
          cursorClassName
        )}
      />
    </div>
  );
}
