import DynamicIcon from "@/components/DynamicIcon";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";

type IconDefinition = {
  package: "lucide";
  name: string;
  type: "icon";
};

interface Decoration {
  content: string;
  className: string;
}

interface PricingTier {
  name: string;
  icon: IconDefinition;
  iconColorClass?: string;
  price: number;
  description: string;
  features: string[];
  popular?: boolean;
  highlightLabel?: string;
  priceSuffix?: string;
  ctaLabel: string;
  ctaHref: string;
  ctaTarget?: "_blank" | "_self";
  ctaRel?: string;
}

interface CreativePricingProps {
  tag?: string;
  title?: string;
  description?: string;
  tiers: PricingTier[];
  priceSuffix?: string;
  checkIcon?: IconDefinition;
  titleDecorations?: Decoration[];
  backgroundDecorations?: Decoration[];
}

const defaultCheckIcon: IconDefinition = {
  package: "lucide",
  name: "Check",
  type: "icon",
};

const defaultTitleDecorations: Decoration[] = [
  {
    content: "✨",
    className: "absolute -right-12 top-0 text-amber-500 rotate-12",
  },
  {
    content: "⭐️",
    className: "absolute -left-8 bottom-0 text-blue-500 -rotate-12",
  },
];

const defaultBackgroundDecorations: Decoration[] = [
  {
    content: "✎",
    className: "absolute top-40 left-20 text-4xl rotate-12",
  },
  {
    content: "✏️",
    className: "absolute bottom-40 right-20 text-4xl -rotate-12",
  },
];

function CreativePricing({
  tag = "Simple Pricing",
  title = "Make Short Videos That Pop",
  description = "Edit, enhance, and go viral in minutes",
  tiers,
  priceSuffix = "/month",
  checkIcon = defaultCheckIcon,
  titleDecorations = defaultTitleDecorations,
  backgroundDecorations = defaultBackgroundDecorations,
}: CreativePricingProps) {
  if (!tiers || tiers.length === 0) {
    return null;
  }

  return (
    <div className="relative w-full max-w-6xl mx-auto px-4">
      <div className="text-center space-y-6 mb-16">
        <div className="font-handwritten text-xl text-blue-500 rotate-[-1deg]">
          {tag}
        </div>
        <div className="relative">
          <h2 className="text-4xl md:text-5xl font-bold font-handwritten text-zinc-900 dark:text-white rotate-[-1deg]">
            {title}
            {titleDecorations.map((decoration, decorationIndex) => (
              <div
                key={`${decoration.content}-${decorationIndex}`}
                className={decoration.className}
              >
                {decoration.content}
              </div>
            ))}
          </h2>
          <div className="absolute -bottom-4 left-1/2 -translate-x-1/2 w-44 h-3 bg-blue-500/20 rotate-[-1deg] rounded-full blur-sm" />
        </div>
        <p className="font-handwritten text-xl text-zinc-600 dark:text-zinc-400 rotate-[-1deg]">
          {description}
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {tiers.map((tier, index) => {
          const rotationClass =
            index === 0
              ? "rotate-[-1deg]"
              : index === 1
              ? "rotate-[1deg]"
              : index === 2
              ? "rotate-[-2deg]"
              : undefined;
          const iconColorClass = tier.iconColorClass ?? "text-blue-500";
          const highlightText = tier.highlightLabel ?? "Popular!";
          const tierPriceSuffix = tier.priceSuffix ?? priceSuffix;
          const target = tier.ctaTarget ?? "_self";
          const rel =
            tier.ctaRel ?? (target === "_blank" ? "noopener noreferrer" : undefined);
          const buttonClassName = cn(
            "w-full h-12 font-handwritten text-lg relative",
            "border-2 border-zinc-900 dark:border-white",
            "transition-all duration-300",
            "shadow-[4px_4px_0px_0px] shadow-zinc-900 dark:shadow-white",
            "hover:shadow-[6px_6px_0px_0px]",
            "hover:translate-x-[-2px] hover:translate-y-[-2px]",
            tier.popular
              ? [
                  "bg-amber-400 text-zinc-900",
                  "hover:bg-amber-300",
                  "active:bg-amber-400",
                  "dark:hover:bg-amber-300",
                  "dark:active:bg-amber-400",
                ]
              : [
                  "bg-zinc-50 dark:bg-zinc-800",
                  "text-zinc-900 dark:text-white",
                  "hover:bg-white dark:hover:bg-zinc-700",
                  "active:bg-zinc-50 dark:active:bg-zinc-800",
                ]
          );

          return (
            <div
              key={`${tier.name}-${index}`}
              className={cn(
                "relative group transition-all duration-300",
                rotationClass
              )}
            >
              <div
                className={cn(
                  "absolute inset-0 bg-white dark:bg-zinc-900",
                  "border-2 border-zinc-900 dark:border-white",
                  "rounded-lg shadow-[4px_4px_0px_0px] shadow-zinc-900 dark:shadow-white",
                  "transition-all duration-300",
                  "group-hover:shadow-[8px_8px_0px_0px]",
                  "group-hover:translate-x-[-4px]",
                  "group-hover:translate-y-[-4px]"
                )}
              />

              <div className="relative p-6">
                {tier.popular && (
                  <div className="absolute -top-2 -right-2 bg-amber-400 text-zinc-900 font-handwritten px-3 py-1 rounded-full rotate-12 text-sm border-2 border-zinc-900">
                    {highlightText}
                  </div>
                )}

                <div className="mb-6">
                  <div
                    className={cn(
                      "w-12 h-12 rounded-full mb-4 flex items-center justify-center",
                      "border-2 border-zinc-900 dark:border-white",
                      iconColorClass
                    )}
                  >
                    <DynamicIcon name={tier.icon.name} className="w-6 h-6" />
                  </div>
                  <h3 className="font-handwritten text-2xl text-zinc-900 dark:text-white">
                    {tier.name}
                  </h3>
                  <p className="font-handwritten text-zinc-600 dark:text-zinc-400">
                    {tier.description}
                  </p>
                </div>

                <div className="mb-6 font-handwritten">
                  <span className="text-4xl font-bold text-zinc-900 dark:text-white">
                    ${tier.price}
                  </span>
                  <span className="text-zinc-600 dark:text-zinc-400">{tierPriceSuffix}</span>
                </div>

                <div className="space-y-3 mb-6">
                  {tier.features.map((feature, featureIndex) => (
                    <div
                      key={`${feature}-${featureIndex}`}
                      className="flex items-center gap-3"
                    >
                      <div className="w-5 h-5 rounded-full border-2 border-zinc-900 dark:border-white flex items-center justify-center">
                        <DynamicIcon name={checkIcon.name} className="w-3 h-3" />
                      </div>
                      <span className="font-handwritten text-lg text-zinc-900 dark:text-white">
                        {feature}
                      </span>
                    </div>
                  ))}
                </div>

                <Button asChild>
                  <Link href={tier.ctaHref} target={target} rel={rel} className={buttonClassName}>
                    {tier.ctaLabel}
                  </Link>
                </Button>
              </div>
            </div>
          );
        })}
      </div>

      {backgroundDecorations.length > 0 && (
        <div className="absolute -z-10 inset-0 overflow-hidden">
          {backgroundDecorations.map((decoration, decorationIndex) => (
            <div
              key={`${decoration.content}-${decorationIndex}`}
              className={decoration.className}
            >
              {decoration.content}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export type { PricingTier, CreativePricingProps };
export { CreativePricing };
