import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";
import Link from "@/components/Link";

// Icon interface as per guidelines
export interface Icon {
  package: 'lucide';
  name: string; // known lucide react icon-name only
  type: 'icon';
  className: string; // tailwind classes for styling
}

// ImageProps interface as per guidelines
export interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface PricingTier {
  name: string;
  icon?: Icon;
  price: number;
  description: string;
  features: string[];
  popular?: boolean;
  ctaLabel: string;
  ctaHref: string;
  ctaTarget?: '_blank' | '_self';
  ctaRel?: string;
}

export interface CreativePricingProps {
  tag?: string;
  title: string;
  description: string;
  tiers: PricingTier[];
  priceSuffix?: string; // e.g., "/month"
  popularBadgeText?: string; // e.g., "Popular!"
  titleRightDecoration?: string; // e.g., "✨"
  titleLeftDecoration?: string; // e.g., "⭐️"
  decorTopLeft?: string; // e.g., "✎"
  decorBottomRight?: string; // e.g., "✏️"
  className?: string;
  backgroundImage?: ImageProps; // optional background image
  backgroundColor?: string; // optional background color
}

export function CreativePricing({
  tag,
  title,
  description,
  tiers,
  priceSuffix,
  popularBadgeText,
  titleRightDecoration,
  titleLeftDecoration,
  decorTopLeft,
  decorBottomRight,
  className,
  backgroundImage,
  backgroundColor,
}: CreativePricingProps) {
  return (
    <div
      className={cn("relative w-full max-w-6xl mx-auto px-4", className)}
      style={backgroundColor ? { backgroundColor } : undefined}
    >
      {backgroundImage && (
        <div className="absolute inset-0 -z-10 overflow-hidden">
          <Image
            {...backgroundImage}
            className={cn(
              "absolute inset-0 h-full w-full object-cover",
              backgroundImage.className
            )}
          />
        </div>
      )}

      <div className="text-center space-y-6 mb-16">
        {tag && (
          <div className="font-handwritten text-xl text-blue-500 rotate-[-1deg]">{tag}</div>
        )}
        <div className="relative">
          <h2 className="text-4xl md:text-5xl font-bold font-handwritten text-zinc-900 dark:text-white rotate-[-1deg]">
            {title}
            {titleRightDecoration && (
              <div className="absolute -right-12 top-0 text-amber-500 rotate-12">
                {titleRightDecoration}
              </div>
            )}
            {titleLeftDecoration && (
              <div className="absolute -left-8 bottom-0 text-blue-500 -rotate-12">
                {titleLeftDecoration}
              </div>
            )}
          </h2>
          <div
            className="absolute -bottom-4 left-1/2 -translate-x-1/2 w-44 h-3 bg-blue-500/20 
                        rotate-[-1deg] rounded-full blur-sm"
          />
        </div>
        <p className="font-handwritten text-xl text-zinc-600 dark:text-zinc-400 rotate-[-1deg]">
          {description}
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {tiers.map((tier, index) => (
          <div
            key={tier.name}
            className={cn(
              "relative group",
              "transition-all duration-300",
              index === 0 && "rotate-[-1deg]",
              index === 1 && "rotate-[1deg]",
              index === 2 && "rotate-[-2deg]"
            )}
          >
            <div
              className={cn(
                "absolute inset-0 bg-white dark:bg-zinc-900",
                "border-2 border-zinc-900 dark:border-white",
                "rounded-lg shadow-[4px_4px_0px_0px] shadow-zinc-900 dark:shadow-white",
                "transition-all duration-300",
                "group-hover:shadow-[8px_8px_0px_0px]",
                "group-hover:translate-x-[-4px]",
                "group-hover:translate-y-[-4px]"
              )}
            />

            <div className="relative p-6">
              {tier.popular && popularBadgeText && (
                <div
                  className="absolute -top-2 -right-2 bg-amber-400 text-zinc-900 
                                    font-handwritten px-3 py-1 rounded-full rotate-12 text-sm border-2 border-zinc-900"
                >
                  {popularBadgeText}
                </div>
              )}

              <div className="mb-6">
                <div
                  className={cn(
                    "w-12 h-12 rounded-full mb-4",
                    "flex items-center justify-center",
                    "border-2 border-zinc-900 dark:border-white"
                  )}
                >
                  {tier.icon && (
                    <DynamicIcon name={tier.icon.name} className={tier.icon.className} />
                  )}
                </div>
                <h3 className="font-handwritten text-2xl text-zinc-900 dark:text-white">
                  {tier.name}
                </h3>
                <p className="font-handwritten text-zinc-600 dark:text-zinc-400">
                  {tier.description}
                </p>
              </div>

              {/* Price */}
              <div className="mb-6 font-handwritten">
                <span className="text-4xl font-bold text-zinc-900 dark:text-white">
                  ${tier.price}
                </span>
                {priceSuffix && (
                  <span className="text-zinc-600 dark:text-zinc-400">{priceSuffix}</span>
                )}
              </div>

              <div className="space-y-3 mb-6">
                {tier.features.map((feature) => (
                  <div key={feature} className="flex items-center gap-3">
                    <div
                      className="w-5 h-5 rounded-full border-2 border-zinc-900 
                                            dark:border-white flex items-center justify-center"
                    >
                      <DynamicIcon name="Check" className="w-3 h-3" />
                    </div>
                    <span className="font-handwritten text-lg text-zinc-900 dark:text-white">
                      {feature}
                    </span>
                  </div>
                ))}
              </div>

              <Button
                asChild
                className={cn(
                  "w-full h-12 font-handwritten text-lg relative",
                  "border-2 border-zinc-900 dark:border-white",
                  "transition-all duration-300",
                  "shadow-[4px_4px_0px_0px] shadow-zinc-900 dark:shadow-white",
                  "hover:shadow-[6px_6px_0px_0px]",
                  "hover:translate-x-[-2px] hover:translate-y-[-2px]",
                  tier.popular
                    ? [
                        "bg-amber-400 text-zinc-900",
                        "hover:bg-amber-300",
                        "active:bg-amber-400",
                        "dark:hover:bg-amber-300",
                        "dark:active:bg-amber-400",
                      ]
                    : [
                        "bg-zinc-50 dark:bg-zinc-800",
                        "text-zinc-900 dark:text-white",
                        "hover:bg-white dark:hover:bg-zinc-700",
                        "active:bg-zinc-50 dark:active:bg-zinc-800",
                      ]
                )}
              >
                <Link href={tier.ctaHref} target={tier.ctaTarget} rel={tier.ctaRel}>
                  {tier.ctaLabel}
                </Link>
              </Button>
            </div>
          </div>
        ))}
      </div>

      {(decorTopLeft || decorBottomRight) && (
        <div className="absolute -z-10 inset-0 overflow-hidden">
          {decorTopLeft && (
            <div className="absolute top-40 left-20 text-4xl rotate-12">{decorTopLeft}</div>
          )}
          {decorBottomRight && (
            <div className="absolute bottom-40 right-20 text-4xl -rotate-12">{decorBottomRight}</div>
          )}
        </div>
      )}
    </div>
  );
}
