"use client";

import { useState } from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";

interface Icon {
  package: "lucide";
  name: string;
  type: "icon";
}

type BillingCycle = "monthly" | "yearly";

interface Feature {
  name: string;
  description: string;
  included: boolean;
}

interface PricingTierPrice {
  monthly: number;
  yearly: number;
  currencySymbol?: string;
}

interface PricingTierCta {
  label: string;
  highlightLabel?: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

interface PricingTier {
  name: string;
  price: PricingTierPrice;
  description: string;
  features: Feature[];
  highlight?: boolean;
  badge?: string;
  icon: Icon;
  cta: PricingTierCta;
}

interface PricingSectionProps {
  title: string;
  billingCycleLabels?: Record<BillingCycle, string>;
  billingPeriodSuffix?: Record<BillingCycle, string>;
  tiers: PricingTier[];
  includedFeatureIcon?: Icon;
  excludedFeatureIcon?: Icon;
  ctaIcon?: Icon;
  className?: string;
}

const DEFAULT_INCLUDED_ICON: Icon = {
  package: "lucide",
  type: "icon",
  name: "Check",
};

const DEFAULT_EXCLUDED_ICON: Icon = {
  package: "lucide",
  type: "icon",
  name: "Minus",
};

const DEFAULT_CTA_ICON: Icon = {
  package: "lucide",
  type: "icon",
  name: "ArrowRight",
};

const DEFAULT_BILLING_CYCLE_LABELS: Record<BillingCycle, string> = {
  monthly: "Monthly",
  yearly: "Yearly",
};

const DEFAULT_BILLING_PERIOD_SUFFIX: Record<BillingCycle, string> = {
  monthly: "month",
  yearly: "year",
};

const DEFAULT_CURRENCY_SYMBOL = "$";

const buttonStyles = {
  default: cn(
    "h-12 bg-white dark:bg-zinc-900",
    "hover:bg-zinc-50 dark:hover:bg-zinc-800",
    "text-zinc-900 dark:text-zinc-100",
    "border border-zinc-200 dark:border-zinc-800",
    "hover:border-zinc-300 dark:hover:border-zinc-700",
    "shadow-sm hover:shadow-md",
    "text-sm font-medium"
  ),
  highlight: cn(
    "h-12 bg-zinc-900 dark:bg-zinc-100",
    "hover:bg-zinc-800 dark:hover:bg-zinc-200",
    "text-white dark:text-zinc-900",
    "shadow-lg hover:shadow-xl",
    "font-semibold text-base"
  ),
};

function PricingSection({
  title,
  billingCycleLabels = DEFAULT_BILLING_CYCLE_LABELS,
  billingPeriodSuffix = DEFAULT_BILLING_PERIOD_SUFFIX,
  tiers,
  includedFeatureIcon = DEFAULT_INCLUDED_ICON,
  excludedFeatureIcon = DEFAULT_EXCLUDED_ICON,
  ctaIcon = DEFAULT_CTA_ICON,
  className,
}: PricingSectionProps) {
  const [billingCycle, setBillingCycle] = useState<BillingCycle>("monthly");

  const billingOptions: BillingCycle[] = ["monthly", "yearly"];

  return (
    <section
      className={cn(
        "relative text-foreground",
        "py-12 px-4 md:py-24 lg:py-32",
        "overflow-hidden",
        className
      )}
    >
      <div className="mx-auto w-full max-w-5xl">
        <div className="mb-12 flex flex-col items-center gap-4">
          <h2 className="text-3xl font-bold text-zinc-900 dark:text-zinc-50">
            {title}
          </h2>
          <div className="inline-flex items-center rounded-full border border-zinc-200 bg-white p-1.5 shadow-sm dark:border-zinc-700 dark:bg-zinc-800/50">
            {billingOptions.map((period) => (
              <button
                key={period}
                type="button"
                onClick={() => setBillingCycle(period)}
                className={cn(
                  "rounded-full px-8 py-2.5 text-sm font-medium transition-all duration-300",
                  billingCycle === period
                    ? "bg-zinc-900 text-white shadow-lg dark:bg-zinc-100 dark:text-zinc-900"
                    : "text-zinc-600 hover:text-zinc-900 dark:text-zinc-400 dark:hover:text-zinc-100"
                )}
                aria-pressed={billingCycle === period}
              >
                {billingCycleLabels[period]}
              </button>
            ))}
          </div>
        </div>

        <div className="grid grid-cols-1 gap-8 md:grid-cols-2">
          {tiers.map((tier) => {
            const currencySymbol = tier.price.currencySymbol ?? DEFAULT_CURRENCY_SYMBOL;
            const activePrice =
              billingCycle === "yearly" ? tier.price.yearly : tier.price.monthly;
            const periodSuffix = billingPeriodSuffix[billingCycle];
            const ctaLabel =
              tier.highlight && tier.cta.highlightLabel
                ? tier.cta.highlightLabel
                : tier.cta.label;
            const featureIconForState = (included: boolean) =>
              included ? includedFeatureIcon : excludedFeatureIcon;
            const linkRel =
              tier.cta.rel ??
              (tier.cta.target === "_blank" ? "noopener noreferrer" : undefined);

            return (
              <div
                key={tier.name}
                className={cn(
                  "group relative flex flex-col rounded-3xl backdrop-blur-sm transition-all duration-300",
                  tier.highlight
                    ? "border border-zinc-400/50 shadow-xl dark:border-zinc-400/20"
                    : "border border-zinc-200 shadow-md dark:border-zinc-700",
                  tier.highlight
                    ? "bg-gradient-to-b from-zinc-100/80 to-transparent dark:from-zinc-400/15"
                    : "bg-white dark:bg-zinc-800/50",
                  "hover:translate-y-0 hover:shadow-lg"
                )}
              >
                {tier.badge && tier.highlight && (
                  <div className="absolute -top-4 left-6">
                    <Badge className="bg-zinc-900 px-4 py-1.5 text-sm font-medium text-white shadow-lg dark:bg-zinc-100 dark:text-zinc-900">
                      {tier.badge}
                    </Badge>
                  </div>
                )}

                <div className="flex-1 p-8">
                  <div className="mb-4 flex items-center justify-between">
                    <div
                      className={cn(
                        "rounded-xl p-3",
                        tier.highlight
                          ? "bg-zinc-100 text-zinc-900 dark:bg-zinc-800 dark:text-zinc-100"
                          : "bg-zinc-100 text-zinc-600 dark:bg-zinc-800 dark:text-zinc-400"
                      )}
                    >
                      <DynamicIcon name={tier.icon.name} className="h-6 w-6" />
                    </div>
                    <h3 className="text-xl font-semibold text-zinc-900 dark:text-zinc-100">
                      {tier.name}
                    </h3>
                  </div>

                  <div className="mb-6">
                    <div className="flex items-baseline gap-2">
                      <span className="text-4xl font-bold text-zinc-900 dark:text-zinc-100">
                        {currencySymbol}
                        {activePrice}
                      </span>
                      <span className="text-sm text-zinc-500 dark:text-zinc-400">
                        /{periodSuffix}
                      </span>
                    </div>
                    <p className="mt-2 text-sm text-zinc-600 dark:text-zinc-400">
                      {tier.description}
                    </p>
                  </div>

                  <div className="space-y-4">
                    {tier.features.map((feature) => {
                      const featureIcon = featureIconForState(feature.included);

                      return (
                        <div key={feature.name} className="flex gap-4">
                          <div
                            className={cn(
                              "mt-1 rounded-full p-0.5 transition-colors duration-200",
                              feature.included
                                ? "text-emerald-600 dark:text-emerald-400"
                                : "text-zinc-400 dark:text-zinc-600"
                            )}
                          >
                            <DynamicIcon name={featureIcon.name} className="h-4 w-4" />
                          </div>
                          <div>
                            <div className="text-sm font-medium text-zinc-900 dark:text-zinc-100">
                              {feature.name}
                            </div>
                            <div className="text-sm text-zinc-500 dark:text-zinc-400">
                              {feature.description}
                            </div>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </div>

                <div className="mt-auto p-8 pt-0">
                  <Button
                    asChild
                    className={cn(
                      "w-full transition-all duration-300",
                      tier.highlight ? buttonStyles.highlight : buttonStyles.default
                    )}
                  >
                    <Link
                      href={tier.cta.href}
                      target={tier.cta.target}
                      rel={linkRel}
                      className="flex w-full items-center justify-center gap-2"
                    >
                      {ctaLabel}
                      <DynamicIcon name={ctaIcon.name} className="h-4 w-4" />
                    </Link>
                  </Button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
}

export { PricingSection };
export type {
  PricingSectionProps,
  PricingTier,
  PricingTierCta,
  PricingTierPrice,
  Feature,
  Icon,
};

import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  "inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground hover:bg-primary/80",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80",
        destructive:
          "border-transparent bg-destructive text-destructive-foreground hover:bg-destructive/80",
        outline: "text-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
)

export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return (
    <div className={cn(badgeVariants({ variant }), className)} {...props} />
  )
}

export { Badge, badgeVariants }

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground hover:bg-destructive/90",
        outline:
          "border border-input bg-background hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-10 px-4 py-2",
        sm: "h-9 rounded-md px-3",
        lg: "h-11 rounded-md px-8",
        icon: "h-10 w-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  },
)
Button.displayName = "Button"

export { Button, buttonVariants }
