import React from "react";
import Image from "@/components/Image";

export interface StatsSectionProps {
  title: string;
  description: string;
  stats: Array<{ value: string; label: string }>;
  className?: string;
  backgroundImage?: {
    src: string;
    alt: string;
    width: number;
    height: number;
    priority?: boolean;
    quality?: number;
    sizes?: string;
    fill?: boolean;
    className?: string;
  };
  backgroundColor?: string;
}

export default function StatsSection({
  title,
  description,
  stats,
  className,
  backgroundImage,
  backgroundColor,
}: StatsSectionProps) {
  return (
    <section
      className={["relative py-12 md:py-20", className].filter(Boolean).join(" ")}
      style={backgroundColor ? ({ backgroundColor } as React.CSSProperties) : undefined}
    >
      {backgroundImage ? (
        <div className="absolute inset-0 -z-10">
          <Image
            src={backgroundImage.src}
            alt={backgroundImage.alt}
            width={backgroundImage.width}
            height={backgroundImage.height}
            priority={backgroundImage.priority}
            quality={backgroundImage.quality}
            sizes={backgroundImage.sizes}
            fill={backgroundImage.fill}
            className={["object-cover object-center", backgroundImage.className]
              .filter(Boolean)
              .join(" ")}
          />
        </div>
      ) : null}

      <div className="mx-auto max-w-5xl space-y-8 px-6 md:space-y-16">
        <div className="relative z-10 mx-auto max-w-xl space-y-6 text-center">
          <h2 className="text-4xl font-medium lg:text-5xl">{title}</h2>
          <p>{description}</p>
        </div>

        <div className="grid gap-12 divide-y *:text-center md:grid-cols-3 md:gap-2 md:divide-x md:divide-y-0">
          {stats.map((item, idx) => (
            <div key={idx} className="space-y-4">
              <div className="text-5xl font-bold">{item.value}</div>
              <p>{item.label}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
