import React from "react";
import Image from "@/components/Image";

export interface TeamMember {
  name: string;
  role: string;
  avatar: {
    src: string;
    alt: string;
    width: number;
    height: number;
    priority?: boolean;
    quality?: number;
    sizes?: string;
    fill?: boolean;
    className?: string;
    placeholder?: string;
  };
}

export interface TeamGroup {
  heading: string;
  members: TeamMember[];
}

export interface TeamSectionProps {
  title: string;
  sections: TeamGroup[];
  backgroundImage?: {
    src: string;
    alt: string;
    width: number;
    height: number;
    priority?: boolean;
    quality?: number;
    sizes?: string;
    fill?: boolean;
    className?: string;
    placeholder?: string;
  };
  backgroundColor?: string;
  className?: string;
}

export default function TeamSection({ title, sections, backgroundImage, backgroundColor, className }: TeamSectionProps) {
  return (
    <section className={`relative py-12 md:py-32 ${className ? className : ""}`} style={backgroundColor ? { backgroundColor } : undefined}>
      {backgroundImage ? (
        <div className="absolute inset-0 -z-10">
          <Image {...backgroundImage} />
        </div>
      ) : null}

      <div className="mx-auto max-w-3xl px-8 lg:px-0">
        <h2 className="mb-8 text-4xl font-bold md:mb-16 lg:text-5xl">{title}</h2>

        {sections.map((group, sectionIndex) => (
          <div key={sectionIndex} className={sectionIndex === 0 ? undefined : "mt-6"}>
            <h3 className="mb-6 text-lg font-medium">{group.heading}</h3>
            <div className="grid grid-cols-2 gap-4 border-t py-6 md:grid-cols-4" data-rounded="full">
              {group.members.map((member, memberIndex) => (
                <div key={`${sectionIndex}-${memberIndex}-${member.name}`}>
                  <div className="bg-background size-20 rounded-full border p-0.5 shadow shadow-zinc-950/5">
                    <Image
                      src={member.avatar.src}
                      alt={member.avatar.alt}
                      width={member.avatar.width}
                      height={member.avatar.height}
                      priority={member.avatar.priority}
                      quality={member.avatar.quality}
                      sizes={member.avatar.sizes}
                      fill={member.avatar.fill}
                      className={`aspect-square rounded-full object-cover ${member.avatar.className ? member.avatar.className : ""}`}
                      placeholder={member.avatar.placeholder}
                    />
                  </div>
                  <span className="mt-2 block text-sm">{member.name}</span>
                  <span className="text-muted-foreground block text-xs">{member.role}</span>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
