import React from "react";
import Image from "@/components/Image";

export type TeamMemberAvatar = {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  type?: "background" | "profile" | "logo" | "banner" | "gallery" | "thumbnail" | "icon" | "other";
  placeholder?: "blur" | "empty";
  blurDataURL?: string;
};

export interface TeamMember {
  name: string;
  role: string;
  avatar: TeamMemberAvatar;
}

export interface TeamGroup {
  title: string;
  members: TeamMember[];
}

export interface TeamSectionProps {
  sectionTitle?: string;
  groups?: TeamGroup[];
  sectionClassName?: string;
  containerClassName?: string;
}

const defaultTeamMembers: TeamMember[] = [
  {
    name: "Méschac Irung",
    role: "Creator",
    avatar: {
      src: "https://avatars.githubusercontent.com/u/47919550?v=4",
      alt: "Portrait of Méschac Irung",
      width: 460,
      height: 460,
      type: "profile",
    },
  },
  {
    name: "Théo Balick",
    role: "Frontend Dev",
    avatar: {
      src: "https://avatars.githubusercontent.com/u/68236786?v=4",
      alt: "Portrait of Théo Balick",
      width: 460,
      height: 460,
      type: "profile",
    },
  },
  {
    name: "Glodie Lukose",
    role: "Frontend Dev",
    avatar: {
      src: "https://avatars.githubusercontent.com/u/99137927?v=4",
      alt: "Portrait of Glodie Lukose",
      width: 460,
      height: 460,
      type: "profile",
    },
  },
  {
    name: "Bernard Ngandu",
    role: "Backend Dev",
    avatar: {
      src: "https://avatars.githubusercontent.com/u/31113941?v=4",
      alt: "Portrait of Bernard Ngandu",
      width: 460,
      height: 460,
      type: "profile",
    },
  },
];

const createDefaultGroupMembers = (): TeamMember[] =>
  defaultTeamMembers.map((member) => ({
    ...member,
    avatar: { ...member.avatar },
  }));

const defaultTeamGroups: TeamGroup[] = [
  {
    title: "Leadership",
    members: createDefaultGroupMembers(),
  },
  {
    title: "Engineering",
    members: createDefaultGroupMembers(),
  },
  {
    title: "Marketing",
    members: createDefaultGroupMembers(),
  },
];

const TeamSection: React.FC<TeamSectionProps> = ({
  sectionTitle = "Our team",
  groups = defaultTeamGroups,
  sectionClassName,
  containerClassName,
}) => {
  const sectionClasses = ["py-12 md:py-32", sectionClassName].filter(Boolean).join(" ");
  const containerClasses = ["mx-auto max-w-3xl px-8 lg:px-0", containerClassName].filter(Boolean).join(" ");

  return (
    <section className={sectionClasses}>
      <div className={containerClasses}>
        <h2 className="mb-8 text-4xl font-bold md:mb-16 lg:text-5xl">{sectionTitle}</h2>
        {groups.map((group, groupIndex) => {
          const groupWrapperClassName = groupIndex === 0 ? undefined : "mt-6";

          return (
            <div key={group.title} className={groupWrapperClassName}>
              <h3 className="mb-6 text-lg font-medium">{group.title}</h3>
              <div className="grid grid-cols-2 gap-4 border-t py-6 md:grid-cols-4">
                {group.members.map((member) => (
                  <div key={`${group.title}-${member.name}`}>
                    <div className="bg-background size-20 rounded-full border p-0.5 shadow shadow-zinc-950/5">
                      <Image
                        src={member.avatar.src}
                        alt={member.avatar.alt}
                        width={member.avatar.width}
                        height={member.avatar.height}
                        className="aspect-square rounded-full object-cover"
                        type={member.avatar.type}
                        placeholder={member.avatar.placeholder}
                        blurDataURL={member.avatar.blurDataURL}
                      />
                    </div>
                    <span className="mt-2 block text-sm">{member.name}</span>
                    <span className="text-muted-foreground block text-xs">{member.role}</span>
                  </div>
                ))}
              </div>
            </div>
          );
        })}
      </div>
    </section>
  );
};

export default TeamSection;
