'use client';

import { useState } from "react";
import Link from "@/components/Link";
import { cn } from "@/lib/utils";

interface NavMenuItem {
  label: string;
  href: string;
}

interface ToggleButtonAriaLabels {
  open: string;
  close: string;
}

export interface NavMenuProps {
  menuItems?: NavMenuItem[];
  className?: string;
  toggleButtonAriaLabels?: ToggleButtonAriaLabels;
}

const defaultMenuItems: NavMenuItem[] = [
  { label: "Home", href: "#home" },
  { label: "About", href: "#about" },
  { label: "Services", href: "#services" },
  { label: "Team", href: "#team" },
  { label: "Portfolio", href: "#portfolio" },
  { label: "Contact", href: "#contact" },
];

const defaultToggleButtonAriaLabels: ToggleButtonAriaLabels = {
  open: "Open menu",
  close: "Close menu",
};

export default function NavMenu({
  menuItems = defaultMenuItems,
  className,
  toggleButtonAriaLabels = defaultToggleButtonAriaLabels,
}: NavMenuProps): JSX.Element {
  const [isMenuOpen, setIsMenuOpen] = useState<boolean>(false);

  const toggleMenu = () => {
    setIsMenuOpen((previousState) => !previousState);
  };

  const navClassName = cn("absolute inset-0 w-full", className);
  const menuContainerClassName = cn(
    "flex items-center justify-center w-full h-full md:block md:h-auto md:pt-8",
    isMenuOpen ? "block" : "hidden md:block"
  );

  return (
    <nav className={navClassName}>
      <button
        type="button"
        onClick={toggleMenu}
        className="md:hidden absolute top-6 right-6 z-20 p-2"
        aria-label={isMenuOpen ? toggleButtonAriaLabels.close : toggleButtonAriaLabels.open}
      >
        <span
          className={cn(
            "block w-6 h-0.5 bg-neutral-900 mb-1.5 transition-transform duration-300",
            isMenuOpen ? "rotate-45 translate-y-2" : ""
          )}
        />
        <span
          className={cn(
            "block w-6 h-0.5 bg-neutral-900 mb-1.5 transition-opacity duration-300",
            isMenuOpen ? "opacity-0" : ""
          )}
        />
        <span
          className={cn(
            "block w-6 h-0.5 bg-neutral-900 transition-transform duration-300",
            isMenuOpen ? "-rotate-45 -translate-y-2" : ""
          )}
        />
      </button>

      <div className={menuContainerClassName}>
        <ul className="flex flex-col items-center space-y-6 md:flex-row md:space-y-0 md:space-x-4 md:justify-center lg:space-x-8">
          {menuItems.map((menuItem) => (
            <li
              key={menuItem.label}
              className="list-none"
              onClick={() => setIsMenuOpen(false)}
            >
              <Link
                href={menuItem.href}
                className="relative inline-block group"
                target="_self"
                rel="noopener noreferrer"
              >
                <span className="relative z-10 block uppercase text-neutral-900 font-sans font-semibold transition-colors duration-300 group-hover:text-white text-xl py-2 px-3 md:text-base md:py-2 md:px-3 lg:text-lg lg:py-2 lg:px-4">
                  {menuItem.label}
                </span>
                <span className="absolute inset-0 border-t-2 border-b-2 border-neutral-900 transform scale-y-[2] opacity-0 transition-all duration-300 origin-center group-hover:scale-y-100 group-hover:opacity-100" />
                <span className="absolute top-[2px] left-0 w-full h-full bg-neutral-900 transform scale-0 opacity-0 transition-all duration-300 origin-top group-hover:scale-100 group-hover:opacity-100" />
              </Link>
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
}
