import DynamicIcon from "@/components/DynamicIcon";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";
import {
  NavigationMenu,
  NavigationMenuContent,
  NavigationMenuItem,
  NavigationMenuLink,
  NavigationMenuList,
  NavigationMenuTrigger,
  NavigationMenuViewport,
} from "@/components/ui/navigation-menu";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { cn } from "@/lib/utils";

interface Icon {
  package: "lucide";
  name: string;
  type: "icon";
}

interface BaseNavigationItem {
  label: string;
  href: string;
}

interface DescriptionNavigationItem extends BaseNavigationItem {
  description: string;
}

interface IconNavigationItem extends BaseNavigationItem {
  icon: Icon;
}

type NavigationLink =
  | {
      label: string;
      href: string;
      submenu?: false;
    }
  | {
      label: string;
      submenu: true;
      type: "description";
      items: DescriptionNavigationItem[];
    }
  | {
      label: string;
      submenu: true;
      type: "simple";
      items: BaseNavigationItem[];
    }
  | {
      label: string;
      submenu: true;
      type: "icon";
      items: IconNavigationItem[];
    };

type SubmenuNavigationLink = Extract<NavigationLink, { submenu: true }>;

const isSubmenuLink = (
  navLink: NavigationLink,
): navLink is SubmenuNavigationLink => navLink.submenu === true;

export interface HeaderNavigationProps {
  className?: string;
  logoHref?: string;
  logoLabel?: string;
  navLinks?: NavigationLink[];
  signInHref?: string;
  signInLabel?: string;
  ctaHref?: string;
  ctaLabel?: string;
  mobileMenuAriaLabel?: string;
}

const defaultNavigationLinks: NavigationLink[] = [
  {
    label: "Home",
    href: "#",
  },
  {
    label: "Features",
    submenu: true,
    type: "description",
    items: [
      {
        href: "#",
        label: "Components",
        description: "Browse all components in the library.",
      },
      {
        href: "#",
        label: "Documentation",
        description: "Learn how to use the library.",
      },
      {
        href: "#",
        label: "Templates",
        description: "Pre-built layouts for common use cases.",
      },
    ],
  },
  {
    label: "Pricing",
    submenu: true,
    type: "simple",
    items: [
      { href: "#", label: "Product A" },
      { href: "#", label: "Product B" },
      { href: "#", label: "Product C" },
      { href: "#", label: "Product D" },
    ],
  },
  {
    label: "About",
    submenu: true,
    type: "icon",
    items: [
      {
        href: "#",
        label: "Getting Started",
        icon: { package: "lucide", name: "BookOpen", type: "icon" },
      },
      {
        href: "#",
        label: "Tutorials",
        icon: { package: "lucide", name: "LifeBuoy", type: "icon" },
      },
      {
        href: "#",
        label: "About Us",
        icon: { package: "lucide", name: "Info", type: "icon" },
      },
    ],
  },
];

export function HeaderNavigation({
  className,
  logoHref = "#",
  logoLabel = "Logo",
  navLinks = defaultNavigationLinks,
  signInHref = "#",
  signInLabel = "Sign In",
  ctaHref = "#",
  ctaLabel = "Get Started",
  mobileMenuAriaLabel = "Toggle navigation menu",
}: HeaderNavigationProps) {
  return (
    <header className={cn("border-b px-4 md:px-6", className)}>
      <div className="flex h-16 items-center justify-between gap-4">
        <div className="flex items-center gap-2">
          <Popover>
            <PopoverTrigger asChild>
              <Button
                aria-label={mobileMenuAriaLabel}
                className="group size-8 md:hidden"
                variant="ghost"
                size="icon"
              >
                <svg
                  className="pointer-events-none"
                  width={16}
                  height={16}
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    d="M4 12L20 12"
                    className="origin-center -translate-y-[7px] transition-all duration-300 ease-[cubic-bezier(.5,.85,.25,1.1)] group-aria-expanded:translate-x-0 group-aria-expanded:translate-y-0 group-aria-expanded:rotate-[315deg]"
                  />
                  <path
                    d="M4 12H20"
                    className="origin-center transition-all duration-300 ease-[cubic-bezier(.5,.85,.25,1.8)] group-aria-expanded:rotate-45"
                  />
                  <path
                    d="M4 12H20"
                    className="origin-center translate-y-[7px] transition-all duration-300 ease-[cubic-bezier(.5,.85,.25,1.1)] group-aria-expanded:translate-y-0 group-aria-expanded:rotate-[135deg]"
                  />
                </svg>
              </Button>
            </PopoverTrigger>
            <PopoverContent align="start" className="w-64 p-1 md:hidden">
              <NavigationMenu className="max-w-none *:w-full">
                <NavigationMenuList className="flex-col items-start gap-0 md:gap-2">
                  {navLinks.map((link, index) => {
                    const isLinkSubmenu = isSubmenuLink(link);
                    let showSeparator = false;

                    if (index < navLinks.length - 1) {
                      const nextLink = navLinks[index + 1];
                      const nextLinkIsSubmenu = nextLink
                        ? isSubmenuLink(nextLink)
                        : false;

                      if (isLinkSubmenu) {
                        if (!nextLinkIsSubmenu && nextLink) {
                          showSeparator = true;
                        } else if (
                          nextLinkIsSubmenu &&
                          nextLink &&
                          link.type !== nextLink.type
                        ) {
                          showSeparator = true;
                        }
                      } else if (nextLinkIsSubmenu) {
                        showSeparator = true;
                      }
                    }

                    return (
                      <NavigationMenuItem
                        key={`${link.label}-${index}`}
                        className="w-full"
                      >
                        {isLinkSubmenu ? (
                          <>
                            <div className="px-2 py-1.5 text-xs font-medium text-muted-foreground">
                              {link.label}
                            </div>
                            <ul>
                              {link.items.map((item, itemIndex) => (
                                <li key={`${item.label}-${itemIndex}`}>
                                  <NavigationMenuLink asChild>
                                    <Link
                                      href={item.href}
                                      className="block space-y-1 py-1.5"
                                    >
                                      {link.type === "icon" ? (
                                        <span className="flex items-center gap-2 text-sm font-medium leading-none">
                                          <DynamicIcon
                                            name={item.icon.name}
                                            className="h-4 w-4 text-foreground/60"
                                            aria-hidden="true"
                                          />
                                          {item.label}
                                        </span>
                                      ) : link.type === "description" ? (
                                        <>
                                          <div className="text-sm font-medium leading-none">
                                            {item.label}
                                          </div>
                                          <p className="text-xs text-muted-foreground">
                                            {item.description}
                                          </p>
                                        </>
                                      ) : (
                                        <div className="text-sm font-medium leading-none">
                                          {item.label}
                                        </div>
                                      )}
                                    </Link>
                                  </NavigationMenuLink>
                                </li>
                              ))}
                            </ul>
                          </>
                        ) : (
                          <NavigationMenuLink asChild>
                            <Link href={link.href} className="block py-1.5">
                              {link.label}
                            </Link>
                          </NavigationMenuLink>
                        )}
                        {showSeparator && (
                          <div
                            role="separator"
                            aria-orientation="horizontal"
                            className="-mx-1 my-1 h-px w-full bg-border"
                          />
                        )}
                      </NavigationMenuItem>
                    );
                  })}
                </NavigationMenuList>
              </NavigationMenu>
            </PopoverContent>
          </Popover>
          <div className="flex items-center gap-6">
            <Link href={logoHref} className="text-primary hover:text-primary/90">
              {logoLabel}
            </Link>
            <div className="max-md:hidden">
              <NavigationMenu>
                <NavigationMenuList>
                  {navLinks.map((link, index) => {
                    const isLinkSubmenu = isSubmenuLink(link);
                    return (
                      <NavigationMenuItem key={`${link.label}-${index}`}>
                        {isLinkSubmenu ? (
                          <>
                            <NavigationMenuTrigger className="bg-transparent px-2 py-1.5 font-medium text-muted-foreground hover:text-primary">
                              {link.label}
                            </NavigationMenuTrigger>
                            <NavigationMenuContent>
                              <ul
                                className={cn(
                                  "grid w-[400px] gap-3 p-4 md:w-[500px] md:grid-cols-2 lg:w-[600px]",
                                  link.type === "description" && "md:grid-cols-1",
                                )}
                              >
                                {link.items.map((item, itemIndex) => (
                                  <li key={`${item.label}-${itemIndex}`}>
                                    <NavigationMenuLink asChild>
                                      <Link
                                        href={item.href}
                                        className="block select-none space-y-1 rounded-md p-3 leading-none no-underline outline-none transition-colors hover:bg-accent hover:text-accent-foreground focus:bg-accent focus:text-accent-foreground"
                                      >
                                        {link.type === "icon" ? (
                                          <div className="flex items-center gap-2">
                                            <DynamicIcon
                                              name={item.icon.name}
                                              className="h-4 w-4 text-foreground/60"
                                              aria-hidden="true"
                                            />
                                            <div className="text-sm font-medium leading-none">
                                              {item.label}
                                            </div>
                                          </div>
                                        ) : link.type === "description" ? (
                                          <>
                                            <div className="text-sm font-medium leading-none">
                                              {item.label}
                                            </div>
                                            <p className="line-clamp-2 text-sm leading-snug text-muted-foreground">
                                              {item.description}
                                            </p>
                                          </>
                                        ) : (
                                          <div className="text-sm font-medium leading-none">
                                            {item.label}
                                          </div>
                                        )}
                                      </Link>
                                    </NavigationMenuLink>
                                  </li>
                                ))}
                              </ul>
                            </NavigationMenuContent>
                          </>
                        ) : (
                          <NavigationMenuLink asChild>
                            <Link
                              href={link.href}
                              className="px-2 py-1.5 font-medium text-muted-foreground hover:text-primary"
                            >
                              {link.label}
                            </Link>
                          </NavigationMenuLink>
                        )}
                      </NavigationMenuItem>
                    );
                  })}
                </NavigationMenuList>
                <NavigationMenuViewport />
              </NavigationMenu>
            </div>
          </div>
        </div>
        <div className="flex items-center gap-2">
          <Button asChild variant="ghost" size="sm" className="text-sm">
            <Link href={signInHref}>{signInLabel}</Link>
          </Button>
          <Button asChild size="sm" className="text-sm">
            <Link href={ctaHref}>{ctaLabel}</Link>
          </Button>
        </div>
      </div>
    </header>
  );
}

export default HeaderNavigation;
