import * as React from "react";
import { cn } from "@/lib/utils";
import Image from "@/components/Image";

type GradientFromClass = `from-${string}`;
type GradientViaClass = `via-${string}`;
type GradientToClass = `to-${string}`;

interface LogoGradient {
  from: GradientFromClass;
  via?: GradientViaClass;
  to: GradientToClass;
}

export interface LogoItem {
  src: string;
  alt: string;
  gradient?: LogoGradient;
  width?: number;
  height?: number;
}

type MarqueeSpeed = "normal" | "slow" | "fast";

export interface MarqueeLogoScrollerProps extends React.ComponentPropsWithoutRef<"section"> {
  title: string;
  description: string;
  logos: LogoItem[];
  speed?: MarqueeSpeed;
}

const MASK_STYLES: React.CSSProperties = {
  maskImage: "linear-gradient(to right, transparent, black 10%, black 90%, transparent)",
  WebkitMaskImage: "linear-gradient(to right, transparent, black 10%, black 90%, transparent)",
};

const DURATION_MAP: Record<MarqueeSpeed, string> = {
  normal: "40s",
  slow: "80s",
  fast: "5s",
};

export const MarqueeLogoScroller = React.forwardRef<React.ElementRef<"section">, MarqueeLogoScrollerProps>(
  ({ title, description, logos, speed = "normal", className, ...props }, ref) => {
    const animationDuration = React.useMemo(() => DURATION_MAP[speed], [speed]);
    const marqueeLogos = React.useMemo(() => [...logos, ...logos], [logos]);

    return (
      <>
        <style>{`@keyframes marquee { from { transform: translateX(0); } to { transform: translateX(-50%); } }`}</style>
        <section
          ref={ref}
          aria-label={title}
          className={cn("w-full text-foreground rounded-lg border overflow-hidden", className)}
          {...props}
        >
          <div className="p-6 md:p-8 lg:p-10">
            <div className="grid grid-cols-1 gap-6 pb-6 md:pb-8 border-b lg:grid-cols-[3fr_2fr] lg:gap-8">
              <h2 className="text-3xl font-semibold tracking-tighter text-balance md:text-4xl">{title}</h2>
              <p className="self-start text-balance text-muted-foreground lg:justify-self-end">{description}</p>
            </div>
          </div>
          <div className="w-full overflow-hidden" style={MASK_STYLES}>
            <div
              className="flex w-max items-center gap-4 py-4 pr-4 transition-all duration-300 ease-in-out hover:[animation-play-state:paused]"
              style={{ animation: `marquee ${animationDuration} linear infinite` }}
            >
              {marqueeLogos.map((logo, index) => (
                <div
                  key={`${logo.alt}-${index}`}
                  className="group relative flex h-24 w-40 shrink-0 items-center justify-center overflow-hidden rounded-lg bg-secondary/70"
                >
                  {logo.gradient && (
                    <div
                      className={cn(
                        "absolute inset-0 scale-150 bg-gradient-to-br opacity-0 transition-transform transition-opacity duration-700 ease-out group-hover:scale-100 group-hover:opacity-100",
                        logo.gradient.from,
                        logo.gradient.via,
                        logo.gradient.to
                      )}
                    />
                  )}
                  <Image
                    src={logo.src}
                    alt={logo.alt}
                    width={logo.width ?? 160}
                    height={logo.height ?? 96}
                    className="relative h-3/4 w-auto object-contain"
                    type="logo"
                  />
                </div>
              ))}
            </div>
          </div>
        </section>
      </>
    );
  }
);

MarqueeLogoScroller.displayName = "MarqueeLogoScroller";
