"use client";

import { motion } from "framer-motion";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";

export interface Pricing05Tier {
  title: string;
  monthlyPrice: number;
  buttonLabel: string;
  buttonHref: string;
  buttonTarget?: "_blank" | "_self";
  buttonRel?: string;
  popular?: boolean;
  inverse?: boolean;
  features: string[];
  badgeText?: string;
}

export interface Pricing05Props {
  heading?: string;
  description?: string;
  currencySymbol?: string;
  billingPeriodLabel?: string;
  tiers?: Pricing05Tier[];
  popularBadgeAnimationDuration?: number;
}

const defaultPricing05Tiers: Pricing05Tier[] = [
  {
    title: "Starter",
    monthlyPrice: 0,
    buttonLabel: "Start free",
    buttonHref: "/signup",
    buttonTarget: "_self",
    popular: false,
    inverse: false,
    features: [
      "Up to 5 team members",
      "Unlimited content projects",
      "2GB storage",
      "Basic integrations",
      "Community support"
    ]
  },
  {
    title: "Pro",
    monthlyPrice: 9,
    buttonLabel: "Upgrade now",
    buttonHref: "/pricing/pro",
    buttonTarget: "_self",
    popular: true,
    inverse: true,
    badgeText: "Popular",
    features: [
      "Up to 50 team members",
      "Unlimited AI-generated content",
      "50GB storage",
      "All integrations",
      "Priority support",
      "Content export",
      "Keyword analytics"
    ]
  },
  {
    title: "Business",
    monthlyPrice: 19,
    buttonLabel: "Contact sales",
    buttonHref: "/contact-sales",
    buttonTarget: "_self",
    popular: false,
    inverse: false,
    features: [
      "Unlimited team members",
      "200GB storage",
      "Dedicated AI workflows",
      "Custom branding",
      "Dedicated support manager",
      "API access",
      "Enterprise-grade security"
    ]
  }
];

const defaultPricing05Props: Required<Pricing05Props> = {
  heading: "Simple pricing for every team",
  description: "Whether you're starting small or scaling fast, Ruixen UI grows with your content needs.",
  currencySymbol: "$",
  billingPeriodLabel: "/month",
  tiers: defaultPricing05Tiers,
  popularBadgeAnimationDuration: 1
};

const Pricing05 = ({
  heading = defaultPricing05Props.heading,
  description = defaultPricing05Props.description,
  currencySymbol = defaultPricing05Props.currencySymbol,
  billingPeriodLabel = defaultPricing05Props.billingPeriodLabel,
  tiers = defaultPricing05Props.tiers,
  popularBadgeAnimationDuration = defaultPricing05Props.popularBadgeAnimationDuration
}: Pricing05Props): JSX.Element => {
  return (
    <section className="py-24">
      <div className="container">
        <div className="mx-auto max-w-2xl text-center">
          <h2 className="text-4xl font-semibold tracking-tight sm:text-5xl">
            {heading}
          </h2>
          <p className="mt-4 text-lg text-muted-foreground">
            {description}
          </p>
        </div>

        <div className="mt-12 flex flex-col items-center gap-6 lg:flex-row lg:items-end lg:justify-center">
          {tiers.map((tier) => {
            const {
              title,
              monthlyPrice,
              buttonLabel,
              buttonHref,
              buttonTarget = "_self",
              buttonRel,
              popular,
              inverse,
              features,
              badgeText
            } = tier;

            const resolvedRel = buttonRel ?? (buttonTarget === "_blank" ? "noopener noreferrer" : undefined);

            return (
              <Card
                key={title}
                className={`w-full max-w-xs border ${inverse ? "bg-black text-white" : ""}`}
              >
                <CardHeader className="flex items-start justify-between">
                  <CardTitle className={`text-lg font-bold ${inverse ? "text-white/70" : "text-muted-foreground"}`}>
                    {title}
                  </CardTitle>
                  {popular && badgeText ? (
                    <motion.div
                      initial={{ backgroundPositionX: "0%" }}
                      animate={{ backgroundPositionX: "-100%" }}
                      transition={{
                        duration: popularBadgeAnimationDuration,
                        repeat: Infinity,
                        ease: "linear",
                        repeatType: "loop"
                      }}
                      className="rounded-xl border border-white/20 bg-gradient-to-r from-pink-500 via-yellow-300 to-cyan-400 px-3 py-1 text-sm font-medium text-transparent bg-clip-text"
                      style={{ backgroundSize: "200% 100%" }}
                    >
                      {badgeText}
                    </motion.div>
                  ) : null}
                </CardHeader>
                <CardContent>
                  <div className="mt-2 flex items-baseline gap-1">
                    <span className="text-4xl font-bold leading-none tracking-tighter">
                      {currencySymbol}
                      {monthlyPrice}
                    </span>
                    <span className={`font-semibold tracking-tight ${inverse ? "text-white/60" : "text-muted-foreground"}`}>
                      {billingPeriodLabel}
                    </span>
                  </div>
                  <Button
                    asChild
                    variant={inverse ? "secondary" : "default"}
                    className="mt-6 w-full"
                  >
                    <Link
                      href={buttonHref}
                      target={buttonTarget}
                      rel={resolvedRel}
                      className="flex w-full items-center justify-center"
                    >
                      {buttonLabel}
                    </Link>
                  </Button>
                  <ul className="mt-6 flex flex-col gap-4 text-sm">
                    {features.map((feature) => (
                      <li key={feature} className="flex items-center gap-2">
                        <span>{feature}</span>
                      </li>
                    ))}
                  </ul>
                </CardContent>
              </Card>
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default Pricing05;
export type { Pricing05Tier };
export { defaultPricing05Props, defaultPricing05Tiers };
