"use client";

import React from "react";
import { motion } from "framer-motion";
import Link from "@/components/Link";
import Image from "@/components/Image";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";

// Local image props shape aligned with the system Image component interface
type BackgroundImageProps = {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
};

interface PricingTier {
  title: string;
  monthlyPrice: number;
  buttonText: string;
  buttonHref: string;
  buttonTarget?: "_blank" | "_self";
  popular?: boolean;
  inverse?: boolean;
  features: string[];
}

export interface Pricing05Props {
  heading: string;
  subheading: string;
  pricingTiers: PricingTier[];
  popularBadgeText: string; // e.g., "Popular"
  pricePrefix: string; // e.g., "$"
  priceSuffix: string; // e.g., "/month"
  backgroundColor?: string;
  backgroundImage?: BackgroundImageProps;
  sectionClassName?: string;
  containerClassName?: string;
}

export default function Pricing_05({
  heading,
  subheading,
  pricingTiers,
  popularBadgeText,
  pricePrefix,
  priceSuffix,
  backgroundColor,
  backgroundImage,
  sectionClassName,
  containerClassName,
}: Pricing05Props) {
  return (
    <section
      className={`py-24 dark:bg-background relative ${sectionClassName ?? ""}`}
      style={backgroundColor ? { backgroundColor } : undefined}
    >
      {backgroundImage ? (
        <div className="absolute inset-0 -z-10 overflow-hidden">
          <Image
            {...backgroundImage}
            className={`w-full h-full object-cover ${backgroundImage.className ?? ""}`}
          />
        </div>
      ) : null}

      <div className={`container ${containerClassName ?? ""}`}>
        <div className="text-center max-w-2xl mx-auto">
          <h2 className="text-4xl font-semibold tracking-tight sm:text-5xl">{heading}</h2>
          <p className="text-muted-foreground mt-4 text-lg">{subheading}</p>
        </div>

        <div className="flex flex-col gap-6 items-center mt-12 lg:flex-row lg:items-end lg:justify-center">
          {pricingTiers.map(({
            title,
            monthlyPrice,
            buttonText,
            buttonHref,
            buttonTarget,
            popular,
            inverse,
            features,
          }) => (
            <Card key={title} className={`max-w-xs w-full border ${inverse ? "bg-black text-white" : ""}`}>
              <CardHeader className="flex justify-between items-start">
                <CardTitle className={`text-lg font-bold ${inverse ? "text-white/70" : "text-muted-foreground"}`}>
                  {title}
                </CardTitle>
                {popular ? (
                  <motion.div
                    animate={{ backgroundPositionX: "-100%" }}
                    transition={{ duration: 1, repeat: Infinity, ease: "linear", repeatType: "loop" }}
                    className="text-sm px-3 py-1 rounded-xl border border-white/20 bg-[linear-gradient(to_right,#DD7DDF,#E1CD86,#BBCB92,#71C2EF,#3BFFFF)] [background-size:200%] text-transparent bg-clip-text font-medium"
                  >
                    {popularBadgeText}
                  </motion.div>
                ) : null}
              </CardHeader>
              <CardContent>
                <div className="flex items-baseline gap-1 mt-2">
                  <span className="text-4xl font-bold tracking-tighter leading-none">
                    {pricePrefix}
                    {monthlyPrice}
                  </span>
                  <span className={`tracking-tight font-semibold ${inverse ? "text-white/60" : "text-muted-foreground"}`}>
                    {priceSuffix}
                  </span>
                </div>
                <Button variant={inverse ? "secondary" : "default"} className="w-full mt-6" asChild>
                  <Link
                    href={buttonHref}
                    target={buttonTarget}
                    rel={buttonTarget === "_blank" ? "noopener noreferrer" : undefined}
                  >
                    {buttonText}
                  </Link>
                </Button>
                <ul className="flex flex-col gap-4 mt-6 text-sm">
                  {features.map((feature) => (
                    <li key={feature} className="flex items-center gap-2">
                      <span>{feature}</span>
                    </li>
                  ))}
                </ul>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </section>
  );
}
