import { cn } from "@/lib/utils";
import Link from "@/components/Link";
import Image from "@/components/Image";
import { Button } from "@/components/ui/button";
import type { FC } from "react";

// Local copy of the expected ImageProps shape for optional background image
interface BackgroundImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface CounterPanelProps {
  className?: string;
  title: string;
  count: number;
  decrementLabel: string;
  decrementHref: string;
  decrementTarget?: "_blank" | "_self";
  decrementRel?: string;
  incrementLabel: string;
  incrementHref: string;
  incrementTarget?: "_blank" | "_self";
  incrementRel?: string;
  backgroundColor?: string;
  backgroundImage?: BackgroundImageProps;
}

export const CounterPanel: FC<CounterPanelProps> = ({
  className,
  title,
  count,
  decrementLabel,
  decrementHref,
  decrementTarget,
  decrementRel,
  incrementLabel,
  incrementHref,
  incrementTarget,
  incrementRel,
  backgroundColor,
  backgroundImage,
}) => {
  const computedDecRel = decrementTarget === "_blank" ? (decrementRel ?? "noopener noreferrer") : decrementRel;
  const computedIncRel = incrementTarget === "_blank" ? (incrementRel ?? "noopener noreferrer") : incrementRel;

  return (
    <div
      className={cn(
        "relative overflow-hidden rounded-lg p-4 flex flex-col items-center gap-4",
        className
      )}
      style={{ backgroundColor }}
    >
      {backgroundImage && (
        <div className="absolute inset-0 -z-10">
          <Image
            {...backgroundImage}
            className={cn("h-full w-full object-cover", backgroundImage.className)}
          />
        </div>
      )}

      <h1 className="text-2xl font-bold mb-2">{title}</h1>
      <h2 className="text-xl font-semibold">{count}</h2>

      <div className="flex gap-2">
        <Button asChild>
          <Link
            href={decrementHref}
            target={decrementTarget}
            rel={computedDecRel}
            className="px-4 py-2"
          >
            {decrementLabel}
          </Link>
        </Button>

        <Button asChild>
          <Link
            href={incrementHref}
            target={incrementTarget}
            rel={computedIncRel}
            className="px-4 py-2"
          >
            {incrementLabel}
          </Link>
        </Button>
      </div>
    </div>
  );
};
