import { useState, type MouseEvent } from "react";
import { cn } from "@/lib/utils";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";

export interface CounterCardProps {
  title?: string;
  initialCount?: number;
  decrementLabel?: string;
  incrementLabel?: string;
  decrementHref?: string;
  incrementHref?: string;
  className?: string;
}

export const CounterCard = ({
  title = "Component Example",
  initialCount = 0,
  decrementLabel = "-",
  incrementLabel = "+",
  decrementHref = "#decrement",
  incrementHref = "#increment",
  className,
}: CounterCardProps): JSX.Element => {
  const [count, setCount] = useState<number>(initialCount);

  const handleDecrement = (event: MouseEvent<HTMLButtonElement | HTMLAnchorElement>) => {
    event.preventDefault();
    setCount((prevCount) => prevCount - 1);
  };

  const handleIncrement = (event: MouseEvent<HTMLButtonElement | HTMLAnchorElement>) => {
    event.preventDefault();
    setCount((prevCount) => prevCount + 1);
  };

  return (
    <div className={cn("flex flex-col items-center gap-4 p-4 rounded-lg", className)}>
      <h1 className="text-2xl font-bold mb-2">{title}</h1>
      <h2 className="text-xl font-semibold">{count}</h2>
      <div className="flex gap-2">
        <Button asChild onClick={handleDecrement}>
          <Link href={decrementHref} className="px-3 py-1">
            {decrementLabel}
          </Link>
        </Button>
        <Button asChild onClick={handleIncrement}>
          <Link href={incrementHref} className="px-3 py-1">
            {incrementLabel}
          </Link>
        </Button>
      </div>
    </div>
  );
};

export default CounterCard;
