"use client";

import { useEffect, useRef, useState } from "react";
import { motion } from "framer-motion";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";

interface Icon {
  package: "lucide";
  name: string;
  type: "icon";
}

interface FeatureItem {
  id: number | string;
  icon: Icon;
  title: string;
  description: string;
  imageSrc: string;
  imageAlt: string;
}

export interface FeaturesProps {
  features: FeatureItem[];
  eyebrowText?: string;
  headlineText?: string;
  className?: string;
  progressGradientLight?: string;
  progressGradientDark?: string;
  autoProgressInterval?: number;
  progressIncrement?: number;
  autoAdvanceDelay?: number;
}

export function Features({
  features,
  eyebrowText = "AI Mentors. Real Results.",
  headlineText = "AI That Actually Teaches",
  className,
  progressGradientLight = "bg-gradient-to-r from-sky-500 to-indigo-500",
  progressGradientDark = "dark:bg-gradient-to-r dark:from-sky-400 dark:to-indigo-500",
  autoProgressInterval = 100,
  progressIncrement = 1,
  autoAdvanceDelay = 200,
}: FeaturesProps) {
  const [currentFeature, setCurrentFeature] = useState(0);
  const [progress, setProgress] = useState(0);
  const featureRefs = useRef<(HTMLDivElement | null)[]>([]);
  const containerRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    if (features.length <= 1) {
      return;
    }

    const interval = window.setInterval(() => {
      setProgress((prevProgress) => {
        if (prevProgress >= 100) {
          return 100;
        }
        const nextProgress = prevProgress + progressIncrement;
        return nextProgress >= 100 ? 100 : nextProgress;
      });
    }, autoProgressInterval);

    return () => window.clearInterval(interval);
  }, [autoProgressInterval, progressIncrement, features.length]);

  useEffect(() => {
    if (progress < 100 || features.length === 0) {
      return;
    }

    const timeout = window.setTimeout(() => {
      setCurrentFeature((prev) => (prev + 1) % features.length);
      setProgress(0);
    }, autoAdvanceDelay);

    return () => window.clearTimeout(timeout);
  }, [progress, features.length, autoAdvanceDelay]);

  useEffect(() => {
    const activeFeatureElement = featureRefs.current[currentFeature];
    const container = containerRef.current;

    if (activeFeatureElement && container) {
      const containerRect = container.getBoundingClientRect();
      const elementRect = activeFeatureElement.getBoundingClientRect();

      container.scrollTo({
        left:
          activeFeatureElement.offsetLeft -
          (containerRect.width - elementRect.width) / 2,
        behavior: "smooth",
      });
    }
  }, [currentFeature]);

  useEffect(() => {
    setCurrentFeature(0);
    setProgress(0);
  }, [features]);

  const handleFeatureClick = (index: number) => {
    setCurrentFeature(index);
    setProgress(0);
  };

  if (features.length === 0) {
    return null;
  }

  const rootClassName = ["min-h-screen py-16 px-4", className].filter(Boolean).join(" ");

  return (
    <div className={rootClassName}>
      <div className="mx-auto max-w-7xl">
        <div className="mb-16 text-center">
          <span className="text-sm font-semibold uppercase tracking-wider text-sky-500">
            {eyebrowText}
          </span>
          <h2 className="mt-4 mb-6 text-4xl font-bold text-gray-900 dark:text-white md:text-5xl">
            {headlineText}
          </h2>
        </div>

        <div className="grid items-center gap-8 lg:grid-cols-2 lg:gap-16">
          <div
            ref={containerRef}
            className="no-scrollbar order-1 flex scroll-smooth flex-row overflow-x-auto overflow-hidden pb-4 md:space-x-6 lg:order-1 lg:flex lg:flex-col lg:space-x-0 lg:space-y-8 lg:overflow-visible"
          >
            {features.map((feature, index) => {
              const isActive = currentFeature === index;

              return (
                <div
                  key={feature.id}
                  ref={(el) => {
                    featureRefs.current[index] = el;
                  }}
                  className="relative flex-shrink-0 cursor-pointer"
                  onClick={() => handleFeatureClick(index)}
                >
                  <div
                    className={`flex max-w-sm flex-col items-start space-x-4 p-3 transition-all duration-300 md:max-w-sm lg:max-w-2xl lg:flex-row ${
                      isActive
                        ? "rounded-xl border border-gray-200 bg-white shadow-xl dark:border-none dark:bg-black/80 dark:drop-shadow-lg"
                        : ""
                    }`}
                  >
                    <div
                      className={`hidden rounded-full p-3 transition-all duration-300 md:block ${
                        isActive
                          ? "bg-sky-500 text-white"
                          : "bg-sky-500/10 text-sky-500 dark:bg-black/80"
                      }`}
                    >
                      <DynamicIcon name={feature.icon.name} className="h-6 w-6" />
                    </div>

                    <div className="flex-1">
                      <h3
                        className={`text-lg font-semibold transition-colors duration-300 md:mt-4 lg:mt-0 ${
                          isActive
                            ? "text-gray-900 dark:text-white"
                            : "text-gray-700 dark:text-white/80"
                        }`}
                      >
                        {feature.title}
                      </h3>
                      <p
                        className={`text-sm transition-colors duration-300 ${
                          isActive
                            ? "text-gray-600 dark:text-white/60"
                            : "text-gray-500 dark:text-white/40"
                        }`}
                      >
                        {feature.description}
                      </p>
                      <div className="mt-4 h-1 overflow-hidden rounded-sm bg-white dark:bg-black/80">
                        {isActive && (
                          <motion.div
                            className={["h-full", progressGradientLight, progressGradientDark]
                              .filter(Boolean)
                              .join(" ")}
                            initial={{ width: 0 }}
                            animate={{ width: `${progress}%` }}
                            transition={{ duration: 0.1, ease: "linear" }}
                          />
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          <div className="order-1 relative mx-auto max-w-lg lg:order-2">
            <motion.div
              key={features[currentFeature].id}
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -50 }}
              transition={{ duration: 0.5, ease: "easeOut" }}
              className="relative"
            >
              <Image
                className="rounded-2xl border border-gray-50 shadow-lg dark:border-none dark:drop-shadow-lg"
                src={features[currentFeature].imageSrc}
                alt={features[currentFeature].imageAlt}
                width={600}
                height={400}
                type="gallery"
              />
            </motion.div>
          </div>
        </div>
      </div>
    </div>
  );
}
