import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import DynamicIcon from "@/components/DynamicIcon";
import Link from "@/components/Link";

interface Icon {
  package: "lucide";
  name: string;
  type: "icon";
}

const bannerVariants = cva(
  "relative w-full",
  {
    variants: {
      variant: {
        default: "border border-border",
        muted: "border border-border text-muted-foreground",
        border: "border-b border-border",
      },
      size: {
        sm: "px-4 py-2",
        default: "px-4 py-3",
        lg: "px-4 py-3 md:py-2",
      },
      rounded: {
        none: "",
        default: "rounded-lg",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
      rounded: "none",
    },
  }
);

interface BannerActionLink {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
  buttonVariant?: React.ComponentProps<typeof Button>["variant"];
  buttonSize?: React.ComponentProps<typeof Button>["size"];
  buttonClassName?: string;
  linkClassName?: string;
}

const DEFAULT_CLOSE_ICON: Icon = {
  package: "lucide",
  name: "X",
  type: "icon",
};

interface BannerProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof bannerVariants> {
  leadingIcon?: Icon;
  leadingIconClassName?: string;
  actionLink?: BannerActionLink;
  onClose?: () => void;
  isClosable?: boolean;
  layout?: "row" | "center" | "complex";
  closeIcon?: Icon;
  closeButtonAriaLabel?: string;
  closeButtonClassName?: string;
  children?: React.ReactNode;
}

const Banner = React.forwardRef<HTMLDivElement, BannerProps>(
  (
    {
      className,
      variant,
      size,
      rounded,
      leadingIcon,
      leadingIconClassName,
      actionLink,
      onClose,
      isClosable,
      layout = "row",
      closeIcon = DEFAULT_CLOSE_ICON,
      closeButtonAriaLabel = "Close banner",
      closeButtonClassName,
      children,
      ...props
    },
    ref
  ) => {
    const shouldRenderActions = Boolean(actionLink || isClosable);

    const actionLinkRel =
      actionLink?.rel ??
      (actionLink?.target === "_blank" ? "noopener noreferrer" : undefined);

    const iconElement = leadingIcon ? (
      <div
        className={cn(
          "flex shrink-0 items-center gap-3",
          layout === "complex" && "max-md:mt-0.5"
        )}
      >
        <DynamicIcon
          name={leadingIcon.name}
          className={cn("size-5", leadingIconClassName)}
        />
      </div>
    ) : null;

    const closeButton = isClosable ? (
      <Button
        variant="ghost"
        className={cn(
          "group -my-1.5 -me-2 size-8 shrink-0 p-0 hover:bg-transparent",
          closeButtonClassName
        )}
        onClick={onClose}
        aria-label={closeButtonAriaLabel}
        type="button"
      >
        <DynamicIcon
          name={closeIcon.name}
          className="size-4 opacity-60 transition-opacity group-hover:opacity-100"
        />
      </Button>
    ) : null;

    const actionButton = actionLink ? (
      <Button
        asChild
        variant={actionLink.buttonVariant}
        size={actionLink.buttonSize}
        className={cn(actionLink.buttonClassName)}
      >
        <Link
          href={actionLink.href}
          target={actionLink.target}
          rel={actionLinkRel}
          className={cn(
            "inline-flex items-center justify-center",
            actionLink.linkClassName
          )}
        >
          {actionLink.label}
        </Link>
      </Button>
    ) : null;

    return (
      <div
        ref={ref}
        className={cn(bannerVariants({ variant, size, rounded }), className)}
        {...props}
      >
        <div
          className={cn(
            "flex gap-2",
            layout === "center" && "justify-center",
            layout === "complex" && "md:items-center"
          )}
        >
          {layout === "complex" ? (
            <div className="flex grow gap-3 md:items-center">
              {iconElement}
              <div
                className={cn(
                  "flex grow",
                  "flex-col justify-between gap-3 md:flex-row md:items-center"
                )}
              >
                {children}
              </div>
            </div>
          ) : (
            <>
              {iconElement}
              <div className="flex grow items-center justify-between gap-3">
                {children}
              </div>
            </>
          )}
          {shouldRenderActions && (
            <div className="flex items-center gap-3">
              {actionButton}
              {closeButton}
            </div>
          )}
        </div>
      </div>
    );
  }
);

Banner.displayName = "Banner";

export { Banner, type BannerProps };
