import type { FC } from "react";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";

interface InputField {
  id: string;
  label: string;
  placeholder: string;
  type?: string;
}

interface MessageField {
  id: string;
  label: string;
  placeholder: string;
}

interface SubmitCta {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

interface ContactDetails {
  heading: string;
  phoneLabel: string;
  phoneValue: string;
  emailLabel: string;
  emailAddress: string;
  emailHref?: string;
  emailTarget?: "_blank" | "_self";
  emailRel?: string;
  webLabel: string;
  webName: string;
  webHref: string;
  webTarget?: "_blank" | "_self";
  webRel?: string;
}

interface FormFields {
  firstRow: InputField[];
  additional: InputField[];
  message: MessageField;
}

export interface Contact2Props {
  title?: string;
  description?: string;
  contactDetails?: ContactDetails;
  formFields?: FormFields;
  submitCta?: SubmitCta;
}

export const Contact2: FC<Contact2Props> = ({
  title = "Contact Us",
  description = "We are available for questions, feedback, or collaboration opportunities. Let us know how we can help!",
  contactDetails = {
    heading: "Contact Details",
    phoneLabel: "Phone",
    phoneValue: "(123) 34567890",
    emailLabel: "Email",
    emailAddress: "email@example.com",
    emailHref: "mailto:email@example.com",
    emailTarget: "_self",
    emailRel: undefined,
    webLabel: "Web",
    webName: "shadcnblocks.com",
    webHref: "https://shadcnblocks.com",
    webTarget: "_blank",
    webRel: "noopener noreferrer",
  },
  formFields = {
    firstRow: [
      {
        id: "firstname",
        label: "First Name",
        placeholder: "First Name",
        type: "text",
      },
      {
        id: "lastname",
        label: "Last Name",
        placeholder: "Last Name",
        type: "text",
      },
    ],
    additional: [
      {
        id: "email",
        label: "Email",
        placeholder: "Email",
        type: "email",
      },
      {
        id: "subject",
        label: "Subject",
        placeholder: "Subject",
        type: "text",
      },
    ],
    message: {
      id: "message",
      label: "Message",
      placeholder: "Type your message here.",
    },
  },
  submitCta = {
    label: "Send Message",
    href: "#",
    target: "_self",
    rel: undefined,
  },
}) => {
  const {
    heading,
    phoneLabel,
    phoneValue,
    emailLabel,
    emailAddress,
    emailHref = `mailto:${emailAddress}`,
    emailTarget,
    emailRel,
    webLabel,
    webName,
    webHref,
    webTarget,
    webRel,
  } = contactDetails;

  return (
    <section className="py-32">
      <div className="container">
        <div className="mx-auto flex max-w-screen-xl flex-col justify-between gap-10 lg:flex-row lg:gap-20">
          <div className="mx-auto flex max-w-sm flex-col justify-between gap-10">
            <div className="text-center lg:text-left">
              <h1 className="mb-2 text-5xl font-semibold lg:mb-1 lg:text-6xl">
                {title}
              </h1>
              <p className="text-muted-foreground">{description}</p>
            </div>
            <div className="mx-auto w-fit lg:mx-0">
              <h3 className="mb-6 text-center text-2xl font-semibold lg:text-left">
                {heading}
              </h3>
              <ul className="ml-4 list-disc">
                <li>
                  <span className="font-bold">{phoneLabel}: </span>
                  {phoneValue}
                </li>
                <li>
                  <span className="font-bold">{emailLabel}: </span>
                  <Link
                    href={emailHref}
                    className="underline"
                    target={emailTarget}
                    rel={emailRel}
                  >
                    {emailAddress}
                  </Link>
                </li>
                <li>
                  <span className="font-bold">{webLabel}: </span>
                  <Link
                    href={webHref}
                    className="underline"
                    target={webTarget}
                    rel={webRel}
                  >
                    {webName}
                  </Link>
                </li>
              </ul>
            </div>
          </div>
          <div className="mx-auto flex max-w-screen-md flex-col gap-6 rounded-lg border p-10">
            <div className="flex gap-4">
              {formFields.firstRow.map((field) => (
                <div key={field.id} className="grid w-full items-center gap-1.5">
                  <Label htmlFor={field.id}>{field.label}</Label>
                  <Input
                    id={field.id}
                    type={field.type ?? "text"}
                    placeholder={field.placeholder}
                  />
                </div>
              ))}
            </div>
            {formFields.additional.map((field) => (
              <div key={field.id} className="grid w-full items-center gap-1.5">
                <Label htmlFor={field.id}>{field.label}</Label>
                <Input
                  id={field.id}
                  type={field.type ?? "text"}
                  placeholder={field.placeholder}
                />
              </div>
            ))}
            <div className="grid w-full gap-1.5">
              <Label htmlFor={formFields.message.id}>{formFields.message.label}</Label>
              <Textarea
                id={formFields.message.id}
                placeholder={formFields.message.placeholder}
              />
            </div>
            <Button asChild className="w-full">
              <Link href={submitCta.href} target={submitCta.target} rel={submitCta.rel}>
                {submitCta.label}
              </Link>
            </Button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact2;
