import React from "react";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import Image from "@/components/Image";
import Link from "@/components/Link";

// Reusable link config matching the app Link component props with a display label
export type LinkConfig = {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  className?: string;
  rel?: string;
};

// Background image props aligned with the project's Image component
export type BackgroundImageProps = {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
};

export interface Contact2Props {
  // Section content
  title?: string;
  description?: string;

  // Contact details block
  contactDetailsHeading?: string;
  phoneLabelText?: string;
  phone?: string;
  emailLabelText?: string;
  email?: string; // used to build mailto link
  emailLinkText?: string; // text shown for the email link; falls back to email if not provided
  webLabelText?: string;
  website?: LinkConfig; // external/internal site link with label

  // Form field labels and placeholders
  firstNameLabel?: string;
  firstNamePlaceholder?: string;
  lastNameLabel?: string;
  lastNamePlaceholder?: string;
  emailFieldLabel?: string;
  emailFieldPlaceholder?: string;
  subjectLabel?: string;
  subjectPlaceholder?: string;
  messageLabel?: string;
  messagePlaceholder?: string;

  // CTA button as Link inside Button
  submitCta?: LinkConfig; // label + href required for rendering

  // Optional background styling
  backgroundColor?: string; // section background color (e.g., "#fff" or tailwind-supported via inline style)
  backgroundImage?: BackgroundImageProps; // optional background image
}

export const Contact2: React.FC<Contact2Props> = (props) => {
  const {
    title,
    description,
    contactDetailsHeading,
    phoneLabelText,
    phone,
    emailLabelText,
    email,
    emailLinkText,
    webLabelText,
    website,
    firstNameLabel,
    firstNamePlaceholder,
    lastNameLabel,
    lastNamePlaceholder,
    emailFieldLabel,
    emailFieldPlaceholder,
    subjectLabel,
    subjectPlaceholder,
    messageLabel,
    messagePlaceholder,
    submitCta,
    backgroundColor,
    backgroundImage,
  } = props;

  return (
    <section className="relative py-32" style={backgroundColor ? { backgroundColor } : undefined}>
      {backgroundImage ? (
        <Image
          {...backgroundImage}
          className={`absolute inset-0 -z-10 h-full w-full object-cover ${backgroundImage.className ?? ""}`}
        />
      ) : null}
      <div className="container">
        <div className="mx-auto flex max-w-screen-xl flex-col justify-between gap-10 lg:flex-row lg:gap-20">
          <div className="mx-auto flex max-w-sm flex-col justify-between gap-10">
            <div className="text-center lg:text-left">
              {title ? (
                <h1 className="mb-2 text-5xl font-semibold lg:mb-1 lg:text-6xl">{title}</h1>
              ) : null}
              {description ? (
                <p className="text-muted-foreground">{description}</p>
              ) : null}
            </div>
            <div className="mx-auto w-fit lg:mx-0">
              {contactDetailsHeading ? (
                <h3 className="mb-6 text-center text-2xl font-semibold lg:text-left">{contactDetailsHeading}</h3>
              ) : null}
              <ul className="ml-4 list-disc">
                {phone || phoneLabelText ? (
                  <li>
                    {phoneLabelText ? <span className="font-bold">{phoneLabelText} </span> : null}
                    {phone}
                  </li>
                ) : null}
                {email || emailLabelText ? (
                  <li>
                    {emailLabelText ? <span className="font-bold">{emailLabelText} </span> : null}
                    {email ? (
                      <Link href={`mailto:${email}`} className="underline" target="_blank" rel="noopener noreferrer">
                        {emailLinkText ?? email}
                      </Link>
                    ) : null}
                  </li>
                ) : null}
                {website || webLabelText ? (
                  <li>
                    {webLabelText ? <span className="font-bold">{webLabelText} </span> : null}
                    {website ? (
                      <Link
                        href={website.href}
                        className={`underline ${website.className ?? ""}`}
                        target={website.target ?? "_blank"}
                        rel={website.rel ?? "noopener noreferrer"}
                      >
                        {website.label}
                      </Link>
                    ) : null}
                  </li>
                ) : null}
              </ul>
            </div>
          </div>
          <div className="mx-auto flex max-w-screen-md flex-col gap-6 rounded-lg border p-10">
            <div className="flex gap-4">
              <div className="grid w-full items-center gap-1.5">
                {firstNameLabel ? <Label htmlFor="firstname">{firstNameLabel}</Label> : null}
                <Input type="text" id="firstname" placeholder={firstNamePlaceholder} />
              </div>
              <div className="grid w-full items-center gap-1.5">
                {lastNameLabel ? <Label htmlFor="lastname">{lastNameLabel}</Label> : null}
                <Input type="text" id="lastname" placeholder={lastNamePlaceholder} />
              </div>
            </div>
            <div className="grid w-full items-center gap-1.5">
              {emailFieldLabel ? <Label htmlFor="email">{emailFieldLabel}</Label> : null}
              <Input type="email" id="email" placeholder={emailFieldPlaceholder} />
            </div>
            <div className="grid w-full items-center gap-1.5">
              {subjectLabel ? <Label htmlFor="subject">{subjectLabel}</Label> : null}
              <Input type="text" id="subject" placeholder={subjectPlaceholder} />
            </div>
            <div className="grid w-full gap-1.5">
              {messageLabel ? <Label htmlFor="message">{messageLabel}</Label> : null}
              <Textarea id="message" placeholder={messagePlaceholder} />
            </div>
            {submitCta ? (
              <Button className="w-full" asChild>
                <Link href={submitCta.href} className={submitCta.className} target={submitCta.target} rel={submitCta.rel}>
                  {submitCta.label}
                </Link>
              </Button>
            ) : null}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact2;
