import React from "react";
import { Button } from "@/components/ui/button";
import Image from "@/components/Image";
import Link from "@/components/Link";

export interface Feature1Props {
  title: string;
  description?: string;
  imageSrc: string;
  imageAlt: string;
  imageWidth: number;
  imageHeight: number;
  imageSizes?: string;
  imagePriority?: boolean;
  imageQuality?: number;
  imageClassName?: string;
  buttonPrimary: {
    label: string;
    href: string;
    target?: "_blank" | "_self";
    rel?: string;
    className?: string;
  };
  buttonSecondary: {
    label: string;
    href: string;
    target?: "_blank" | "_self";
    rel?: string;
    className?: string;
  };
  backgroundColor?: string;
  backgroundImage?: {
    src: string;
    alt: string;
    width: number;
    height: number;
    className?: string;
    priority?: boolean;
    quality?: number;
    sizes?: string;
  };
  sectionClassName?: string;
}

export const Feature1: React.FC<Feature1Props> = ({
  title,
  description,
  imageSrc,
  imageAlt,
  imageWidth,
  imageHeight,
  imageSizes,
  imagePriority,
  imageQuality,
  imageClassName,
  buttonPrimary,
  buttonSecondary,
  backgroundColor,
  backgroundImage,
  sectionClassName,
}) => {
  return (
    <section
      className={`relative py-32 ${sectionClassName ?? ""}`}
      style={backgroundColor ? { backgroundColor } : undefined}
    >
      {backgroundImage ? (
        <div className="absolute inset-0 -z-10">
          <Image
            src={backgroundImage.src}
            alt={backgroundImage.alt}
            width={backgroundImage.width}
            height={backgroundImage.height}
            priority={backgroundImage.priority}
            quality={backgroundImage.quality}
            sizes={backgroundImage.sizes}
            className={`h-full w-full object-cover ${backgroundImage.className ?? ""}`}
          />
        </div>
      ) : null}
      <div className="container">
        <div className="grid items-center gap-8 lg:grid-cols-2">
          <div className="flex flex-col items-center text-center lg:items-start lg:text-left">
            <h1 className="my-6 mt-0 text-4xl font-semibold text-balance lg:text-5xl">
              {title}
            </h1>
            {description ? (
              <p className="mb-8 max-w-xl text-muted-foreground lg:text-lg">
                {description}
              </p>
            ) : null}
            <div className="flex w-full flex-col justify-center gap-2 sm:flex-row lg:justify-start">
              <Button asChild className={buttonPrimary.className}>
                <Link
                  href={buttonPrimary.href}
                  target={buttonPrimary.target ?? "_blank"}
                  rel={buttonPrimary.rel ?? "noopener noreferrer"}
                >
                  {buttonPrimary.label}
                </Link>
              </Button>
              <Button variant="outline" asChild className={buttonSecondary.className}>
                <Link
                  href={buttonSecondary.href}
                  target={buttonSecondary.target ?? "_blank"}
                  rel={buttonSecondary.rel ?? "noopener noreferrer"}
                >
                  {buttonSecondary.label}
                </Link>
              </Button>
            </div>
          </div>
          <Image
            src={imageSrc}
            alt={imageAlt}
            width={imageWidth}
            height={imageHeight}
            className={`max-h-96 w-full rounded-md object-cover ${imageClassName ?? ""}`}
            sizes={imageSizes}
            priority={imagePriority}
            quality={imageQuality}
          />
        </div>
      </div>
    </section>
  );
};

export default Feature1;
