import type { JSX } from "react";
import Image from "@/components/Image";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";

type Feature1ImageType =
  | "background"
  | "profile"
  | "logo"
  | "banner"
  | "gallery"
  | "thumbnail"
  | "icon"
  | "other";

interface Feature1Cta {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

export interface Feature1Props {
  title?: string;
  description?: string;
  imageSrc?: string;
  imageAlt?: string;
  imageWidth?: number;
  imageHeight?: number;
  imageType?: Feature1ImageType;
  primaryCta?: Feature1Cta;
  secondaryCta?: Feature1Cta;
  sectionAriaLabel?: string;
}

const defaultTitle = "Blocks built with Shadcn & Tailwind";
const defaultDescription =
  "Hundreds of finely crafted components built with React, Tailwind and Shadcn UI. Developers can copy and paste these blocks directly into their project.";

const defaultImage = {
  src: "https://shadcnblocks.com/images/block/placeholder-1.svg",
  alt: "Placeholder hero image showcasing the application UI",
  width: 600,
  height: 384,
  type: "other" as Feature1ImageType,
};

const defaultPrimaryCta: Feature1Cta = {
  label: "Get Started",
  href: "https://shadcnblocks.com",
  target: "_blank",
  rel: "noopener noreferrer",
};

const defaultSecondaryCta: Feature1Cta = {
  label: "Learn More",
  href: "https://shadcnblocks.com",
  target: "_blank",
  rel: "noopener noreferrer",
};

export const Feature1 = ({
  title = defaultTitle,
  description = defaultDescription,
  imageSrc = defaultImage.src,
  imageAlt = defaultImage.alt,
  imageWidth = defaultImage.width,
  imageHeight = defaultImage.height,
  imageType = defaultImage.type,
  primaryCta = defaultPrimaryCta,
  secondaryCta = defaultSecondaryCta,
  sectionAriaLabel = "Product feature section",
}: Feature1Props): JSX.Element => {
  const primaryRel =
    primaryCta.rel ?? (primaryCta.target === "_blank" ? "noopener noreferrer" : undefined);
  const secondaryRel =
    secondaryCta.rel ?? (secondaryCta.target === "_blank" ? "noopener noreferrer" : undefined);

  return (
    <section aria-label={sectionAriaLabel} className="py-32">
      <div className="container">
        <div className="grid items-center gap-8 lg:grid-cols-2">
          <div className="flex flex-col items-center text-center lg:items-start lg:text-left">
            <h1 className="my-6 mt-0 text-4xl font-semibold text-balance lg:text-5xl">
              {title}
            </h1>
            <p className="mb-8 max-w-xl text-muted-foreground lg:text-lg">{description}</p>
            <div className="flex w-full flex-col justify-center gap-2 sm:flex-row lg:justify-start">
              <Button asChild>
                <Link href={primaryCta.href} target={primaryCta.target} rel={primaryRel}>
                  {primaryCta.label}
                </Link>
              </Button>
              <Button variant="outline" asChild>
                <Link
                  href={secondaryCta.href}
                  target={secondaryCta.target}
                  rel={secondaryRel}
                >
                  {secondaryCta.label}
                </Link>
              </Button>
            </div>
          </div>
          <Image
            src={imageSrc}
            alt={imageAlt}
            width={imageWidth}
            height={imageHeight}
            className="max-h-96 w-full rounded-md object-cover"
            type={imageType}
          />
        </div>
      </div>
    </section>
  );
};
