import type { FC } from "react";
import Image from "@/components/Image";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";

export interface Feature2ButtonLink {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

export interface Feature2Props {
  title?: string;
  description?: string;
  imageSrc?: string;
  imageAlt?: string;
  imageWidth?: number;
  imageHeight?: number;
  buttonPrimary?: Feature2ButtonLink;
  buttonSecondary?: Feature2ButtonLink;
}

export const Feature2: FC<Feature2Props> = ({
  title = "Blocks built with Shadcn & Tailwind",
  description = "Hundreds of finely crafted components built with React, Tailwind and Shadcn UI. Developers can copy and paste these blocks directly into their project.",
  imageSrc = "https://shadcnblocks.com/images/block/placeholder-1.svg",
  imageAlt = "placeholder hero",
  imageWidth = 600,
  imageHeight = 384,
  buttonPrimary = {
    label: "Get Started",
    href: "https://shadcnblocks.com",
    target: "_blank",
    rel: "noopener noreferrer",
  },
  buttonSecondary = {
    label: "Learn More",
    href: "https://shadcnblocks.com",
    target: "_blank",
    rel: "noopener noreferrer",
  },
}) => {
  const primaryButton = {
    ...buttonPrimary,
    rel:
      buttonPrimary.rel ??
      (buttonPrimary.target === "_blank" ? "noopener noreferrer" : undefined),
  };

  const secondaryButton = {
    ...buttonSecondary,
    rel:
      buttonSecondary.rel ??
      (buttonSecondary.target === "_blank" ? "noopener noreferrer" : undefined),
  };

  return (
    <section className="py-32">
      <div className="container">
        <div className="grid items-center gap-8 md:gap-16 lg:grid-cols-2">
          <Image
            src={imageSrc}
            alt={imageAlt}
            width={imageWidth}
            height={imageHeight}
            className="max-h-96 w-full rounded-md object-cover"
            type="banner"
          />
          <div className="flex flex-col items-center text-center lg:items-start lg:text-left">
            <h1 className="my-6 mt-0 text-4xl font-semibold text-balance lg:text-5xl">
              {title}
            </h1>
            <p className="mb-8 max-w-xl text-muted-foreground lg:text-lg">
              {description}
            </p>
            <div className="flex w-full flex-col justify-center gap-2 sm:flex-row lg:justify-start">
              <Button asChild>
                <Link
                  href={primaryButton.href}
                  target={primaryButton.target}
                  rel={primaryButton.rel}
                >
                  {primaryButton.label}
                </Link>
              </Button>
              <Button variant="outline" asChild>
                <Link
                  href={secondaryButton.href}
                  target={secondaryButton.target}
                  rel={secondaryButton.rel}
                >
                  {secondaryButton.label}
                </Link>
              </Button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
