import type { CSSProperties } from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";

interface IconDefinition {
  package: "lucide";
  name: string;
  type: "icon";
}

interface FeatureImage {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  placeholder?: "blur" | "empty";
  blurDataURL?: string;
  style?: CSSProperties;
  className?: string;
  type?: "background" | "profile" | "logo" | "banner" | "gallery" | "thumbnail" | "icon" | "other";
}

interface FeatureItem {
  id: string;
  title: string;
  description: string;
  image: FeatureImage;
}

interface Feature73Action {
  href: string;
  text: string;
  target?: "_blank" | "_self";
  rel?: string;
  icon?: IconDefinition;
  iconClassName?: string;
}

export interface Feature73Props {
  heading?: string;
  description?: string;
  primaryAction?: Feature73Action;
  features?: FeatureItem[];
}

const defaultPrimaryAction: Feature73Action = {
  href: "https://www.shadcnblocks.com",
  text: "Book a demo",
  icon: {
    package: "lucide",
    name: "ArrowRight",
    type: "icon",
  },
};

const defaultFeatures: FeatureItem[] = [
  {
    id: "feature-1",
    title: "Modern Design",
    description:
      "Clean and intuitive interface built with the latest design principles. Optimized for the best user experience.",
    image: {
      src: "https://www.shadcnblocks.com/images/block/placeholder-1.svg",
      alt: "Modern design illustration",
    },
  },
  {
    id: "feature-2",
    title: "Responsive Layout",
    description:
      "Fully responsive design that works seamlessly across all devices and screen sizes. Perfect for any platform.",
    image: {
      src: "https://www.shadcnblocks.com/images/block/placeholder-2.svg",
      alt: "Responsive layout illustration",
    },
  },
  {
    id: "feature-3",
    title: "Easy Integration",
    description:
      "Simple integration process with comprehensive documentation and dedicated support team.",
    image: {
      src: "https://www.shadcnblocks.com/images/block/placeholder-3.svg",
      alt: "Easy integration illustration",
    },
  },
];

export const Feature73 = ({
  heading = "Powerful Features",
  description = "Discover the powerful features that make our platform stand out from the rest. Built with the latest technology and designed for maximum productivity.",
  primaryAction = defaultPrimaryAction,
  features = defaultFeatures,
}: Feature73Props) => {
  const [primaryFeature, ...secondaryFeatures] = features;
  const actionRel =
    primaryAction.rel ?? (primaryAction.target === "_blank" ? "noopener noreferrer" : undefined);

  return (
    <section className="py-32">
      <div className="container flex flex-col gap-16 lg:px-16">
        <div className="lg:max-w-sm">
          <h2 className="mb-3 text-xl font-semibold md:mb-4 md:text-4xl lg:mb-6">{heading}</h2>
          <p className="mb-8 text-muted-foreground lg:text-lg">{description}</p>
          {primaryAction.href && primaryAction.text ? (
            <Button
              asChild
              variant="link"
              className="group inline-flex items-center p-0 text-xs font-medium md:text-base lg:text-lg"
            >
              <Link href={primaryAction.href} target={primaryAction.target} rel={actionRel}>
                <span>{primaryAction.text}</span>
                {primaryAction.icon ? (
                  <DynamicIcon
                    name={primaryAction.icon.name}
                    className={cn(
                      "ml-2 size-4 transition-transform group-hover:translate-x-1",
                      primaryAction.iconClassName
                    )}
                  />
                ) : null}
              </Link>
            </Button>
          ) : null}
        </div>
        {primaryFeature || secondaryFeatures.length ? (
          <div className="grid gap-6 md:grid-cols-2 lg:gap-8">
            {primaryFeature ? (
              <div className="flex flex-col overflow-clip rounded-xl border border-border md:col-span-2 md:grid md:grid-cols-2 md:gap-6 lg:gap-8">
                <div className="md:min-h-[24rem] lg:min-h-[28rem] xl:min-h-[32rem]">
                  <Image
                    src={primaryFeature.image.src}
                    alt={primaryFeature.image.alt}
                    width={primaryFeature.image.width}
                    height={primaryFeature.image.height}
                    priority={primaryFeature.image.priority}
                    quality={primaryFeature.image.quality}
                    sizes={primaryFeature.image.sizes}
                    fill={primaryFeature.image.fill}
                    placeholder={primaryFeature.image.placeholder}
                    blurDataURL={primaryFeature.image.blurDataURL}
                    style={primaryFeature.image.style}
                    className={cn(
                      "aspect-[16/9] h-full w-full object-cover object-center",
                      primaryFeature.image.className
                    )}
                    type={primaryFeature.image.type}
                  />
                </div>
                <div className="flex flex-col justify-center px-6 py-8 md:px-8 md:py-10 lg:px-10 lg:py-12">
                  <h3 className="mb-3 text-lg font-semibold md:mb-4 md:text-2xl lg:mb-6">
                    {primaryFeature.title}
                  </h3>
                  <p className="text-muted-foreground lg:text-lg">{primaryFeature.description}</p>
                </div>
              </div>
            ) : null}
            {secondaryFeatures.map((feature) => (
              <div
                key={feature.id}
                className="flex flex-col overflow-clip rounded-xl border border-border"
              >
                <div>
                  <Image
                    src={feature.image.src}
                    alt={feature.image.alt}
                    width={feature.image.width}
                    height={feature.image.height}
                    priority={feature.image.priority}
                    quality={feature.image.quality}
                    sizes={feature.image.sizes}
                    fill={feature.image.fill}
                    placeholder={feature.image.placeholder}
                    blurDataURL={feature.image.blurDataURL}
                    style={feature.image.style}
                    className={cn(
                      "aspect-[16/9] h-full w-full object-cover object-center",
                      feature.image.className
                    )}
                    type={feature.image.type}
                  />
                </div>
                <div className="px-6 py-8 md:px-8 md:py-10 lg:px-10 lg:py-12">
                  <h3 className="mb-3 text-lg font-semibold md:mb-4 md:text-2xl lg:mb-6">
                    {feature.title}
                  </h3>
                  <p className="text-muted-foreground lg:text-lg">{feature.description}</p>
                </div>
              </div>
            ))}
          </div>
        ) : null}
      </div>
    </section>
  );
};
