import type { FC } from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";
import Link from "@/components/Link";

type LucideSocialIconName = "Instagram" | "Facebook" | "Twitter" | "Linkedin";

interface Icon {
  package: "lucide";
  name: LucideSocialIconName;
  type: "icon";
}

interface FooterLogoImage {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  className?: string;
}

interface FooterLogo {
  href: string;
  image: FooterLogoImage;
  title: string;
}

interface FooterLink {
  name: string;
  href: string;
}

interface FooterSection {
  title: string;
  links: FooterLink[];
}

interface FooterSocialLink {
  icon: Icon;
  href: string;
  label: string;
}

export interface Footer7Props {
  logo?: FooterLogo;
  sections?: FooterSection[];
  description?: string;
  socialLinks?: FooterSocialLink[];
  copyright?: string;
  legalLinks?: FooterLink[];
}

const defaultLogo: FooterLogo = {
  href: "https://www.shadcnblocks.com",
  image: {
    src: "https://www.shadcnblocks.com/images/block/logos/shadcnblockscom-icon.svg",
    alt: "Shadcnblocks.com logo",
    width: 32,
    height: 32,
    className: "h-8 w-auto",
  },
  title: "Shadcnblocks.com",
};

const defaultSections: FooterSection[] = [
  {
    title: "Product",
    links: [
      { name: "Overview", href: "#" },
      { name: "Pricing", href: "#" },
      { name: "Marketplace", href: "#" },
      { name: "Features", href: "#" },
    ],
  },
  {
    title: "Company",
    links: [
      { name: "About", href: "#" },
      { name: "Team", href: "#" },
      { name: "Blog", href: "#" },
      { name: "Careers", href: "#" },
    ],
  },
  {
    title: "Resources",
    links: [
      { name: "Help", href: "#" },
      { name: "Sales", href: "#" },
      { name: "Advertise", href: "#" },
      { name: "Privacy", href: "#" },
    ],
  },
];

const defaultSocialLinks: FooterSocialLink[] = [
  { icon: { package: "lucide", name: "Instagram", type: "icon" }, href: "#", label: "Instagram" },
  { icon: { package: "lucide", name: "Facebook", type: "icon" }, href: "#", label: "Facebook" },
  { icon: { package: "lucide", name: "Twitter", type: "icon" }, href: "#", label: "Twitter" },
  { icon: { package: "lucide", name: "Linkedin", type: "icon" }, href: "#", label: "LinkedIn" },
];

const defaultLegalLinks: FooterLink[] = [
  { name: "Terms and Conditions", href: "#" },
  { name: "Privacy Policy", href: "#" },
];

export const Footer7: FC<Footer7Props> = ({
  logo = defaultLogo,
  sections = defaultSections,
  description = "A collection of components for your startup business or side project.",
  socialLinks = defaultSocialLinks,
  copyright = "© 2024 Shadcnblocks.com. All rights reserved.",
  legalLinks = defaultLegalLinks,
}) => {
  return (
    <section className="py-32">
      <div className="container mx-auto">
        <div className="flex w-full flex-col justify-between gap-10 lg:flex-row lg:items-start lg:text-left">
          <div className="flex w-full flex-col justify-between gap-6 lg:items-start">
            <div className="flex items-center gap-2 lg:justify-start">
              <Link href={logo.href} className="inline-flex" aria-label={logo.title}>
                <Image
                  src={logo.image.src}
                  alt={logo.image.alt}
                  width={logo.image.width}
                  height={logo.image.height}
                  className={logo.image.className ?? "h-8 w-auto"}
                />
              </Link>
              <h2 className="text-xl font-semibold">{logo.title}</h2>
            </div>
            <p className="max-w-[70%] text-sm text-muted-foreground">{description}</p>
            <ul className="flex items-center space-x-6 text-muted-foreground">
              {socialLinks.map((social) => (
                <li key={social.label}>
                  <Link
                    href={social.href}
                    aria-label={social.label}
                    className="inline-flex text-muted-foreground transition-colors hover:text-primary"
                  >
                    <DynamicIcon name={social.icon.name} className="size-5" />
                  </Link>
                </li>
              ))}
            </ul>
          </div>
          <div className="grid w-full gap-6 md:grid-cols-3 lg:gap-20">
            {sections.map((section) => (
              <div key={section.title}>
                <h3 className="mb-4 font-bold text-foreground">{section.title}</h3>
                <ul className="space-y-3 text-sm text-muted-foreground">
                  {section.links.map((link) => (
                    <li key={link.name}>
                      <Link
                        href={link.href}
                        className="font-medium text-muted-foreground transition-colors hover:text-primary"
                      >
                        {link.name}
                      </Link>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
        <div className="mt-8 flex flex-col justify-between gap-4 border-t py-8 text-xs font-medium text-muted-foreground md:flex-row md:items-center md:text-left">
          <p className="order-2 text-xs font-medium text-muted-foreground lg:order-1">{copyright}</p>
          <ul className="order-1 flex flex-col gap-2 md:order-2 md:flex-row">
            {legalLinks.map((link) => (
              <li key={link.name}>
                <Link
                  href={link.href}
                  className="text-xs font-medium text-muted-foreground transition-colors hover:text-primary"
                >
                  {link.name}
                </Link>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </section>
  );
};
