import React from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";

// Standardized interfaces per guidelines
interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

interface LinkProps {
  href: string;
  target?: '_blank' | '_self';
  className?: string;
  rel?: string;
}

export interface Hero7Props {
  heading: string;
  description: string;
  button: { text: string } & LinkProps;
  reviews: {
    count: number;
    avatars: ImageProps[];
  };
  rating: number;
  reviewsLabelPrefix: string; // e.g., "from"
  reviewsLabelSuffix: string; // e.g., "reviews"
  backgroundImage?: ImageProps; // optional background image
  backgroundColor?: string; // optional CSS color value (e.g., "#fff" or "rgb(...)" or "hsl(...)")
}

export const Hero7: React.FC<Hero7Props> = ({
  heading,
  description,
  button,
  reviews,
  rating,
  reviewsLabelPrefix,
  reviewsLabelSuffix,
  backgroundImage,
  backgroundColor,
}) => {
  return (
    <section className="py-32 relative" style={backgroundColor ? { backgroundColor } : undefined}>
      {backgroundImage && (
        <div className="absolute inset-0 -z-10">
          <Image
            src={backgroundImage.src}
            alt={backgroundImage.alt}
            width={backgroundImage.width}
            height={backgroundImage.height}
            priority={backgroundImage.priority}
            quality={backgroundImage.quality}
            sizes={backgroundImage.sizes}
            fill={backgroundImage.fill}
            className={`h-full w-full object-cover ${backgroundImage.className ?? ''}`}
            placeholder={backgroundImage.placeholder}
          />
        </div>
      )}
      <div className="container text-center relative">
        <div className="mx-auto flex max-w-screen-lg flex-col gap-6">
          <h1 className="text-3xl font-extrabold lg:text-6xl">{heading}</h1>
          <p className="text-balance text-muted-foreground lg:text-lg">{description}</p>
        </div>
        <Button asChild size="lg" className="mt-10">
          <Link href={button.href} target={button.target} rel={button.rel} className={button.className}>
            {button.text}
          </Link>
        </Button>
        <div className="mx-auto mt-10 flex w-fit flex-col items-center gap-4 sm:flex-row">
          <span className="mx-4 inline-flex items-center -space-x-4">
            {reviews.avatars.map((avatar, index) => (
              <span key={index} className="size-14 border rounded-full overflow-hidden inline-block">
                <Image
                  src={avatar.src}
                  alt={avatar.alt}
                  width={avatar.width}
                  height={avatar.height}
                  priority={avatar.priority}
                  quality={avatar.quality}
                  sizes={avatar.sizes}
                  fill={avatar.fill}
                  className={`h-full w-full object-cover ${avatar.className ?? ''}`}
                  placeholder={avatar.placeholder}
                />
              </span>
            ))}
          </span>
          <div>
            <div className="flex items-center gap-1">
              {Array.from({ length: rating }).map((_, index) => (
                <DynamicIcon key={index} name="Star" className="size-5 fill-yellow-400 text-yellow-400" />
              ))}
            </div>
            <p className="text-left font-medium text-muted-foreground">
              {reviewsLabelPrefix} {reviews.count}+ {reviewsLabelSuffix}
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export { Hero7 };
