"use client";

import React from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";
import Link from "@/components/Link";
import { cn } from "@/lib/utils";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/ui/accordion";
import { Button } from "@/components/ui/button";
import {
  NavigationMenu,
  NavigationMenuContent,
  NavigationMenuItem,
  NavigationMenuLink,
  NavigationMenuList,
  NavigationMenuTrigger,
  navigationMenuTriggerStyle,
} from "@/components/ui/navigation-menu";
import {
  Sheet,
  SheetContent,
  SheetHeader,
  SheetTitle,
  SheetTrigger,
} from "@/components/ui/sheet";

interface LabelledLink {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

interface FeatureItem {
  title: string;
  description: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

interface LogoConfig {
  href: string;
  src: string;
  alt: string;
  width?: number;
  height?: number;
  target?: "_blank" | "_self";
  rel?: string;
}

export interface Navbar5Props {
  className?: string;
  logo?: LogoConfig;
  brandName?: string;
  featureTriggerLabel?: string;
  mobileFeatureAccordionLabel?: string;
  featureItems?: FeatureItem[];
  navigationLinks?: LabelledLink[];
  mobileLinks?: LabelledLink[];
  signInLink?: LabelledLink;
  primaryCtaLink?: LabelledLink;
  menuIconName?: string;
}

const defaultLogo: LogoConfig = {
  href: "https://www.shadcnblocks.com",
  src: "https://shadcnblocks.com/images/block/logos/shadcnblockscom-icon.svg",
  alt: "Shadcn UI Navbar",
  width: 32,
  height: 32,
};

const defaultFeatureItems: FeatureItem[] = [
  {
    title: "Dashboard",
    description: "Overview of your activity",
    href: "#",
  },
  {
    title: "Analytics",
    description: "Track your performance",
    href: "#",
  },
  {
    title: "Settings",
    description: "Configure your preferences",
    href: "#",
  },
  {
    title: "Integrations",
    description: "Connect with other tools",
    href: "#",
  },
  {
    title: "Storage",
    description: "Manage your files",
    href: "#",
  },
  {
    title: "Support",
    description: "Get help when needed",
    href: "#",
  },
];

const defaultNavigationLinks: LabelledLink[] = [
  { label: "Products", href: "#" },
  { label: "Resources", href: "#" },
  { label: "Contact", href: "#" },
];

const defaultMobileLinks: LabelledLink[] = [
  { label: "Templates", href: "#" },
  { label: "Blog", href: "#" },
  { label: "Pricing", href: "#" },
];

const defaultSignInLink: LabelledLink = {
  label: "Sign in",
  href: "#",
};

const defaultPrimaryCtaLink: LabelledLink = {
  label: "Start for free",
  href: "#",
};

const getRel = (link: { target?: "_blank" | "_self"; rel?: string }) =>
  link.rel ?? (link.target === "_blank" ? "noopener noreferrer" : undefined);

export const Navbar5: React.FC<Navbar5Props> = ({
  className,
  logo,
  brandName,
  featureTriggerLabel,
  mobileFeatureAccordionLabel,
  featureItems,
  navigationLinks,
  mobileLinks,
  signInLink,
  primaryCtaLink,
  menuIconName = "Menu",
}) => {
  const resolvedLogo = logo ?? defaultLogo;
  const resolvedBrandName = brandName ?? "Shadcnblocks.com";
  const resolvedFeatureTriggerLabel = featureTriggerLabel ?? "Features";
  const resolvedFeatureItems = featureItems ?? defaultFeatureItems;
  const resolvedNavigationLinks = navigationLinks ?? defaultNavigationLinks;
  const resolvedMobileLinks = mobileLinks ?? defaultMobileLinks;
  const resolvedSignInLink = signInLink ?? defaultSignInLink;
  const resolvedPrimaryCtaLink = primaryCtaLink ?? defaultPrimaryCtaLink;
  const resolvedMobileFeatureAccordionLabel =
    mobileFeatureAccordionLabel ?? resolvedFeatureTriggerLabel;

  return (
    <section className={cn("py-4", className)}>
      <div className="container">
        <nav className="flex items-center justify-between">
          <Link
            href={resolvedLogo.href}
            className="flex items-center gap-2"
            target={resolvedLogo.target}
            rel={getRel(resolvedLogo)}
          >
            <Image
              src={resolvedLogo.src}
              alt={resolvedLogo.alt}
              width={resolvedLogo.width}
              height={resolvedLogo.height}
              className="max-h-8"
              type="logo"
            />
            <span className="text-lg font-semibold tracking-tighter">
              {resolvedBrandName}
            </span>
          </Link>
          <NavigationMenu className="hidden lg:block">
            <NavigationMenuList>
              <NavigationMenuItem>
                <NavigationMenuTrigger>
                  {resolvedFeatureTriggerLabel}
                </NavigationMenuTrigger>
                <NavigationMenuContent>
                  <div className="grid w-[600px] grid-cols-2 p-3">
                    {resolvedFeatureItems.map((feature) => (
                      <NavigationMenuLink asChild key={feature.title}>
                        <Link
                          href={feature.href}
                          className="rounded-md p-3 transition-colors hover:bg-muted/70"
                          target={feature.target}
                          rel={getRel(feature)}
                        >
                          <p className="mb-1 font-semibold text-foreground">
                            {feature.title}
                          </p>
                          <p className="text-sm text-muted-foreground">
                            {feature.description}
                          </p>
                        </Link>
                      </NavigationMenuLink>
                    ))}
                  </div>
                </NavigationMenuContent>
              </NavigationMenuItem>
              {resolvedNavigationLinks.map((navItem) => (
                <NavigationMenuItem key={navItem.label}>
                  <NavigationMenuLink asChild>
                    <Link
                      href={navItem.href}
                      className={navigationMenuTriggerStyle()}
                      target={navItem.target}
                      rel={getRel(navItem)}
                    >
                      {navItem.label}
                    </Link>
                  </NavigationMenuLink>
                </NavigationMenuItem>
              ))}
            </NavigationMenuList>
          </NavigationMenu>
          <div className="hidden items-center gap-4 lg:flex">
            <Button asChild variant="outline">
              <Link
                href={resolvedSignInLink.href}
                target={resolvedSignInLink.target}
                rel={getRel(resolvedSignInLink)}
              >
                {resolvedSignInLink.label}
              </Link>
            </Button>
            <Button asChild>
              <Link
                href={resolvedPrimaryCtaLink.href}
                target={resolvedPrimaryCtaLink.target}
                rel={getRel(resolvedPrimaryCtaLink)}
              >
                {resolvedPrimaryCtaLink.label}
              </Link>
            </Button>
          </div>
          <Sheet>
            <SheetTrigger asChild className="lg:hidden">
              <Button variant="outline" size="icon">
                <DynamicIcon name={menuIconName} className="h-4 w-4" />
              </Button>
            </SheetTrigger>
            <SheetContent side="top" className="max-h-screen overflow-auto">
              <SheetHeader>
                <SheetTitle>
                  <Link
                    href={resolvedLogo.href}
                    className="flex items-center gap-2"
                    target={resolvedLogo.target}
                    rel={getRel(resolvedLogo)}
                  >
                    <Image
                      src={resolvedLogo.src}
                      alt={resolvedLogo.alt}
                      width={resolvedLogo.width}
                      height={resolvedLogo.height}
                      className="max-h-8"
                      type="logo"
                    />
                    <span className="text-lg font-semibold tracking-tighter">
                      {resolvedBrandName}
                    </span>
                  </Link>
                </SheetTitle>
              </SheetHeader>
              <div className="flex flex-col p-4">
                <Accordion type="single" collapsible className="mt-4 mb-2">
                  <AccordionItem value="features" className="border-none">
                    <AccordionTrigger className="text-base hover:no-underline">
                      {resolvedMobileFeatureAccordionLabel}
                    </AccordionTrigger>
                    <AccordionContent>
                      <div className="grid md:grid-cols-2">
                        {resolvedFeatureItems.map((feature) => (
                          <Link
                            key={feature.title}
                            href={feature.href}
                            className="rounded-md p-3 transition-colors hover:bg-muted/70"
                            target={feature.target}
                            rel={getRel(feature)}
                          >
                            <p className="mb-1 font-semibold text-foreground">
                              {feature.title}
                            </p>
                            <p className="text-sm text-muted-foreground">
                              {feature.description}
                            </p>
                          </Link>
                        ))}
                      </div>
                    </AccordionContent>
                  </AccordionItem>
                </Accordion>
                <div className="flex flex-col gap-6">
                  {resolvedMobileLinks.map((mobileLink) => (
                    <Link
                      key={mobileLink.label}
                      href={mobileLink.href}
                      className="font-medium"
                      target={mobileLink.target}
                      rel={getRel(mobileLink)}
                    >
                      {mobileLink.label}
                    </Link>
                  ))}
                </div>
                <div className="mt-6 flex flex-col gap-4">
                  <Button asChild variant="outline">
                    <Link
                      href={resolvedSignInLink.href}
                      target={resolvedSignInLink.target}
                      rel={getRel(resolvedSignInLink)}
                    >
                      {resolvedSignInLink.label}
                    </Link>
                  </Button>
                  <Button asChild>
                    <Link
                      href={resolvedPrimaryCtaLink.href}
                      target={resolvedPrimaryCtaLink.target}
                      rel={getRel(resolvedPrimaryCtaLink)}
                    >
                      {resolvedPrimaryCtaLink.label}
                    </Link>
                  </Button>
                </div>
              </div>
            </SheetContent>
          </Sheet>
        </nav>
      </div>
    </section>
  );
};
