"use client";

import { useState, type FC } from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Switch } from "@/components/ui/switch";

interface PricingFeature {
  text: string;
  iconName?: string;
}

interface PricingPlanButton {
  text: string;
  url: string;
  target?: "_blank" | "_self";
  rel?: string;
  iconName?: string;
}

interface PricingPlan {
  id: string;
  name: string;
  description: string;
  monthlyPrice: string;
  yearlyPrice: string;
  features: PricingFeature[];
  button: PricingPlanButton;
  upgradeNote?: string;
}

export interface Pricing2Props {
  heading?: string;
  description?: string;
  monthlyLabel?: string;
  yearlyLabel?: string;
  billingDescriptor?: string;
  billingFrequencySuffix?: string;
  plans?: PricingPlan[];
  featureIconName?: string;
  buttonIconName?: string;
}

const DEFAULT_HEADING = "Pricing";
const DEFAULT_DESCRIPTION = "Check out our affordable pricing plans";
const DEFAULT_MONTHLY_LABEL = "Monthly";
const DEFAULT_YEARLY_LABEL = "Yearly";
const DEFAULT_BILLING_DESCRIPTOR = "Billed";
const DEFAULT_BILLING_FREQUENCY_SUFFIX = "annually";
const DEFAULT_FEATURE_ICON_NAME = "CircleCheck";
const DEFAULT_BUTTON_ICON_NAME = "ArrowRight";
const DEFAULT_UPGRADE_NOTE = "Everything in Plus, and:";

const DEFAULT_PLANS: PricingPlan[] = [
  {
    id: "plus",
    name: "Plus",
    description: "For personal use",
    monthlyPrice: "$19",
    yearlyPrice: "$15",
    features: [
      { text: "Up to 5 team members" },
      { text: "Basic components library" },
      { text: "Community support" },
      { text: "1GB storage space" },
    ],
    button: {
      text: "Purchase",
      url: "https://www.shadcnblocks.com",
      target: "_blank",
      rel: "noopener noreferrer",
      iconName: "ArrowRight",
    },
  },
  {
    id: "pro",
    name: "Pro",
    description: "For professionals",
    monthlyPrice: "$49",
    yearlyPrice: "$35",
    features: [
      { text: "Unlimited team members" },
      { text: "Advanced components" },
      { text: "Priority support" },
      { text: "Unlimited storage" },
    ],
    button: {
      text: "Purchase",
      url: "https://www.shadcnblocks.com",
      target: "_blank",
      rel: "noopener noreferrer",
      iconName: "ArrowRight",
    },
    upgradeNote: DEFAULT_UPGRADE_NOTE,
  },
];

const getAnnualBillingAmount = (price: string): string => {
  const numericValue = Number.parseFloat(price.replace(/[^0-9.]/g, ""));

  if (Number.isNaN(numericValue)) {
    return price;
  }

  const annualTotal = numericValue * 12;

  return `$${annualTotal.toLocaleString(undefined, {
    minimumFractionDigits: annualTotal % 1 === 0 ? 0 : 2,
    maximumFractionDigits: 2,
  })}`;
};

export const Pricing2: FC<Pricing2Props> = ({
  heading = DEFAULT_HEADING,
  description = DEFAULT_DESCRIPTION,
  monthlyLabel = DEFAULT_MONTHLY_LABEL,
  yearlyLabel = DEFAULT_YEARLY_LABEL,
  billingDescriptor = DEFAULT_BILLING_DESCRIPTOR,
  billingFrequencySuffix = DEFAULT_BILLING_FREQUENCY_SUFFIX,
  plans = DEFAULT_PLANS,
  featureIconName = DEFAULT_FEATURE_ICON_NAME,
  buttonIconName = DEFAULT_BUTTON_ICON_NAME,
}) => {
  const [isYearly, setIsYearly] = useState(false);

  return (
    <section className="py-32">
      <div className="container">
        <div className="mx-auto flex max-w-5xl flex-col items-center gap-6 text-center">
          <h2 className="text-pretty text-4xl font-bold lg:text-6xl">{heading}</h2>
          <p className="text-muted-foreground lg:text-xl">{description}</p>
          <div className="flex items-center gap-3 text-lg">
            {monthlyLabel}
            <Switch
              checked={isYearly}
              onCheckedChange={(checked) => setIsYearly(checked)}
            />
            {yearlyLabel}
          </div>
          <div className="flex flex-col items-stretch gap-6 md:flex-row">
            {plans.map((plan) => {
              const priceToDisplay = isYearly ? plan.yearlyPrice : plan.monthlyPrice;
              const annualTotal = getAnnualBillingAmount(
                isYearly ? plan.yearlyPrice : plan.monthlyPrice,
              );
              const resolvedButtonIconName = plan.button.iconName ?? buttonIconName;
              const linkTarget = plan.button.target ?? "_blank";
              const linkRel = plan.button.rel ?? "noopener noreferrer";

              return (
                <Card
                  key={plan.id}
                  className="flex w-80 flex-col justify-between text-left"
                >
                  <CardHeader>
                    <CardTitle>
                      <p>{plan.name}</p>
                    </CardTitle>
                    <p className="text-sm text-muted-foreground">{plan.description}</p>
                    <span className="text-4xl font-bold">{priceToDisplay}</span>
                    <p className="text-muted-foreground">
                      {billingDescriptor} {annualTotal} {billingFrequencySuffix}
                    </p>
                  </CardHeader>
                  <CardContent>
                    <Separator className="mb-6" />
                    {plan.upgradeNote && (
                      <p className="mb-3 font-semibold">{plan.upgradeNote}</p>
                    )}
                    <ul className="space-y-4">
                      {plan.features.map((feature, index) => {
                        const resolvedFeatureIconName = feature.iconName ?? featureIconName;

                        return (
                          <li
                            key={`${plan.id}-feature-${index}`}
                            className="flex items-center gap-2"
                          >
                            {resolvedFeatureIconName && (
                              <DynamicIcon name={resolvedFeatureIconName} className="size-4" />
                            )}
                            <span>{feature.text}</span>
                          </li>
                        );
                      })}
                    </ul>
                  </CardContent>
                  <CardFooter className="mt-auto">
                    <Button asChild className="w-full">
                      <Link
                        href={plan.button.url}
                        target={linkTarget}
                        rel={linkRel}
                        className="inline-flex items-center justify-center gap-2"
                      >
                        {plan.button.text}
                        {resolvedButtonIconName && (
                          <DynamicIcon name={resolvedButtonIconName} className="size-4" />
                        )}
                      </Link>
                    </Button>
                  </CardFooter>
                </Card>
              );
            })}
          </div>
        </div>
      </div>
    </section>
  );
};

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground hover:bg-destructive/90",
        outline:
          "border border-input bg-background hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-10 px-4 py-2",
        sm: "h-9 rounded-md px-3",
        lg: "h-11 rounded-md px-8",
        icon: "h-10 w-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  },
)
Button.displayName = "Button"

export { Button, buttonVariants }

"use client"

import * as React from "react"
import * as SwitchPrimitives from "@radix-ui/react-switch"

import { cn } from "@/lib/utils"

const Switch = React.forwardRef<
  React.ElementRef<typeof SwitchPrimitives.Root>,
  React.ComponentPropsWithoutRef<typeof SwitchPrimitives.Root>
>(({ className, ...props }, ref) => (
  <SwitchPrimitives.Root
    className={cn(
      "peer inline-flex h-6 w-11 shrink-0 cursor-pointer items-center rounded-full border-2 border-transparent transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 focus-visible:ring-offset-background disabled:cursor-not-allowed disabled:opacity-50 data-[state=checked]:bg-primary data-[state=unchecked]:bg-input",
      className,
    )}
    {...props}
    ref={ref}
  >
    <SwitchPrimitives.Thumb
      className={cn(
        "pointer-events-none block h-5 w-5 rounded-full bg-background shadow-lg ring-0 transition-transform data-[state=checked]:translate-x-5 data-[state=unchecked]:translate-x-0",
      )}
    />
  </SwitchPrimitives.Root>
))
Switch.displayName = SwitchPrimitives.Root.displayName

export { Switch }

"use client"

import * as React from "react"
import * as SeparatorPrimitive from "@radix-ui/react-separator"

import { cn } from "@/lib/utils"

const Separator = React.forwardRef<
  React.ElementRef<typeof SeparatorPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof SeparatorPrimitive.Root>
>(
  (
    { className, orientation = "horizontal", decorative = true, ...props },
    ref
  ) => (
    <SeparatorPrimitive.Root
      ref={ref}
      decorative={decorative}
      orientation={orientation}
      className={cn(
        "shrink-0 bg-border",
        orientation === "horizontal" ? "h-[1px] w-full" : "h-full w-[1px]",
        className
      )}
      {...props}
    />
  )
)
Separator.displayName = SeparatorPrimitive.Root.displayName

export { Separator }

import * as React from "react"

import { cn } from "@/lib/utils"

const Card = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn(
      "rounded-lg border bg-card text-card-foreground shadow-sm",
      className,
    )}
    {...props}
  />
))
Card.displayName = "Card"

const CardHeader = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn("flex flex-col space-y-1.5 p-6", className)}
    {...props}
  />
))
CardHeader.displayName = "CardHeader"

const CardTitle = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLHeadingElement>
>(({ className, ...props }, ref) => (
  <h3
    ref={ref}
    className={cn(
      "text-2xl font-semibold leading-none tracking-tight",
      className,
    )}
    {...props}
  />
))
CardTitle.displayName = "CardTitle"

const CardDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, ...props }, ref) => (
  <p
    ref={ref}
    className={cn("text-sm text-muted-foreground", className)}
    {...props}
  />
))
CardDescription.displayName = "CardDescription"

const CardContent = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div ref={ref} className={cn("p-6 pt-0", className)} {...props} />
))
CardContent.displayName = "CardContent"

const CardFooter = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn("flex items-center p-6 pt-0", className)}
    {...props}
  />
))
CardFooter.displayName = "CardFooter"

export { Card, CardHeader, CardFooter, CardTitle, CardDescription, CardContent }

"use client"

import * as React from "react"
import * as LabelPrimitive from "@radix-ui/react-label"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const labelVariants = cva(
  "text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70",
)

const Label = React.forwardRef<
  React.ElementRef<typeof LabelPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof LabelPrimitive.Root> &
    VariantProps<typeof labelVariants>
>(({ className, ...props }, ref) => (
  <LabelPrimitive.Root
    ref={ref}
    className={cn(labelVariants(), className)}
    {...props}
  />
))
Label.displayName = LabelPrimitive.Root.displayName

export { Label }
