import { Fragment, type CSSProperties } from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { cn } from "@/lib/utils";

interface IconDescriptor {
  package: "lucide";
  name: string;
  type: "icon";
}

interface Hero45Feature {
  icon: IconDescriptor;
  title: string;
  description: string;
}

interface Hero45ImageProps {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  placeholder?: "blur" | "empty";
  blurDataURL?: string;
  style?: CSSProperties;
  className?: string;
  type?: "background" | "profile" | "logo" | "banner" | "gallery" | "thumbnail" | "icon" | "other";
}

interface Hero45Props {
  badge?: string;
  heading?: string;
  image?: Hero45ImageProps;
  features?: Hero45Feature[];
  className?: string;
}

const defaultImage: Hero45ImageProps = {
  src: "/images/block/placeholder-1.svg",
  alt: "placeholder",
};

const defaultFeatures: Hero45Feature[] = [
  {
    icon: { package: "lucide", name: "HelpingHand", type: "icon" },
    title: "Flexible Support",
    description: "Benefit from around-the-clock assistance to keep your business running smoothly.",
  },
  {
    icon: { package: "lucide", name: "Users", type: "icon" },
    title: "Collaborative Tools",
    description: "Enhance teamwork with tools designed to simplify project management and communication.",
  },
  {
    icon: { package: "lucide", name: "Zap", type: "icon" },
    title: "Lightning Fast Speed",
    description: "Experience the fastest load times with our high performance servers.",
  },
];

const Hero45 = ({
  badge = "shadcnblocks.com",
  heading = "Blocks built with Shadcn & Tailwind",
  image = defaultImage,
  features = defaultFeatures,
  className,
}: Hero45Props) => {
  const { className: imageClassName, ...imageRest } = image;

  return (
    <section className={cn("py-32", className)}>
      <div className="container mx-auto overflow-hidden">
        <div className="mb-20 flex flex-col items-center gap-6 text-center">
          {badge ? <Badge variant="outline">{badge}</Badge> : null}
          {heading ? <h1 className="text-4xl font-semibold lg:text-5xl">{heading}</h1> : null}
        </div>
        <div className="relative mx-auto max-w-screen-lg">
          <Image
            {...imageRest}
            className={cn("aspect-video max-h-[500px] w-full rounded-xl object-cover", imageClassName)}
          />
          <div className="absolute inset-0 bg-gradient-to-t from-background via-transparent to-transparent" />
          <div className="absolute -right-28 -top-28 -z-10 aspect-video h-72 w-96 opacity-40 [background-size:12px_12px] [mask-image:radial-gradient(ellipse_50%_50%_at_50%_50%,#000_20%,transparent_100%)] sm:bg-[radial-gradient(hsl(var(--muted-foreground))_1px,transparent_1px)]" />
          <div className="absolute -left-28 -top-28 -z-10 aspect-video h-72 w-96 opacity-40 [background-size:12px_12px] [mask-image:radial-gradient(ellipse_50%_50%_at_50%_50%,#000_20%,transparent_100%)] sm:bg-[radial-gradient(hsl(var(--muted-foreground))_1px,transparent_1px)]" />
        </div>
        <div className="mx-auto mt-10 flex max-w-screen-lg flex-col md:flex-row">
          {features.map((feature, index) => (
            <Fragment key={feature.title}>
              {index > 0 && (
                <Separator
                  orientation="vertical"
                  className="mx-6 hidden h-auto w-[2px] bg-gradient-to-b from-muted via-transparent to-muted md:block"
                />
              )}
              <div className="flex grow basis-0 flex-col rounded-md bg-background p-4">
                <div className="mb-6 flex size-10 items-center justify-center rounded-full bg-background drop-shadow-lg">
                  <DynamicIcon name={feature.icon.name} className="h-auto w-5" />
                </div>
                <h3 className="mb-2 font-semibold">{feature.title}</h3>
                <p className="text-sm text-muted-foreground">{feature.description}</p>
              </div>
            </Fragment>
          ))}
        </div>
      </div>
    </section>
  );
};

export type { Hero45Feature, Hero45ImageProps, Hero45Props };
export { Hero45 };

import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  "inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground hover:bg-primary/80",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80",
        destructive:
          "border-transparent bg-destructive text-destructive-foreground hover:bg-destructive/80",
        outline: "text-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
)

export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return (
    <div className={cn(badgeVariants({ variant }), className)} {...props} />
  )
}

export { Badge, badgeVariants }

"use client"

import * as React from "react"
import * as SeparatorPrimitive from "@radix-ui/react-separator"

import { cn } from "@/lib/utils"

const Separator = React.forwardRef<
  React.ElementRef<typeof SeparatorPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof SeparatorPrimitive.Root>
>(
  (
    { className, orientation = "horizontal", decorative = true, ...props },
    ref
  ) => (
    <SeparatorPrimitive.Root
      ref={ref}
      decorative={decorative}
      orientation={orientation}
      className={cn(
        "shrink-0 bg-border",
        orientation === "horizontal" ? "h-[1px] w-full" : "h-full w-[1px]",
        className
      )}
      {...props}
    />
  )
)
Separator.displayName = SeparatorPrimitive.Root.displayName

export { Separator }
