import * as React from "react";
import { cn } from "@/lib/utils";
import DynamicIcon from "@/components/DynamicIcon";

export interface Icon {
  package: "lucide";
  name: string;
  type: "icon";
}

export interface FeatureCardProps extends React.ComponentPropsWithoutRef<"div"> {
  title: string;
  description: string;
  icon: Icon;
  patternSquares?: number[][];
  patternWidth?: number;
  patternHeight?: number;
  patternOffsetX?: string;
  patternOffsetY?: string;
  iconStrokeWidth?: number;
  iconLabel?: string;
}

export const FeatureCard = React.forwardRef<HTMLDivElement, FeatureCardProps>(
  (
    {
      title,
      description,
      icon,
      patternSquares,
      patternWidth = 20,
      patternHeight = 20,
      patternOffsetX = "-12",
      patternOffsetY = "4",
      iconStrokeWidth = 1,
      iconLabel,
      className,
      ...props
    },
    ref,
  ) => {
    const squares = React.useMemo(
      () => patternSquares ?? genRandomPattern(),
      [patternSquares],
    );
    const { name: iconName } = icon;

    return (
      <div ref={ref} className={cn("relative overflow-hidden p-6", className)} {...props}>
        <div className="pointer-events-none absolute left-1/2 top-0 -ml-20 -mt-2 h-full w-full [mask-image:linear-gradient(white,transparent)]">
          <div className="absolute inset-0 bg-gradient-to-r from-foreground/5 to-foreground/1 opacity-100 [mask-image:radial-gradient(farthest-side_at_top,white,transparent)]">
            <GridPattern
              width={patternWidth}
              height={patternHeight}
              x={patternOffsetX}
              y={patternOffsetY}
              squares={squares}
              className="absolute inset-0 h-full w-full fill-foreground/5 stroke-foreground/25 mix-blend-overlay"
            />
          </div>
        </div>
        <DynamicIcon
          name={iconName}
          className="size-6 text-foreground/75"
          strokeWidth={iconStrokeWidth}
          aria-hidden={iconLabel ? undefined : true}
          aria-label={iconLabel}
        />
        <h3 className="mt-10 text-sm md:text-base">{title}</h3>
        <p className="relative z-20 mt-2 text-xs font-light text-muted-foreground">{description}</p>
      </div>
    );
  },
);

FeatureCard.displayName = "FeatureCard";

interface GridPatternProps extends React.ComponentPropsWithoutRef<"svg"> {
  width: number;
  height: number;
  x: string;
  y: string;
  squares?: number[][];
}

function GridPattern({ width, height, x, y, squares, ...props }: GridPatternProps) {
  const patternId = React.useId();

  return (
    <svg aria-hidden="true" {...props}>
      <defs>
        <pattern id={patternId} width={width} height={height} patternUnits="userSpaceOnUse" x={x} y={y}>
          <path d={`M.5 ${height}V.5H${width}`} fill="none" />
        </pattern>
      </defs>
      <rect width="100%" height="100%" fill={`url(#${patternId})`} strokeWidth={0} />
      {squares ? (
        <svg x={x} y={y} className="overflow-visible">
          {squares.map(([squareX, squareY], index) => (
            <rect
              key={`${squareX}-${squareY}-${index}`}
              width={width + 1}
              height={height + 1}
              x={squareX * width}
              y={squareY * height}
              strokeWidth={0}
            />
          ))}
        </svg>
      ) : null}
    </svg>
  );
}

function genRandomPattern(length: number = 5): number[][] {
  return Array.from({ length }, () => [
    Math.floor(Math.random() * 4) + 7,
    Math.floor(Math.random() * 6) + 1,
  ]);
}
