"use client";

import React from "react";
import { motion } from "motion/react";
import Image from "@/components/Image";

export interface TestimonialItem {
  text: string;
  imageSrc: string;
  imageAlt: string;
  name: string;
  role: string;
}

export interface TestimonialsColumnProps {
  className?: string;
  testimonials: TestimonialItem[];
  duration?: number;
}

export const TestimonialsColumn: React.FC<TestimonialsColumnProps> = ({
  className,
  testimonials,
  duration = 10,
}) => {
  return (
    <div className={className}>
      <motion.div
        animate={{ translateY: "-50%" }}
        transition={{
          duration,
          repeat: Infinity,
          ease: "linear",
          repeatType: "loop",
        }}
        className="flex flex-col gap-6 pb-6"
      >
        {Array.from({ length: 2 }).map((_, loopIndex) => (
          <React.Fragment key={`testimonials-loop-${loopIndex}`}>
            {testimonials.map(({ text, imageSrc, imageAlt, name, role }, testimonialIndex) => (
              <div
                className="w-full max-w-xs rounded-3xl border p-10 shadow-lg shadow-primary/10"
                key={`testimonial-${loopIndex}-${testimonialIndex}`}
              >
                <div>{text}</div>
                <div className="mt-5 flex items-center gap-2">
                  <Image
                    src={imageSrc}
                    alt={imageAlt}
                    width={40}
                    height={40}
                    className="h-10 w-10 rounded-full"
                    type="profile"
                  />
                  <div className="flex flex-col">
                    <div className="font-medium leading-5 tracking-tight">
                      {name}
                    </div>
                    <div className="leading-5 tracking-tight opacity-60">
                      {role}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </React.Fragment>
        ))}
      </motion.div>
    </div>
  );
};

export default TestimonialsColumn;
