"use client";
import React from "react";
import { motion } from "motion/react";
import Image from "@/components/Image";

export interface TestimonialItem {
  text: string;
  name: string;
  role: string;
  imageSrc: string;
  imageAlt: string;
  imageWidth: number;
  imageHeight: number;
  imageClassName?: string;
}

export interface BackgroundImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface TestimonialsColumnProps {
  className?: string;
  testimonials: TestimonialItem[];
  duration: number;
  backgroundColor?: string;
  backgroundImage?: BackgroundImageProps;
}

const TestimonialsColumn: React.FC<TestimonialsColumnProps> = ({
  className,
  testimonials,
  duration,
  backgroundColor,
  backgroundImage,
}) => {
  const rootClassName = ["relative", className].filter(Boolean).join(" ");

  return (
    <div className={rootClassName} style={{ backgroundColor }}>
      {backgroundImage ? (
        <Image
          src={backgroundImage.src}
          alt={backgroundImage.alt}
          width={backgroundImage.width}
          height={backgroundImage.height}
          priority={backgroundImage.priority}
          quality={backgroundImage.quality}
          sizes={backgroundImage.sizes}
          fill={backgroundImage.fill}
          className={[
            "absolute inset-0 -z-10 object-cover",
            backgroundImage.className,
          ]
            .filter(Boolean)
            .join(" ")}
          placeholder={backgroundImage.placeholder}
        />
      ) : null}

      <motion.div
        animate={{ translateY: "-50%" }}
        transition={{
          duration: duration,
          repeat: Infinity,
          ease: "linear",
          repeatType: "loop",
        }}
        className="flex flex-col gap-6 pb-6 bg-background"
      >
        {Array.from({ length: 2 }).map((_, index) => (
          <React.Fragment key={index}>
            {testimonials.map(
              (
                {
                  text,
                  imageSrc,
                  imageAlt,
                  imageWidth,
                  imageHeight,
                  imageClassName,
                  name,
                  role,
                },
                i
              ) => (
                <div
                  className="p-10 rounded-3xl border shadow-lg shadow-primary/10 max-w-xs w-full"
                  key={`${index}-${i}`}
                >
                  <div>{text}</div>
                  <div className="flex items-center gap-2 mt-5">
                    <Image
                      src={imageSrc}
                      alt={imageAlt}
                      width={imageWidth}
                      height={imageHeight}
                      className={["h-10 w-10 rounded-full", imageClassName]
                        .filter(Boolean)
                        .join(" ")}
                    />
                    <div className="flex flex-col">
                      <div className="font-medium tracking-tight leading-5">{name}</div>
                      <div className="leading-5 opacity-60 tracking-tight">{role}</div>
                    </div>
                  </div>
                </div>
              )
            )}
          </React.Fragment>
        ))}
      </motion.div>
    </div>
  );
};

export default TestimonialsColumn;
