"use client";

import { useState } from "react";
import Link from "@/components/Link";
import DynamicIcon from "@/components/DynamicIcon";
import { Button } from "@/components/ui/button";
import {
  NavigationMenu,
  NavigationMenuContent,
  NavigationMenuItem,
  NavigationMenuLink,
  NavigationMenuList,
  NavigationMenuTrigger,
} from "@/components/ui/navigation-menu";

interface ActionLink {
  label: string;
  href: string;
  target?: "_blank" | "_self";
  rel?: string;
}

interface NavigationSubItem extends ActionLink {}

interface NavigationItem {
  title: string;
  description?: string;
  href?: string;
  target?: "_blank" | "_self";
  rel?: string;
  items?: NavigationSubItem[];
  ctaLabel?: string;
  ctaHref?: string;
  ctaTarget?: "_blank" | "_self";
  ctaRel?: string;
}

interface Header1Props {
  brandName?: string;
  navigationItems?: NavigationItem[];
  demoLink?: ActionLink;
  signInLink?: ActionLink;
  primaryLink?: ActionLink;
  mobileMenuIconName?: string;
  mobileMenuCloseIconName?: string;
  subItemIconName?: string;
  mobileMenuToggleAriaLabel?: string;
}

const defaultNavigationItems: NavigationItem[] = [
  {
    title: "Home",
    href: "/",
  },
  {
    title: "Product",
    description: "Managing a small business today is already tough.",
    items: [
      { title: "Reports", href: "/reports" },
      { title: "Statistics", href: "/statistics" },
      { title: "Dashboards", href: "/dashboards" },
      { title: "Recordings", href: "/recordings" },
    ],
    ctaLabel: "Book a call today",
    ctaHref: "/book-a-call",
  },
  {
    title: "Company",
    description: "Managing a small business today is already tough.",
    items: [
      { title: "About us", href: "/about" },
      { title: "Fundraising", href: "/fundraising" },
      { title: "Investors", href: "/investors" },
      { title: "Contact us", href: "/contact" },
    ],
    ctaLabel: "Book a call today",
    ctaHref: "/book-a-call",
  },
];

const Header1: React.FC<Header1Props> = ({
  brandName = "TWBlocks",
  navigationItems = defaultNavigationItems,
  demoLink = { label: "Book a demo", href: "/book-a-demo" },
  signInLink = { label: "Sign in", href: "/sign-in" },
  primaryLink = { label: "Get started", href: "/get-started" },
  mobileMenuIconName = "Menu",
  mobileMenuCloseIconName = "X",
  subItemIconName = "MoveRight",
  mobileMenuToggleAriaLabel = "Toggle navigation menu",
}) => {
  const [isOpen, setOpen] = useState(false);

  const handleToggleMenu = () => {
    setOpen((prev) => !prev);
  };

  return (
    <header className="w-full z-40 fixed top-0 left-0">
      <div className="container relative mx-auto min-h-20 flex gap-4 flex-row lg:grid lg:grid-cols-3 items-center">
        <div className="justify-start items-center gap-4 lg:flex hidden flex-row">
          <NavigationMenu className="flex justify-start items-start">
            <NavigationMenuList className="flex justify-start gap-4 flex-row">
              {navigationItems.map((item) => (
                <NavigationMenuItem key={item.title}>
                  {item.href ? (
                    <NavigationMenuLink asChild>
                      <Button variant="ghost" asChild>
                        <Link
                          href={item.href}
                          target={item.target ?? "_self"}
                          rel={item.rel}
                        >
                          {item.title}
                        </Link>
                      </Button>
                    </NavigationMenuLink>
                  ) : (
                    <>
                      <NavigationMenuTrigger className="font-medium text-sm">
                        {item.title}
                      </NavigationMenuTrigger>
                      {item.items && (
                        <NavigationMenuContent className="!w-[450px] p-4">
                          <div className="flex flex-col lg:grid grid-cols-2 gap-4">
                            <div className="flex flex-col h-full justify-between">
                              <div className="flex flex-col">
                                <p className="text-base">{item.title}</p>
                                {item.description && (
                                  <p className="text-muted-foreground text-sm">
                                    {item.description}
                                  </p>
                                )}
                              </div>
                              {item.ctaLabel && item.ctaHref && (
                                <Button size="sm" className="mt-10" asChild>
                                  <Link
                                    href={item.ctaHref}
                                    target={item.ctaTarget ?? "_self"}
                                    rel={item.ctaRel}
                                  >
                                    {item.ctaLabel}
                                  </Link>
                                </Button>
                              )}
                            </div>
                            <div className="flex flex-col text-sm h-full justify-end">
                              {item.items.map((subItem) => (
                                <NavigationMenuLink asChild key={subItem.title}>
                                  <Link
                                    href={subItem.href}
                                    target={subItem.target ?? "_self"}
                                    rel={subItem.rel}
                                    className="flex flex-row justify-between items-center hover:bg-muted py-2 px-4 rounded"
                                  >
                                    <span>{subItem.title}</span>
                                    <DynamicIcon
                                      name={subItemIconName}
                                      className="w-4 h-4 text-muted-foreground"
                                    />
                                  </Link>
                                </NavigationMenuLink>
                              ))}
                            </div>
                          </div>
                        </NavigationMenuContent>
                      )}
                    </>
                  )}
                </NavigationMenuItem>
              ))}
            </NavigationMenuList>
          </NavigationMenu>
        </div>
        <div className="flex lg:justify-center">
          <p className="font-semibold">{brandName}</p>
        </div>
        <div className="flex justify-end w-full gap-4">
          {demoLink && (
            <Button variant="ghost" className="hidden md:inline" asChild>
              <Link
                href={demoLink.href}
                target={demoLink.target ?? "_self"}
                rel={demoLink.rel}
              >
                {demoLink.label}
              </Link>
            </Button>
          )}
          <div className="border-r hidden md:inline" />
          {signInLink && (
            <Button variant="outline" asChild>
              <Link
                href={signInLink.href}
                target={signInLink.target ?? "_self"}
                rel={signInLink.rel}
              >
                {signInLink.label}
              </Link>
            </Button>
          )}
          {primaryLink && (
            <Button asChild>
              <Link
                href={primaryLink.href}
                target={primaryLink.target ?? "_self"}
                rel={primaryLink.rel}
              >
                {primaryLink.label}
              </Link>
            </Button>
          )}
        </div>
        <div className="flex w-12 shrink lg:hidden items-end justify-end">
          <Button
            variant="ghost"
            onClick={handleToggleMenu}
            aria-label={mobileMenuToggleAriaLabel}
          >
            {isOpen ? (
              <DynamicIcon name={mobileMenuCloseIconName} className="w-5 h-5" />
            ) : (
              <DynamicIcon name={mobileMenuIconName} className="w-5 h-5" />
            )}
          </Button>
          {isOpen && (
            <div className="absolute top-20 border-t flex flex-col w-full right-0 shadow-lg py-4 container gap-8">
              {navigationItems.map((item) => (
                <div key={`${item.title}-mobile`}>
                  <div className="flex flex-col gap-2">
                    {item.href ? (
                      <Link
                        href={item.href}
                        target={item.target ?? "_self"}
                        rel={item.rel}
                        className="flex justify-between items-center"
                      >
                        <span className="text-lg">{item.title}</span>
                        <DynamicIcon
                          name={subItemIconName}
                          className="w-4 h-4 text-muted-foreground"
                        />
                      </Link>
                    ) : (
                      <p className="text-lg">{item.title}</p>
                    )}
                    {item.items &&
                      item.items.map((subItem) => (
                        <Link
                          key={`${subItem.title}-mobile`}
                          href={subItem.href}
                          target={subItem.target ?? "_self"}
                          rel={subItem.rel}
                          className="flex justify-between items-center"
                        >
                          <span className="text-muted-foreground">
                            {subItem.title}
                          </span>
                          <DynamicIcon
                            name={subItemIconName}
                            className="w-4 h-4 text-muted-foreground"
                          />
                        </Link>
                      ))}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export { Header1 };
export type { Header1Props, NavigationItem, NavigationSubItem, ActionLink };

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground hover:bg-destructive/90",
        outline:
          "border border-input bg-background hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-10 px-4 py-2",
        sm: "h-9 rounded-md px-3",
        lg: "h-11 rounded-md px-8",
        icon: "h-10 w-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  },
)
Button.displayName = "Button"

export { Button, buttonVariants }
