import * as React from "react";
import DynamicIcon from "@/components/DynamicIcon";
import { Badge } from "@/components/ui/badge";

type DynamicIconProps = React.ComponentProps<typeof DynamicIcon>;
type LucideIconName = DynamicIconProps["name"];

const mergeClasses = (...classes: Array<string | undefined>) =>
  classes.filter((cls): cls is string => Boolean(cls)).join(" ");

interface Icon {
  package: "lucide";
  name: LucideIconName;
  type: "icon";
}

export interface StatsCard {
  icon: Icon;
  iconClassName?: string;
  value: string;
  change: string;
  description: string;
  changeClassName?: string;
  containerClassName?: string;
}

export interface StatsProps {
  badgeLabel?: string;
  heading?: string;
  description?: string;
  cards?: StatsCard[];
  className?: string;
}

const defaultCards: StatsCard[] = [
  {
    icon: { package: "lucide", name: "MoveUpRight", type: "icon" },
    value: "500.000",
    change: "+20.1%",
    description: "Monthly active users",
  },
  {
    icon: { package: "lucide", name: "MoveDownLeft", type: "icon" },
    iconClassName: "text-destructive",
    value: "20.105",
    change: "-2%",
    description: "Daily active users",
  },
  {
    icon: { package: "lucide", name: "MoveUpRight", type: "icon" },
    value: "$523.520",
    change: "+8%",
    description: "Monthly recurring revenue",
  },
  {
    icon: { package: "lucide", name: "MoveUpRight", type: "icon" },
    value: "$1052",
    change: "+2%",
    description: "Cost per acquisition",
  },
];

export const Stats: React.FC<StatsProps> = ({
  badgeLabel = "Platform",
  heading = "This is the start of something new",
  description = "Managing a small business today is already tough. Avoid further complications by ditching outdated, tedious trade methods. Our goal is to streamline SMB trade, making it easier and faster than ever.",
  cards = defaultCards,
  className,
}) => {
  return (
    <div className={mergeClasses("w-full py-20 lg:py-40", className)}>
      <div className="container mx-auto">
        <div className="grid grid-cols-1 gap-10 lg:grid-cols-2">
          <div className="flex flex-col items-start gap-4">
            <div>
              <Badge>{badgeLabel}</Badge>
            </div>
            <div className="flex flex-col gap-2">
              <h2 className="text-left text-xl tracking-tighter md:text-3xl lg:text-5xl font-regular">
                {heading}
              </h2>
              <p className="text-left text-lg leading-relaxed tracking-tight text-muted-foreground lg:max-w-sm">
                {description}
              </p>
            </div>
          </div>
          <div className="flex items-center justify-center">
            <div className="grid w-full grid-cols-1 gap-2 text-left sm:grid-cols-2">
              {cards.map((card) => (
                <div
                  key={`${card.value}-${card.description}`}
                  className={mergeClasses(
                    "flex flex-col justify-between gap-0 rounded-md border p-6",
                    card.containerClassName
                  )}
                >
                  <DynamicIcon
                    name={card.icon.name}
                    className={mergeClasses(
                      "mb-10 h-4 w-4",
                      card.iconClassName ?? "text-primary"
                    )}
                  />
                  <h2 className="flex flex-row items-end gap-4 text-left text-4xl font-regular tracking-tighter">
                    {card.value}
                    <span
                      className={mergeClasses(
                        "text-sm tracking-normal text-muted-foreground",
                        card.changeClassName
                      )}
                    >
                      {card.change}
                    </span>
                  </h2>
                  <p className="max-w-xl text-left text-base leading-relaxed tracking-tight text-muted-foreground">
                    {card.description}
                  </p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  "inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground hover:bg-primary/80",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80",
        destructive:
          "border-transparent bg-destructive text-destructive-foreground hover:bg-destructive/80",
        outline: "text-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
)

export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return (
    <div className={cn(badgeVariants({ variant }), className)} {...props} />
  )
}

export { Badge, badgeVariants }
