import React from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";
import { Badge } from "@/components/ui/badge";

export interface Icon {
  package: 'lucide';
  name: string; // known lucide react icon-name only
  type: 'icon';
  className: string; // tailwind classes for styling
}

export interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface StatsItem {
  icon: Icon;
  value: string;
  change: string;
  label: string;
}

export interface StatsProps {
  badgeText: string;
  title: string;
  description: string;
  stats: StatsItem[];
  backgroundImage?: ImageProps;
  backgroundColor?: string;
  className?: string;
}

export function Stats({
  badgeText,
  title,
  description,
  stats,
  backgroundImage,
  backgroundColor,
  className,
}: StatsProps) {
  return (
    <div
      className={`w-full py-20 lg:py-40 ${backgroundImage ? "relative overflow-hidden" : ""} ${className ?? ""}`}
      style={backgroundColor ? { backgroundColor } : undefined}
    >
      {backgroundImage ? (
        <div className="absolute inset-0 -z-10">
          <Image
            src={backgroundImage.src}
            alt={backgroundImage.alt}
            width={backgroundImage.width}
            height={backgroundImage.height}
            priority={backgroundImage.priority}
            quality={backgroundImage.quality}
            sizes={backgroundImage.sizes}
            fill={backgroundImage.fill}
            className={`${backgroundImage.className ?? ""} w-full h-full object-cover`}
            placeholder={backgroundImage.placeholder}
          />
        </div>
      ) : null}
      <div className="container mx-auto">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-10">
          <div className="flex gap-4 flex-col items-start">
            <div>
              <Badge>{badgeText}</Badge>
            </div>
            <div className="flex gap-2 flex-col">
              <h2 className="text-xl md:text-3xl md:text-5xl tracking-tighter lg:max-w-xl font-regular text-left">
                {title}
              </h2>
              <p className="text-lg lg:max-w-sm leading-relaxed tracking-tight text-muted-foreground text-left">
                {description}
              </p>
            </div>
          </div>
          <div className="flex justify-center items-center">
            <div className="grid text-left grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 w-full gap-2">
              {stats.map((item, idx) => (
                <div key={idx} className="flex gap-0 flex-col justify-between p-6 border rounded-md">
                  <DynamicIcon name={item.icon.name} className={item.icon.className} />
                  <h2 className="text-4xl tracking-tighter max-w-xl text-left font-regular flex flex-row gap-4 items-end">
                    {item.value}
                    <span className="text-muted-foreground text-sm tracking-normal">
                      {item.change}
                    </span>
                  </h2>
                  <p className="text-base leading-relaxed tracking-tight text-muted-foreground max-w-xl text-left">
                    {item.label}
                  </p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Stats;

import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  "inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground hover:bg-primary/80",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80",
        destructive:
          "border-transparent bg-destructive text-destructive-foreground hover:bg-destructive/80",
        outline: "text-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
)

export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return (
    <div className={cn(badgeVariants({ variant }), className)} {...props} />
  )
}

export { Badge, badgeVariants }
