import type { JSX } from "react";
import DynamicIcon from "@/components/DynamicIcon";

interface Icon {
  package: "lucide";
  name: string;
  type: "icon";
}

interface StatsItem {
  value: string;
  changeLabel: string;
  description: string;
  icon: Icon;
  iconClassName?: string;
  changeClassName?: string;
}

export interface StatsProps {
  items?: StatsItem[];
  className?: string;
  containerClassName?: string;
  cardClassName?: string;
}

const defaultStatsItems: StatsItem[] = [
  {
    value: "500.000",
    changeLabel: "+20.1%",
    description: "Monthly active users",
    icon: { package: "lucide", name: "MoveUpRight", type: "icon" },
    iconClassName: "text-primary",
    changeClassName: "text-muted-foreground",
  },
  {
    value: "20.105",
    changeLabel: "-2%",
    description: "Daily active users",
    icon: { package: "lucide", name: "MoveDownLeft", type: "icon" },
    iconClassName: "text-destructive",
    changeClassName: "text-muted-foreground",
  },
  {
    value: "$523.520",
    changeLabel: "+8%",
    description: "Monthly recurring revenue",
    icon: { package: "lucide", name: "MoveUpRight", type: "icon" },
    iconClassName: "text-success",
    changeClassName: "text-muted-foreground",
  },
  {
    value: "$1052",
    changeLabel: "+2%",
    description: "Cost per acquisition",
    icon: { package: "lucide", name: "MoveUpRight", type: "icon" },
    iconClassName: "text-primary",
    changeClassName: "text-muted-foreground",
  },
];

const mergeClasses = (...classes: Array<string | undefined | null | false>): string =>
  classes.filter(Boolean).join(" ");

export function Stats({
  items = defaultStatsItems,
  className,
  containerClassName,
  cardClassName,
}: StatsProps): JSX.Element {
  return (
    <div className={mergeClasses("w-full py-20 lg:py-40", className)}>
      <div className={mergeClasses("container mx-auto", containerClassName)}>
        <div className="grid text-left grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 w-full gap-4 lg:gap-8">
          {items.map((item) => (
            <div
              key={`${item.icon.name}-${item.description}`}
              className={mergeClasses(
                "flex gap-0 flex-col justify-between p-6 border rounded-md",
                cardClassName
              )}
            >
              <DynamicIcon
                name={item.icon.name}
                className={mergeClasses(
                  "w-4 h-4 mb-10",
                  item.iconClassName ?? "text-primary"
                )}
              />
              <h2 className="text-4xl tracking-tighter max-w-xl text-left font-normal flex flex-row gap-4 items-end">
                {item.value}
                <span
                  className={mergeClasses(
                    "text-sm tracking-normal",
                    item.changeClassName ?? "text-muted-foreground"
                  )}
                >
                  {item.changeLabel}
                </span>
              </h2>
              <p className="text-base leading-relaxed tracking-tight text-muted-foreground max-w-xl text-left">
                {item.description}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
