import React from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";

// Icon interface guideline reference
interface Icon {
  package: 'lucide';
  name: string; // known lucide react icon-name only
  type: 'icon';
  className: string; // tailwind classes for styling
}

// Background Image props (aligned with Image component contract)
interface BackgroundImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface StatsItem {
  iconName: Icon['name'];
  iconClassName?: string;
  value: string;
  change: string;
  description: string;
}

export interface StatsProps {
  items: StatsItem[];
  className?: string;
  backgroundColor?: string;
  backgroundImage?: BackgroundImageProps;
}

export function Stats({ items, className, backgroundColor, backgroundImage }: StatsProps) {
  return (
    <div className={`w-full py-20 lg:py-40 ${backgroundColor ?? ''} ${className ?? ''} ${backgroundImage ? 'relative' : ''}`}>
      {backgroundImage ? (
        <Image
          {...backgroundImage}
          className={`${backgroundImage.className ?? ''} absolute inset-0 -z-10 object-cover`}
        />
      ) : null}
      <div className="container mx-auto">
        <div className="grid text-left grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 w-full gap-4 lg:gap-8">
          {items.map((item, idx) => (
            <div key={idx} className="flex gap-0 flex-col justify-between p-6 border rounded-md">
              <DynamicIcon name={item.iconName} className={`w-4 h-4 mb-10 ${item.iconClassName ?? ''}`} />
              <h2 className="text-4xl tracking-tighter max-w-xl text-left font-regular flex flex-row gap-4 items-end">
                {item.value}
                <span className="text-muted-foreground text-sm tracking-normal">{item.change}</span>
              </h2>
              <p className="text-base leading-relaxed tracking-tight text-muted-foreground max-w-xl text-left">
                {item.description}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
