"use client";

import * as React from "react";
import { useMeasure } from "@uidotdev/usehooks";
import { VariantProps, cva } from "class-variance-authority";
import {
  HTMLMotionProps,
  motion,
  useScroll,
  useTransform,
} from "motion/react";
import type { MotionValue } from "motion/react";

import { cn } from "@/lib/utils";

const processCardVariants = cva(
  "flex border backdrop-blur-lg transition-transform duration-500",
  {
    variants: {
      variant: {
        indigo:
          "border-slate-700 bg-gradient-to-br from-slate-900/70 via-slate-900/40 to-indigo-700/90 text-slate-50",
        light: "border-slate-200 bg-slate-50 text-slate-900 shadow-lg",
      },
      size: {
        sm: "min-w-[25%] max-w-[25%]",
        md: "min-w-[50%] max-w-[50%]",
        lg: "min-w-[75%] max-w-[75%]",
        xl: "min-w-full max-w-full",
      },
    },
    defaultVariants: {
      variant: "indigo",
      size: "md",
    },
  }
);

interface ContainerScrollContextValue {
  scrollYProgress: MotionValue<number>;
}

export interface ContainerScrollProps
  extends React.HTMLAttributes<HTMLDivElement> {}

export interface ContainerStickyProps
  extends React.HTMLAttributes<HTMLDivElement> {}

export interface ProcessCardTitleProps
  extends React.HTMLAttributes<HTMLDivElement> {}

export interface ProcessCardBodyProps
  extends React.HTMLAttributes<HTMLDivElement> {}

export interface ProcessCardProps
  extends HTMLMotionProps<"div">,
    VariantProps<typeof processCardVariants> {
  itemsLength: number;
  index: number;
}

const ContainerScrollContext = React.createContext<
  ContainerScrollContextValue | undefined
>(undefined);

function useContainerScrollContext(): ContainerScrollContextValue {
  const context = React.useContext(ContainerScrollContext);
  if (!context) {
    throw new Error(
      "useContainerScrollContext must be used within a ContainerScroll component"
    );
  }
  return context;
}

export const ContainerScroll = ({
  children,
  className,
  ...props
}: ContainerScrollProps) => {
  const scrollRef = React.useRef<HTMLDivElement | null>(null);
  const { scrollYProgress } = useScroll({
    target: scrollRef,
  });

  return (
    <ContainerScrollContext.Provider value={{ scrollYProgress }}>
      <div
        ref={scrollRef}
        className={cn("relative min-h-[120vh]", className)}
        {...props}
      >
        {children}
      </div>
    </ContainerScrollContext.Provider>
  );
};

export const ContainerSticky = React.forwardRef<
  HTMLDivElement,
  ContainerStickyProps
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn("sticky left-0 top-0 w-full overflow-hidden", className)}
    {...props}
  />
));

ContainerSticky.displayName = "ContainerSticky";

export const ProcessCardTitle = React.forwardRef<
  HTMLDivElement,
  ProcessCardTitleProps
>(({ className, ...props }, ref) => (
  <div ref={ref} className={cn("p-6", className)} {...props} />
));

ProcessCardTitle.displayName = "ProcessCardTitle";

export const ProcessCardBody = React.forwardRef<
  HTMLDivElement,
  ProcessCardBodyProps
>(({ className, ...props }, ref) => (
  <div ref={ref} className={cn("flex flex-col gap-8 p-6", className)} {...props} />
));

ProcessCardBody.displayName = "ProcessCardBody";

const CARD_SPACING = 64;

export const ProcessCard: React.FC<ProcessCardProps> = ({
  className,
  style,
  variant,
  size,
  itemsLength,
  index,
  ...rest
}) => {
  const { scrollYProgress } = useContainerScrollContext();
  const safeItemsLength = Math.max(itemsLength, 1);
  const normalizedIndex = Math.min(
    Math.max(Math.floor(index), 0),
    safeItemsLength - 1
  );
  const start = normalizedIndex / safeItemsLength;
  const end = Math.min(start + 1 / safeItemsLength, 1);

  const [elementRef, { width }] = useMeasure<HTMLDivElement>();
  const [viewportWidth, setViewportWidth] = React.useState<number>(() =>
    typeof window === "undefined" ? 0 : window.innerWidth
  );

  React.useEffect(() => {
    if (typeof window === "undefined") {
      return;
    }
    const handleResize = () => {
      setViewportWidth(window.innerWidth);
    };
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const inputRange = React.useMemo<[number, number]>(
    () => [start, end],
    [start, end]
  );

  const outputRange = React.useMemo<[number, number]>(() => {
    const measuredWidth = width ?? 0;
    const spacingOffset = normalizedIndex * CARD_SPACING;
    return [
      viewportWidth,
      -(measuredWidth * normalizedIndex) + spacingOffset,
    ];
  }, [normalizedIndex, viewportWidth, width]);

  const x = useTransform(scrollYProgress, inputRange, outputRange);

  return (
    <motion.div
      ref={elementRef}
      style={{
        ...style,
        x: normalizedIndex > 0 ? x : 0,
      }}
      className={cn(processCardVariants({ variant, size }), className)}
      {...rest}
    />
  );
};

ProcessCard.displayName = "ProcessCard";
