import React from "react";

import Link from "@/components/Link";
import Image from "@/components/Image";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";

export interface Contact2Props {
  title: string;
  description: string;
  contactDetailsHeading: string;
  phoneLabel: string;
  phone: string;
  emailLabel: string;
  email: string;
  webLabel: string;
  webLink: {
    href: string;
    label: string;
    target?: "_blank" | "_self";
    rel?: string;
  };
  firstNameLabel: string;
  firstNamePlaceholder: string;
  lastNameLabel: string;
  lastNamePlaceholder: string;
  emailFieldLabel: string;
  emailFieldPlaceholder: string;
  subjectLabel: string;
  subjectPlaceholder: string;
  messageLabel: string;
  messagePlaceholder: string;
  cta: {
    label: string;
    href: string;
    target?: "_blank" | "_self";
    rel?: string;
  };
  backgroundColor?: string;
  backgroundImage?: {
    src: string;
    alt: string;
    width: number;
    height: number;
    priority?: boolean;
    quality?: number;
    sizes?: string;
    fill?: boolean;
    className?: string;
    placeholder?: string;
  };
  className?: string;
}

export const Contact2: React.FC<Contact2Props> = ({
  title,
  description,
  contactDetailsHeading,
  phoneLabel,
  phone,
  emailLabel,
  email,
  webLabel,
  webLink,
  firstNameLabel,
  firstNamePlaceholder,
  lastNameLabel,
  lastNamePlaceholder,
  emailFieldLabel,
  emailFieldPlaceholder,
  subjectLabel,
  subjectPlaceholder,
  messageLabel,
  messagePlaceholder,
  cta,
  backgroundColor,
  backgroundImage,
  className,
}) => {
  return (
    <section
      className={`relative py-32 ${className ? className : ""}`}
      style={backgroundColor ? { backgroundColor } : undefined}
    >
      {backgroundImage ? (
        <div className="absolute inset-0 -z-10">
          <Image
            src={backgroundImage.src}
            alt={backgroundImage.alt}
            width={backgroundImage.width}
            height={backgroundImage.height}
            priority={backgroundImage.priority}
            quality={backgroundImage.quality}
            sizes={backgroundImage.sizes}
            fill={true}
            className={`h-full w-full object-cover ${backgroundImage.className ? backgroundImage.className : ""}`}
            placeholder={backgroundImage.placeholder}
          />
        </div>
      ) : null}

      <div className="container">
        <div className="mx-auto flex max-w-screen-xl flex-col justify-between gap-10 lg:flex-row lg:gap-20">
          <div className="mx-auto flex max-w-sm flex-col justify-between gap-10">
            <div className="text-center lg:text-left">
              <h1 className="mb-2 text-5xl font-semibold lg:mb-1 lg:text-6xl">{title}</h1>
              <p className="text-muted-foreground">{description}</p>
            </div>
            <div className="mx-auto w-fit lg:mx-0">
              <h3 className="mb-6 text-center text-2xl font-semibold lg:text-left">{contactDetailsHeading}</h3>
              <ul className="ml-4 list-disc">
                <li>
                  <span className="font-bold">{phoneLabel}: </span>
                  {phone}
                </li>
                <li>
                  <span className="font-bold">{emailLabel}: </span>
                  <Link href={`mailto:${email}`} className="underline" target="_self">
                    {email}
                  </Link>
                </li>
                <li>
                  <span className="font-bold">{webLabel}: </span>
                  <Link href={webLink.href} className="underline" target={webLink.target} rel={webLink.rel}>
                    {webLink.label}
                  </Link>
                </li>
              </ul>
            </div>
          </div>
          <div className="mx-auto flex max-w-screen-md flex-col gap-6 rounded-lg border p-10">
            <div className="flex gap-4">
              <div className="grid w-full items-center gap-1.5">
                <Label htmlFor="firstname">{firstNameLabel}</Label>
                <Input type="text" id="firstname" placeholder={firstNamePlaceholder} />
              </div>
              <div className="grid w-full items-center gap-1.5">
                <Label htmlFor="lastname">{lastNameLabel}</Label>
                <Input type="text" id="lastname" placeholder={lastNamePlaceholder} />
              </div>
            </div>
            <div className="grid w-full items-center gap-1.5">
              <Label htmlFor="email">{emailFieldLabel}</Label>
              <Input type="email" id="email" placeholder={emailFieldPlaceholder} />
            </div>
            <div className="grid w-full items-center gap-1.5">
              <Label htmlFor="subject">{subjectLabel}</Label>
              <Input type="text" id="subject" placeholder={subjectPlaceholder} />
            </div>
            <div className="grid w-full gap-1.5">
              <Label htmlFor="message">{messageLabel}</Label>
              <Textarea id="message" placeholder={messagePlaceholder} />
            </div>
            <Button className="w-full" asChild>
              <Link href={cta.href} target={cta.target} rel={cta.rel}>
                {cta.label}
              </Link>
            </Button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact2;
