import React from "react";

import Link from "@/components/Link";
import Image from "@/components/Image";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";

export type LinkTarget = "_blank" | "_self";

export interface BackgroundImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface Contact2Props {
  // Section heading
  title: string;
  description: string;

  // Contact details block
  contactDetailsHeading: string;
  phoneLabel: string;
  phone: string;
  emailLabel: string;
  email: string;
  webLabel: string;
  web: { label: string; url: string; target?: LinkTarget; rel?: string };

  // Form labels/placeholders
  firstNameLabel: string;
  firstNamePlaceholder: string;
  lastNameLabel: string;
  lastNamePlaceholder: string;
  emailFieldLabel: string;
  emailFieldPlaceholder: string;
  subjectLabel: string;
  subjectPlaceholder: string;
  messageLabel: string;
  messagePlaceholder: string;

  // CTA button (rendered as Link inside Button)
  ctaLabel: string;
  ctaHref: string;
  ctaTarget?: LinkTarget;
  ctaRel?: string;

  // Optional background styling
  bgColor?: string;
  bgImage?: BackgroundImageProps;
}

export const Contact2: React.FC<Contact2Props> = ({
  title,
  description,
  contactDetailsHeading,
  phoneLabel,
  phone,
  emailLabel,
  email,
  webLabel,
  web,
  firstNameLabel,
  firstNamePlaceholder,
  lastNameLabel,
  lastNamePlaceholder,
  emailFieldLabel,
  emailFieldPlaceholder,
  subjectLabel,
  subjectPlaceholder,
  messageLabel,
  messagePlaceholder,
  ctaLabel,
  ctaHref,
  ctaTarget,
  ctaRel,
  bgColor,
  bgImage,
}) => {
  return (
    <section className="relative py-32" style={bgColor ? { backgroundColor: bgColor } : undefined}>
      {bgImage ? (
        <div className="absolute inset-0 -z-10">
          <Image
            src={bgImage.src}
            alt={bgImage.alt}
            width={bgImage.width}
            height={bgImage.height}
            priority={bgImage.priority}
            quality={bgImage.quality}
            sizes={bgImage.sizes}
            fill={bgImage.fill}
            className={bgImage.className ? bgImage.className : "object-cover h-full w-full"}
            placeholder={bgImage.placeholder}
          />
        </div>
      ) : null}
      <div className="container">
        <div className="mx-auto flex max-w-screen-xl flex-col justify-between gap-10 lg:flex-row lg:gap-20">
          <div className="mx-auto flex max-w-sm flex-col justify-between gap-10">
            <div className="text-center lg:text-left">
              <h1 className="mb-2 text-5xl font-semibold lg:mb-1 lg:text-6xl">{title}</h1>
              <p className="text-muted-foreground">{description}</p>
            </div>
            <div className="mx-auto w-fit lg:mx-0">
              <h3 className="mb-6 text-center text-2xl font-semibold lg:text-left">{contactDetailsHeading}</h3>
              <ul className="ml-4 list-disc">
                <li>
                  <span className="font-bold">{phoneLabel} </span>
                  {phone}
                </li>
                <li>
                  <span className="font-bold">{emailLabel} </span>
                  <Link href={`mailto:${email}`} className="underline" target="_self">
                    {email}
                  </Link>
                </li>
                <li>
                  <span className="font-bold">{webLabel} </span>
                  <Link href={web.url} className="underline" target={web.target} rel={web.rel}>
                    {web.label}
                  </Link>
                </li>
              </ul>
            </div>
          </div>
          <div className="mx-auto flex max-w-screen-md flex-col gap-6 rounded-lg border p-10">
            <div className="flex gap-4">
              <div className="grid w-full items-center gap-1.5">
                <Label htmlFor="firstname">{firstNameLabel}</Label>
                <Input type="text" id="firstname" placeholder={firstNamePlaceholder} />
              </div>
              <div className="grid w-full items-center gap-1.5">
                <Label htmlFor="lastname">{lastNameLabel}</Label>
                <Input type="text" id="lastname" placeholder={lastNamePlaceholder} />
              </div>
            </div>
            <div className="grid w-full items-center gap-1.5">
              <Label htmlFor="email">{emailFieldLabel}</Label>
              <Input type="email" id="email" placeholder={emailFieldPlaceholder} />
            </div>
            <div className="grid w-full items-center gap-1.5">
              <Label htmlFor="subject">{subjectLabel}</Label>
              <Input type="text" id="subject" placeholder={subjectPlaceholder} />
            </div>
            <div className="grid w-full gap-1.5">
              <Label htmlFor="message">{messageLabel}</Label>
              <Textarea placeholder={messagePlaceholder} id="message" />
            </div>
            <Button className="w-full" asChild>
              <Link href={ctaHref} target={ctaTarget} rel={ctaRel} className="inline-flex w-full items-center justify-center">
                {ctaLabel}
              </Link>
            </Button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact2;
