import React from "react";

import Link from "@/components/Link";
import Image from "@/components/Image";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";

interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface Contact2Props {
  title: string;
  description: string;
  contactDetailsHeading: string;
  contactPhoneLabel: string; // e.g., "Phone:"
  contactEmailLabel: string; // e.g., "Email:"
  contactWebsiteLabel: string; // e.g., "Web:"
  phone: string;
  email: string; // visible email and for mailto
  websiteText: string; // visible text for website link
  websiteUrl: string; // href for website link

  firstNameLabel: string;
  firstNamePlaceholder: string;
  lastNameLabel: string;
  lastNamePlaceholder: string;
  emailFieldLabel: string;
  emailFieldPlaceholder: string;
  subjectLabel: string;
  subjectPlaceholder: string;
  messageLabel: string;
  messagePlaceholder: string;

  ctaLabel: string; // button text
  ctaHref: string; // link for button
  ctaTarget?: "_blank" | "_self";
  ctaRel?: string;
  ctaClassName?: string;

  bgColor?: string; // optional tailwind background class, e.g., "bg-muted"
  bgImage?: ImageProps; // optional background image props
}

const Contact2: React.FC<Contact2Props> = ({
  title,
  description,
  contactDetailsHeading,
  contactPhoneLabel,
  contactEmailLabel,
  contactWebsiteLabel,
  phone,
  email,
  websiteText,
  websiteUrl,
  firstNameLabel,
  firstNamePlaceholder,
  lastNameLabel,
  lastNamePlaceholder,
  emailFieldLabel,
  emailFieldPlaceholder,
  subjectLabel,
  subjectPlaceholder,
  messageLabel,
  messagePlaceholder,
  ctaLabel,
  ctaHref,
  ctaTarget,
  ctaRel,
  ctaClassName,
  bgColor,
  bgImage,
}) => {
  return (
    <section className={`py-32 ${bgColor ?? ""} ${bgImage ? "relative" : ""}`}>
      {bgImage ? (
        <div className="absolute inset-0 -z-10">
          <Image
            {...bgImage}
            className={`h-full w-full object-cover ${bgImage.className ?? ""}`}
          />
        </div>
      ) : null}
      <div className="container">
        <div className="mx-auto flex max-w-screen-xl flex-col justify-between gap-10 lg:flex-row lg:gap-20">
          <div className="mx-auto flex max-w-sm flex-col justify-between gap-10">
            <div className="text-center lg:text-left">
              <h1 className="mb-2 text-5xl font-semibold lg:mb-1 lg:text-6xl">{title}</h1>
              <p className="text-muted-foreground">{description}</p>
            </div>
            <div className="mx-auto w-fit lg:mx-0">
              <h3 className="mb-6 text-center text-2xl font-semibold lg:text-left">{contactDetailsHeading}</h3>
              <ul className="ml-4 list-disc">
                <li>
                  <span className="font-bold">{contactPhoneLabel} </span>
                  {phone}
                </li>
                <li>
                  <span className="font-bold">{contactEmailLabel} </span>
                  <Link href={`mailto:${email}`} className="underline" target="_self" rel="noopener noreferrer">
                    {email}
                  </Link>
                </li>
                <li>
                  <span className="font-bold">{contactWebsiteLabel} </span>
                  <Link href={websiteUrl} className="underline" target="_blank" rel="noopener noreferrer">
                    {websiteText}
                  </Link>
                </li>
              </ul>
            </div>
          </div>
          <div className="mx-auto flex max-w-screen-md flex-col gap-6 rounded-lg border p-10">
            <div className="flex gap-4">
              <div className="grid w-full items-center gap-1.5">
                <Label htmlFor="firstname">{firstNameLabel}</Label>
                <Input type="text" id="firstname" placeholder={firstNamePlaceholder} />
              </div>
              <div className="grid w-full items-center gap-1.5">
                <Label htmlFor="lastname">{lastNameLabel}</Label>
                <Input type="text" id="lastname" placeholder={lastNamePlaceholder} />
              </div>
            </div>
            <div className="grid w-full items-center gap-1.5">
              <Label htmlFor="email">{emailFieldLabel}</Label>
              <Input type="email" id="email" placeholder={emailFieldPlaceholder} />
            </div>
            <div className="grid w-full items-center gap-1.5">
              <Label htmlFor="subject">{subjectLabel}</Label>
              <Input type="text" id="subject" placeholder={subjectPlaceholder} />
            </div>
            <div className="grid w-full gap-1.5">
              <Label htmlFor="message">{messageLabel}</Label>
              <Textarea id="message" placeholder={messagePlaceholder} />
            </div>
            <Button asChild className="w-full">
              <Link href={ctaHref} target={ctaTarget} rel={ctaRel} className={`w-full ${ctaClassName ?? ""}`}>
                {ctaLabel}
              </Link>
            </Button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact2;
