import React from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Image from "@/components/Image";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";

// Interfaces per project guidelines
interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

interface LinkProps {
  href: string;
  target?: "_blank" | "_self";
  className?: string;
  rel?: string;
}

export interface Cta4Props {
  title: string;
  description: string;
  buttonText: string;
  buttonUrl: string;
  items: string[];
  // Optional background enhancements
  backgroundImage?: ImageProps;
  backgroundColor?: string; // Tailwind color/class string applied to the section wrapper
  // Optional link attributes for the CTA button
  buttonTarget?: LinkProps["target"];
  buttonRel?: string;
}

export const Cta4: React.FC<Cta4Props> = ({
  title,
  description,
  buttonText,
  buttonUrl,
  items,
  backgroundImage,
  backgroundColor,
  buttonTarget,
  buttonRel,
}) => {
  return (
    <section className={`relative py-32 ${backgroundColor ? backgroundColor : ""}`}>
      {backgroundImage && (
        <div className="absolute inset-0 -z-10 overflow-hidden">
          <Image {...backgroundImage} />
        </div>
      )}
      <div className="container mx-auto">
        <div className="flex justify-center">
          <div className="max-w-5xl">
            <div className="flex flex-col items-start justify-between gap-8 rounded-lg bg-muted px-6 py-10 md:flex-row lg:px-20 lg:py-16">
              <div className="md:w-1/2">
                <h4 className="mb-1 text-2xl font-bold md:text-3xl">{title}</h4>
                <p className="text-muted-foreground">{description}</p>
                <Button className="mt-6" asChild>
                  <Link href={buttonUrl} target={buttonTarget} rel={buttonRel} className="inline-flex items-center gap-2">
                    {buttonText}
                    <DynamicIcon name="ArrowRight" className="size-4" />
                  </Link>
                </Button>
              </div>
              <div className="md:w-1/3">
                <ul className="flex flex-col space-y-2 text-sm font-medium">
                  {items.map((item, idx) => (
                    <li className="flex items-center" key={idx}>
                      <DynamicIcon name="Check" className="mr-4 size-4 flex-shrink-0" />
                      <span>{item}</span>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Cta4;
