import React from "react";
import DynamicIcon from "@/components/DynamicIcon";
import Image, { type ImageProps as BgImageProps } from "@/components/Image";
import Link from "@/components/Link";
import { Button } from "@/components/ui/button";

export interface Cta4Props {
  title: string;
  description: string;
  buttonText: string;
  buttonHref: string;
  items: string[];
  buttonTarget?: '_blank' | '_self';
  buttonRel?: string;
  ctaIconName?: string; // lucide icon name for CTA button (e.g., "ArrowRight")
  listIconName?: string; // lucide icon name for list items (e.g., "Check")
  backgroundImage?: BgImageProps; // optional background image
  backgroundColor?: string; // optional background color (e.g., "#ffffff" or "rgb(...)" or tailwind-compatible via inline style)
  className?: string;
}

export function Cta4({
  title,
  description,
  buttonText,
  buttonHref,
  items,
  buttonTarget,
  buttonRel,
  ctaIconName,
  listIconName,
  backgroundImage,
  backgroundColor,
  className,
}: Cta4Props): JSX.Element {
  return (
    <section
      className={`relative py-32 ${className ?? ""}`}
      style={backgroundColor ? { backgroundColor } : undefined}
    >
      {backgroundImage && (
        <div className="absolute inset-0 -z-10">
          <Image
            {...backgroundImage}
            className={`h-full w-full object-cover ${backgroundImage.className ?? ""}`}
          />
        </div>
      )}
      <div className="container mx-auto">
        <div className="flex justify-center">
          <div className="max-w-5xl">
            <div className="flex flex-col items-start justify-between gap-8 rounded-lg bg-muted px-6 py-10 md:flex-row lg:px-20 lg:py-16">
              <div className="md:w-1/2">
                <h4 className="mb-1 text-2xl font-bold md:text-3xl">{title}</h4>
                <p className="text-muted-foreground">{description}</p>
                <Button className="mt-6" asChild>
                  <Link href={buttonHref} target={buttonTarget} rel={buttonRel}>
                    <span className="inline-flex items-center gap-2">
                      {buttonText}
                      {ctaIconName && (
                        <DynamicIcon name={ctaIconName} className="size-4" />
                      )}
                    </span>
                  </Link>
                </Button>
              </div>
              <div className="md:w-1/3">
                <ul className="flex flex-col space-y-2 text-sm font-medium">
                  {items.map((item, idx) => (
                    <li className="flex items-center" key={idx}>
                      {listIconName && (
                        <DynamicIcon
                          name={listIconName}
                          className="mr-4 size-4 flex-shrink-0"
                        />
                      )}
                      {item}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Cta4;
