import * as React from "react";
import { Badge } from "@/components/ui/badge";
import Image from "@/components/Image";

export interface FaqItem {
  question: string;
  answer: string;
}

// ImageProps interface as per guidelines
interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface Faq5Props {
  badge: string;
  heading: string;
  description: string;
  faqs: FaqItem[];
  backgroundImage?: ImageProps;
  backgroundColor?: string;
}

export const Faq5: React.FC<Faq5Props> = ({
  badge,
  heading,
  description,
  faqs,
  backgroundImage,
  backgroundColor,
}) => {
  return (
    <section
      className="relative py-32"
      style={backgroundColor ? { backgroundColor } : undefined}
      aria-label={heading}
    >
      {backgroundImage && (
        <Image
          {...backgroundImage}
          className={`pointer-events-none absolute inset-0 -z-10 h-full w-full object-cover ${backgroundImage.className ?? ""}`}
        />
      )}
      <div className="container">
        <div className="text-center">
          <Badge className="text-xs font-medium">{badge}</Badge>
          <h1 className="mt-4 text-4xl font-semibold">{heading}</h1>
          <p className="mt-6 font-medium text-muted-foreground">{description}</p>
        </div>
        <div className="mx-auto mt-14 max-w-screen-sm">
          {faqs.map((faq, index) => (
            <div key={index} className="mb-8 flex gap-4">
              <span className="flex size-6 shrink-0 items-center justify-center rounded-sm bg-secondary font-mono text-xs text-primary">
                {index + 1}
              </span>
              <div>
                <div className="mb-2 flex items-center justify-between">
                  <h3 className="font-medium">{faq.question}</h3>
                </div>
                <p className="text-sm text-muted-foreground">{faq.answer}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Faq5;
