import { Badge } from "@/components/ui/badge";
import Image, { type ImageProps as ImageComponentProps } from "@/components/Image";
import { cn } from "@/lib/utils";

export interface FaqItem {
  question: string;
  answer: string;
}

export interface Faq5Props {
  badge?: string;
  heading?: string;
  description?: string;
  faqs?: FaqItem[];
  backgroundImage?: ImageComponentProps;
  backgroundColor?: string;
  className?: string;
}

export const Faq5: React.FC<Faq5Props> = ({
  badge,
  heading,
  description,
  faqs,
  backgroundImage,
  backgroundColor,
  className,
}) => {
  return (
    <section
      className={cn("relative py-32", className)}
      style={backgroundColor ? { backgroundColor } : undefined}
    >
      {backgroundImage ? (
        <div className="absolute inset-0 -z-10">
          <Image
            {...backgroundImage}
            className={cn("h-full w-full object-cover", backgroundImage.className)}
          />
        </div>
      ) : null}
      <div className="container">
        <div className="text-center">
          {badge ? <Badge className="text-xs font-medium">{badge}</Badge> : null}
          {heading ? (
            <h1 className="mt-4 text-4xl font-semibold">{heading}</h1>
          ) : null}
          {description ? (
            <p className="mt-6 font-medium text-muted-foreground">{description}</p>
          ) : null}
        </div>
        {Array.isArray(faqs) && faqs.length > 0 ? (
          <div className="mx-auto mt-14 max-w-screen-sm">
            {faqs.map((faq, index) => (
              <div key={index} className="mb-8 flex gap-4">
                <span className="flex size-6 shrink-0 items-center justify-center rounded-sm bg-secondary font-mono text-xs text-primary">
                  {index + 1}
                </span>
                <div>
                  <div className="mb-2 flex items-center justify-between">
                    <h3 className="font-medium">{faq.question}</h3>
                  </div>
                  <p className="text-sm text-muted-foreground">{faq.answer}</p>
                </div>
              </div>
            ))}
          </div>
        ) : null}
      </div>
    </section>
  );
};

export default Faq5;
