import Image from "@/components/Image";
import { Badge } from "@/components/ui/badge";

export interface FaqItem {
  question: string;
  answer: string;
}

// ImageProps interface as per guidelines for optional background image
export interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface Faq5Props {
  badge?: string;
  heading?: string;
  description?: string;
  faqs?: FaqItem[];
  backgroundColor?: string;
  backgroundImage?: ImageProps;
  className?: string;
  id?: string;
}

export function Faq5({
  badge,
  heading,
  description,
  faqs,
  backgroundColor,
  backgroundImage,
  className,
  id,
}: Faq5Props) {
  return (
    <section
      id={id}
      className={`relative py-32 ${className ?? ""}`}
      style={{ backgroundColor: backgroundColor }}
      aria-label={heading || badge || "FAQ Section"}
    >
      {backgroundImage ? (
        <div className="absolute inset-0 -z-10 overflow-hidden">
          <Image
            src={backgroundImage.src}
            alt={backgroundImage.alt}
            width={backgroundImage.width}
            height={backgroundImage.height}
            priority={backgroundImage.priority}
            quality={backgroundImage.quality}
            sizes={backgroundImage.sizes}
            fill={backgroundImage.fill}
            className={`object-cover ${backgroundImage.className ?? ""}`}
            placeholder={backgroundImage.placeholder}
          />
        </div>
      ) : null}

      <div className="container">
        <div className="text-center">
          {badge ? <Badge className="text-xs font-medium">{badge}</Badge> : null}
          {heading ? <h1 className="mt-4 text-4xl font-semibold">{heading}</h1> : null}
          {description ? (
            <p className="mt-6 font-medium text-muted-foreground">{description}</p>
          ) : null}
        </div>

        {Array.isArray(faqs) && faqs.length > 0 ? (
          <div className="mx-auto mt-14 max-w-screen-sm">
            {faqs.map((faq, index) => (
              <div key={index} className="mb-8 flex gap-4">
                <span className="flex size-6 shrink-0 items-center justify-center rounded-sm bg-secondary font-mono text-xs text-primary">
                  {index + 1}
                </span>
                <div>
                  <div className="mb-2 flex items-center justify-between">
                    <h3 className="font-medium">{faq.question}</h3>
                  </div>
                  <p className="text-sm text-muted-foreground">{faq.answer}</p>
                </div>
              </div>
            ))}
          </div>
        ) : null}
      </div>
    </section>
  );
}

export default Faq5;
