import React from "react";
import { Button } from "@/components/ui/button";
import Image from "@/components/Image";
import Link from "@/components/Link";

interface LinkProps {
  href: string;
  target?: "_blank" | "_self";
  className?: string;
  rel?: string;
}

interface CTALink extends LinkProps {
  label: string;
}

interface ImageProps {
  src: string;
  alt: string;
  width: number;
  height: number;
  priority?: boolean;
  quality?: number;
  sizes?: string;
  fill?: boolean;
  className?: string;
  placeholder?: string;
}

export interface Feature1Props {
  title: string;
  description?: string;
  image: ImageProps;
  buttonPrimary: CTALink;
  buttonSecondary?: CTALink;
  backgroundColor?: string;
  backgroundImage?: ImageProps;
}

export const Feature1: React.FC<Feature1Props> = ({
  title,
  description,
  image,
  buttonPrimary,
  buttonSecondary,
  backgroundColor,
  backgroundImage,
}) => {
  return (
    <section className="relative py-32" style={{ backgroundColor }}>
      {backgroundImage && (
        <div className="absolute inset-0 -z-10">
          <Image
            src={backgroundImage.src}
            alt={backgroundImage.alt}
            width={backgroundImage.width}
            height={backgroundImage.height}
            priority={backgroundImage.priority}
            quality={backgroundImage.quality}
            sizes={backgroundImage.sizes}
            fill={true}
            className={`${backgroundImage.className ?? ""} object-cover`}
            placeholder={backgroundImage.placeholder}
          />
        </div>
      )}
      <div className="container">
        <div className="grid items-center gap-8 lg:grid-cols-2">
          <div className="flex flex-col items-center text-center lg:items-start lg:text-left">
            <h1 className="my-6 mt-0 text-4xl font-semibold text-balance lg:text-5xl">{title}</h1>
            {description && (
              <p className="mb-8 max-w-xl text-muted-foreground lg:text-lg">{description}</p>
            )}
            <div className="flex w-full flex-col justify-center gap-2 sm:flex-row lg:justify-start">
              <Button asChild>
                <Link
                  href={buttonPrimary.href}
                  target={buttonPrimary.target}
                  rel={buttonPrimary.target === "_blank" ? "noopener noreferrer" : buttonPrimary.rel}
                >
                  {buttonPrimary.label}
                </Link>
              </Button>
              {buttonSecondary && (
                <Button variant="outline" asChild>
                  <Link
                    href={buttonSecondary.href}
                    target={buttonSecondary.target}
                    rel={buttonSecondary.target === "_blank" ? "noopener noreferrer" : buttonSecondary.rel}
                  >
                    {buttonSecondary.label}
                  </Link>
                </Button>
              )}
            </div>
          </div>
          <Image
            src={image.src}
            alt={image.alt}
            width={image.width}
            height={image.height}
            priority={image.priority}
            quality={image.quality}
            sizes={image.sizes}
            fill={image.fill}
            className={image.className ?? "max-h-96 w-full rounded-md object-cover"}
            placeholder={image.placeholder}
          />
        </div>
      </div>
    </section>
  );
};

export default Feature1;
