import type { CSSProperties } from "react";
import { Button } from "@/components/ui/button";
import Link from "@/components/Link";
import Image from "@/components/Image";

export interface Feature1Props {
  title: string;
  description?: string;
  imageSrc: string;
  imageAlt: string;
  imageWidth: number;
  imageHeight: number;
  buttonPrimary: {
    label: string;
    href: string;
    target?: "_blank" | "_self";
    rel?: string;
  };
  buttonSecondary: {
    label: string;
    href: string;
    target?: "_blank" | "_self";
    rel?: string;
  };
  backgroundImageSrc?: string;
  backgroundColor?: string;
}

export function Feature1({
  title,
  description,
  imageSrc,
  imageAlt,
  imageWidth,
  imageHeight,
  buttonPrimary,
  buttonSecondary,
  backgroundImageSrc,
  backgroundColor,
}: Feature1Props) {
  const sectionStyle: CSSProperties = {
    ...(backgroundColor ? { backgroundColor } : {}),
    ...(backgroundImageSrc ? { backgroundImage: `url(${backgroundImageSrc})` } : {}),
  };

  return (
    <section className={`py-32 ${backgroundImageSrc ? "bg-cover bg-center" : ""}`} style={sectionStyle}>
      <div className="container">
        <div className="grid items-center gap-8 lg:grid-cols-2">
          <div className="flex flex-col items-center text-center lg:items-start lg:text-left">
            <h1 className="my-6 mt-0 text-4xl font-semibold text-balance lg:text-5xl">{title}</h1>
            {description && (
              <p className="mb-8 max-w-xl text-muted-foreground lg:text-lg">{description}</p>
            )}
            <div className="flex w-full flex-col justify-center gap-2 sm:flex-row lg:justify-start">
              <Button asChild>
                <Link href={buttonPrimary.href} target={buttonPrimary.target} rel={buttonPrimary.rel}>
                  {buttonPrimary.label}
                </Link>
              </Button>
              <Button variant="outline" asChild>
                <Link href={buttonSecondary.href} target={buttonSecondary.target} rel={buttonSecondary.rel}>
                  {buttonSecondary.label}
                </Link>
              </Button>
            </div>
          </div>
          <Image
            src={imageSrc}
            alt={imageAlt}
            width={imageWidth}
            height={imageHeight}
            className="max-h-96 w-full rounded-md object-cover"
          />
        </div>
      </div>
    </section>
  );
}
